body > div {box-shadow: none !important;}
.rc-doscaptcha-header {background-color: #C4C4C4; border: 1px solid #000000;}
.rc-doscaptcha-header-text {color: #4A90E2;}
/* 4chan X specific */
#qr .captcha-root {margin-top: 1px;}
/* 4chan X Noscript CAPTCHA specific */
.fbc {background: none; border-color: #000000; border-radius: 0;}
.fbc-button-verify input {background-color: #C4C4C4; border: 1px solid #000000; border-radius: 0; color: #4A90E2}
#captcha-forced-noscript > div > div > div {background: none; border-color: #000000; border-radius: 0;}
#captcha-forced-noscript > div > div > div {background: #DBDBDB !important; border-color: #000000 !important; border-radius: 0 !important;}

}

