[id="5a4bb3b24148f60004a5e7fe"] {display: none !important;}

/*
[id="5a4bb3b24148f60004a5e7fe"] = DEFAULT (Hidden, unable to delete)
[id="5acda5a01c8f970004d12428"] = PERSONAL 
[id="5acda4da1c8f970004d12427"] = WORK
[id="5aa8c71de4fb2c00040f19e5"] = MY MISC
[id="5aa8c95be4fb2c00040f19f0"] = MY PROJECTS
[id="5a55e9d92fe90e0004a7e02e"] = WORK MISC
[id="5aa8ce30e4fb2c00040f19f2"] = WORK PROJECTS
*/

/* ------------------------------------------------------------------ SCOPE HEADERS */
[id="5acda5a01c8f970004d12428"] /* PERSONAL */, 
[id="5acda4da1c8f970004d12427"] /* WORK */,
[id="5aa8c71de4fb2c00040f19e5"] /* MY MISC */,
[id="5aa8c95be4fb2c00040f19f0"] /* MY PROJECTS */,
[id="5a55e9d92fe90e0004a7e02e"] /* WORK MISC */,
[id="5aa8ce30e4fb2c00040f19f2"] /* WORK PROJECTS */ {
    pointer-events: none !important;
    padding: 0rem 0 !important;
    
}
[id="5acda5a01c8f970004d12428"] > div /* PERSONAL */, 
[id="5acda4da1c8f970004d12427"] > div /* WORK */,
[id="5aa8c71de4fb2c00040f19e5"] > div /* MY MISC */,
[id="5aa8c95be4fb2c00040f19f0"] > div /* MY PROJECTS */,
[id="5a55e9d92fe90e0004a7e02e"] > div /* WORK MISC */,
[id="5aa8ce30e4fb2c00040f19f2"] > div /* WORK PROJECTS */ {
	border: none;
}
/* ------------------------------------------------ SCOPE HEADERS - MAJOR */
[id="5acda5a01c8f970004d12428"] /* PERSONAL */, 
[id="5acda4da1c8f970004d12427"] /* WORK */ {
    background: rgba(0,0,0,.1	) !important;
}
[id="5acda5a01c8f970004d12428"] > div /* PERSONAL */, 
[id="5acda4da1c8f970004d12427"] > div /* WORK */ {
    border: none !important;
}
[id="5acda5a01c8f970004d12428"] > div > span /* PERSONAL */, 
[id="5acda4da1c8f970004d12427"] > div > span /* WORK */ {
    font-weight: bold !important;
    text-transform: uppercase !important;
    text-align: center !important;
    font-size: 1.8rem !important;
}


/* ------------------------------------------------ SCOPE HEADERS - MINOR */
[id="5aa8c71de4fb2c00040f19e5"] > div > span /* MY MISC */,
[id="5aa8c95be4fb2c00040f19f0"] > div > span /* MY PROJECTS */,
[id="5a55e9d92fe90e0004a7e02e"] > div > span /* WORK MISC */,
[id="5aa8ce30e4fb2c00040f19f2"] > div > span /* WORK PROJECTS */  {
    font-weight: bold !important;
    text-transform: uppercase !important;
    font-size: 1.2rem !important;
}

