[id="5a55e9d92fe90e0004a7e02e"] * /* WORK MISC */ {color: rgba(251, 216, 90, 1) !important}
[id="5aa8ce30e4fb2c00040f19f2"] * /* WORK PROJECTS */  {color: rgba(190, 143, 212, 1) !important}

