css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("4chan.org") {


/* changes font to Verdana */
a,
a:hover,
a.quotejs,
a.quotejs:hover,
a.quotelink,
body,
td.reply,
td.replyhl,
span.filesize,
#navtop,
#navtopr {
   /* http://en.wikipedia.org/wiki/Em_(typography) */
   font:1em Verdana !important;
}

/* Block a shitload of stuff */
a[href*="http://www.4chan.org/advertise/"],
center+hr,
center > a > img,
div.logo,
div.logo img,
hr,
hr[size="1"][width="90%"],
iframe,
img[src*="http://content.4chan.org/dontblockthis"],
img + br,td.rules,
#ad,
div#footer,
td.deletebuttons,
div#header,
div#twi_table_div,
div.logo {
   display:none !important;
}

#navtop{
   display:block !important;
}

body{
   background-color:#FFF !important;
   background-image:none !important;
}

form hr{
   display:block !important;
}

/* rounded corners */
td.reply,
td.replyhl {
   -moz-border-radius:1em !important;
   background-color: #E5EFFB !important;
   width:60em !important;
   height:auto !important;
}

td.reply {
   border-color: #294A87 !important;
   border-style:solid !important;
   border-width:1px !important;
}

/* links look now like regular text (except for >>'s) */
a {
   text-decoration:none !important;
}

a:hover,
a.quotejs:hover {
   font-weight:normal !important;
   text-decoration:underline !important;
}

/* removes borders on page overview [0,1,2...] */
table,
td {
   border:none !important;
}

/* post area */
#header {
   position:static !important;
}

td.postblock {
   font-size:0.8em !important;
   vertical-align:top !important;
   background-color:#4D94EA !important;
}

input,
textarea {
   border:#aaa 1px solid !important;
   margin-right:2px !important;
}

textarea {
   height:10em !important;
   width:30em !important;
}

.inputtext {
   width:16.5em;
}

/* blurry thumbnails fix */
img[md5] {
   height:auto !important;
   width:auto !important;
}

.postername, span.commentpostername {
   color: #294A87 !important;
}

.pages {
   background-color:#fff !important;
}

div.postarea {
   position:static !important;
   width:472px !important;
   right:0px !important;
}

}
