css:
  @-moz-document url-prefix("https://avenoel.org/")
{
html
  {
    font-family: Avantgarde, TeX Gyre Adventor, URW Gothic L, sans-serif;
    background-image: url(https://i.imgur.com/qiEWdtN.gif);
    background-attachment: fixed;
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
  }

  @font-face {
    font-family: 'sith_prophecyregular';
    src: url('https://dl.dropboxusercontent.com/s/5vmvx4r8kuvccsi/sthproph-webfont.woff2') format('woff2'),
         url('https://dl.dropboxusercontent.com/s/ul8xxzo4m9sjnm9/sthproph-webfont.woff') format('woff');
    font-weight: normal;
    font-style: normal;

}

  @font-face 
   {
    font-family: 'lord_of_the_sithregular';
    src: url('https://dl.dropboxusercontent.com/s/jf31jlkc1xk5ajo/lordsith3-webfont.woff2') format('woff2'),
         url('https://dl.dropboxusercontent.com/s/llxcyhh7p82gofl/lordsith3-webfont.woff') format('woff');
    font-weight: normal;
    font-style: normal;
   }
  
  @font-face
  {
    font-family: 'conthraxsemibold';
    src: url('https://dl.dropboxusercontent.com/s/48tnyv0jrx17pn7/conthrax-sb-webfont.woff2') format('woff2'),
         url('https://dl.dropboxusercontent.com/s/pxw2uvyg50xsik1/conthrax-sb-webfont.woff') format('woff');
    font-weight: normal;
    font-style: normal;
  } 
  
  @font-face
  {
    font-family: 'aurebeshregular';
    src: url('https://dl.dropboxusercontent.com/s/s1vk7udw6g71hfo/aurebesh-webfont.woff2') format('woff2'),
         url('https://dl.dropboxusercontent.com/s/emz6m04drlybaqy/aurebesh-webfont.woff') format('woff');
    font-weight: normal;
    font-style: normal;
  }
  
  @font-face
  {
    font-family: 'sf_distant_galaxyregular';
    src: url('https://dl.dropboxusercontent.com/s/fg1yf58zc6aeo9b/sf_distant_galaxy-webfont.woff2') format('woff2'),
         url('https://dl.dropboxusercontent.com/s/a6nbyzc05d6baw3/sf_distant_galaxy-webfont.woff') format('woff');
    font-weight: 400;
    font-style: normal;
  }

  body
  {
    font-family: Avantgarde, TeX Gyre Adventor, URW Gothic L, sans-serif;
    background-color: rgba(122, 13, 13, 0);
  }

  main.container .container-content section
  {
     /*couleur de fond (derrière la liste des sujets et les blocs messages)*/
    background-color: rgba(89, 2, 0, 0.73);
    opacity: 0.89;
  }

  tbody
  {
    background-color: rgba(89, 2, 0, 0.73);
    opacity: 0.8;
 /*fond caché derrière la liste des sujet, faire attention à sa couleur en cas de transparence*/;
  }

  td
  {
    background-color: rgba(89, 2, 0, 0.73);
 /*fond des lignes de la liste des sujets*/;
  }

  tr
  {
    background-color: rgba(89, 2, 0, 0.73);
 /*fond de l'en tête de la liste des sujets*/
  }

  .active
  {
   font-family: 'sith_prophecyregular';
  }

  circle.background
  {
   display : none;
  }

  ellipse.stroke
  {
   display :none;
  }

  .onesignal-bell-svg
  {
   background-image: url(https://i.imgur.com/w7t6Bn9.png) !important;
  }

  .form-control
  {
   font-family: sans-serif;
   color : rgba(44, 1, 0, 0.73);
   font-family: lord_of_the_sithregular;
   border-top-right-radius: 10% !important;
    border-bottom-left-radius: 10% !important;
    border-top-left-radius: 10% !important;
    border-bottom-right-radius: 10% !important;
  }

  .breadcrumb a
  {
   color: rgba(44, 1, 0, 0.73);
   font-family: 'sith_prophecyregular'; 
  }

  .breadcrumb
  {
    background-color: rgba(89, 2, 0, 0); /*fond de la bande où il est écrit "avenoel.org / forum*/
    border-color : rgba(89, 2, 0, 0)
  }

  tr.active, tr.active td
  {
    background-color: rgba(33, 6, 43, 0.73) !important; /*couleur de fond lorsque le sujet est suivi et qu'il y a une réponse*/;
    border-top-right-radius: 20% !important;
    border-bottom-left-radius: 20% !important;
    border-top-left-radius: 20% !important;
    border-bottom-right-radius: 20% !important;
  }

  .btn.btn-primary
  {
    background-color: rgba(49, 1, 0, 0.73) !important;
    font-family: lord_of_the_sithregular;
 /*fond des boutons normaux*/;
  }

  .navbar li.active a
  {
    background-color: rgb(221, 51, 51) !important;
 /*fond lorsque un des onglets de la barre de navigation au dessus est actif*/
    opacity: 0.45;
  }

  .foreground
  {
    background-image: url(https://i.imgur.com/UnjyrEF.png) !important;
    opacity: 0;
  }

  .aside audio
  {
   opacity : 0;
  }

  .aside img
  {
    display: none;
  }

  .aside a
  {
    font-family: 'sith_prophecyregular';
  }

  .aside
  {
    background-image: url(https://i.imgur.com/wIuYCDy.gif) !important;
    background-color: rgba(89, 2, 0, 0.73) !important;
    weight: 150px !important;
    height: 300px !important;
    border-top-right-radius: 30% !important;
    border-bottom-left-radius: 30% !important;
    border-top-left-radius: 0% !important;
    border-bottom-right-radius: 0% !important;
  }

  .bloc-title
  {
    color: rgb(47, 1, 0);
    font-family: 'sith_prophecyregular';
  }

  .hidden-xs.hidden-sm.date
  {
    font-family: sans-serif;
  }

  label
  {
    color: rgba(89, 2, 0, 0.73);
  }

  a, th, .topics-author, .topics-amount, .topics-date, .topic-title
  {
    color: #ffffff;
    font-family: 'conthraxsemibold';
  }

  .breadcrumb li.active
  {
    color: rgb(41, 1, 0) !important;

  }

  .row.topic-message.odd
  {
    background-color: rgba(89, 2, 0, 0.73);
 /*fond messages impairs*/;
  }

  .row.topic-message
  {
    background-color: rgba(89, 2, 0, 0.73);
 /*fond messages pairs*/;
  }

  .message-content
  {
    color: #ffffff;
  }

  .message-username a
  {
    color: #ffffff !important;
  }

  .message-infos
  {
    color: #ffffff;
  }

  .message-content-quote
  {
    background-color: rgba(89, 2, 0, 0.84) !important;
 /*fond des citations*/;
  }

  .message-content-quote a, .message-content-quote i
  {
    color: #ffffff;
  }

  .profile-wrapper, .profile-title, profile-stats
  {
    background-color: rgba(86, 4, 4, 0.19) !important;
 /*fond infos profil en dessous de l'avatar*/
    color: rgb(255, 255, 255) !important;
  }



  .profile-title
  {
    color: #ffffff !important;
  }

  section
  {
    color: #ffffff;
    border-top-right-radius: 7% !important;
    border-bottom-left-radius: 7% !important;
    border-top-left-radius: 0% !important;
    border-bottom-right-radius: 0% !important;
  }

  .pagination.pagination-topic a
  {
    color: #ffffff !important;
  }

  .chat-message, .chat-message-username a
  {
    color: #ffffff !important;
  }

  .link
  {
    font-family: sans-serif;
    color : #ffd8d8;
  }

  .aside a
  {
    display: none;
  }

  .aside li:before 
  {
    display: none !important;
  }

  .spoiler-content
  {
    background-color: rgb(141, 0, 0) !important; /*fond spoiler*/
  }

  .grey-btn.btn
  {
    background-color: rgb(65, 0, 0); /*fond des boutons restans*/
    border-top-right-radius: 50% !important;
    border-bottom-left-radius: 50% !important;
    border-top-left-radius: 0% !important;
    border-bottom-right-radius: 50% !important;
    position: relative; 
  }

  .form-control.post-content /*champ de nouveau message*/
  {
    opacity: 0.85;
    color: #360000c2;
    font-family: arial;
  }

  a:hover
  {
    color: #ffffff;
  }

  .form-group.preview
  {
    background-color: #640000; /*ond prévisualisation*/
    color: #640000;
  }

  .profile-biography
  {
    background-color: rgb(171, 11, 11) !important; /*fond profil*/
    color: #eb1010;
  }

  .profile-title
  {
   font-family: lord_of_the_sithregular;
  }

  .profile-wrapper-right td, .profile-wrapper-right tr, .profile-wrapper-right tbody, .profile-stats td, .profile-stats tr, .profile-stats tbody {
  background-color: rgba(0, 0, 0, 0);
}

  a:focus
  {
    color: #4d0000;
  }

  .admin a
  {
    color: #261849 !important;
  }

  .mod a
  {
    color: #6b2222 !important;
  }

  .active .topics-author a
  {
    color: #ffffff;
  }

  .row-form-inline
  {
   color: #400000; 
  }

  .chat-form
  {
    background-color: rgba(89, 2, 0, 0.73);
  }

  .btn-danger
  {
    background-color: rgba(48, 1, 0, 0.73); /*fond bouton suivre*/
    border-color: rgba(100, 0, 0, 0);
    font-family :'lord_of_the_sithregular';
  }

  .btn-primary
  {
    background-color: rgba(48, 1, 0, 0.73); /*fond bouton suivre*/
    border-color: rgba(52, 0, 100, 0);
    font-family :conthraxsemibold;
    color : white !important;
  }

  .special a
  {
    color: rgb(90, 5, 5) !important;
  }

  .topics-author .special
  {
    color: #0B2161;
  }

  .topics-author a
  {
    color: #ffffff;
  }

  .btn.btn-primary:hover
  {
    background-color: rgb(120, 10, 10) !important; /*couleur survol bouton*/
  }

  .btn.grey-btn:hover
  {
    background-color: rgb(118, 1, 1) !important;
  }

  .navbar li a:hover
  {
    background-color: rgb(183, 28, 28) !important; /*couleur survol navbar en haut*/
    opacity : 0.3;
  }
  
  .navbar-logo img
  {
    background-image: url(https://i.imgur.com/sV87kWg.png)!important;
    max-width: 200px;
    max-height: 41px;
    width: 100%;
    height: auto;
    padding-bottom: 41px!important;
  }

  .message-quote img
  {
    background-image: url(https://i.imgur.com/oc5NGX5.png);
    max-width: 16px;
    max-height: 16px;
    padding-right: 16px!important;
  }
  
  .message-edit img
  {
    background-image: url(https://i.imgur.com/WJi0WHB.png);
    max-width: 16px;
    max-height: 16px;
    padding-right: 16px!important;
  }
  
  .message-delete img
  {
    background-image: url(https://i.imgur.com/JJ8btj8.png);
    max-width: 16px;
    max-height: 16px;
    padding-right: 16px!important;
  }
  
  input.ddb-bouton
  {
  background-image: url(https://i.imgur.com/xg5KRGj.png);
  padding-right: 20px !important;
  max-width: 20px; /* tu indiques sa largeur */
max-height: 20px;
  }
  
  @media screen and (max-width: 640px) {
  .glyphicon 
  {
   background-size: 100%;
   background-repeat: no-repeat;
   background-position: center; 
  }
  } 
  
  .glyphicon-envelope
  {
    content: "\2709";
    background-image: url(https://i.imgur.com/oQ4koZ5.png);
    color: transparent!important;
    font-size: 17px;
    display: inline-block;
    top: 1px;
    font-style: normal;
    font-weight: normal;
    line-height: 1;
  }
  
  .glyphicon-envelope.active
  {
    content: "\2709";
    background-image: url(https://i.imgur.com/Jjsv609.png);
    color: transparent!important;
    font-size: 17px;
    display: inline-block;
    top: 1px;
    font-style: normal;
    font-weight: normal;
    line-height: 1;
  }
  
  .glyphicon-cog
  {
    content: "\2709";
    background-image: url(https://i.imgur.com/6ju62qG.png);
    color: transparent!important;
    font-size: 17px;
    display: inline-block;
    top: 1px;
    font-style: normal;
    font-weight: normal;
    line-height: 1;
  }
  
  .glyphicon-off
  {
    content: "\2709";
    background-image: url(https://i.imgur.com/M4AK2zK.png);
    color: transparent!important;
    font-size: 17px;
    display: inline-block;
    top: 1px;
    font-style: normal;
    font-weight: normal;
    line-height: 1;
  }

  .account-title
  {
   font-family: lord_of_the_sithregular;
  }

  .account-secondary-title
  {
   color: #ffffff !important;
   font-family: lord_of_the_sithregular;
  }

  .message-permalink
  {
   font-family: 'aurebeshregular';
  }

  .btn-primary
  {
   color: #069a98;
   font-family: 'lord_of_the_sithregular';
  }

  .glyphicon-refresh
  {
   color: #ffffff;
  }


  .navbar-fixed-top
  {
   background-image: url(https://www.pixelstalk.net/wp-content/uploads/images1/Desktop-dark-space-wallpaper.jpg);
  }

  .footer
  {
   background-image: url(https://i.imgur.com/DJlH7Dz.png);
  }

  .navbar-nav a
  {
   font-family: 'lord_of_the_sithregular' !important;
   color: #932020 !important;
  }

.text-muted
{
color : #ff0000 !important;
}

.text-muted a
{
color : #ff0000;
font-family : sans-serif;
}
}
