css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.twitch.tv"), domain("clips.twitch.tv") {
/* TODO :
Log in à finir et la page "Problèmes de connexion ?"

Barres blanches horizontales du chat en mode normal (sur un live seulement?)
Settings
https://www.twitch.tv/subscriptions
	refaire les notifs, cadeaux et champ de recherche pour cette page
Logo twitch dans la page de creation de clip et refaire bouttons du chat
*/

/* Research field */
.tw-input {
    background: #37285a !important;
    color: #c5c5c5 !important;
    border: solid 1px #37285a !important;
}
/* Results catergories */
.search-result-section__titlesep {
    background: #4b367c !important;
    color: #c5c5c5 !important;
}
/* Category name */
.tw-c-text-link {
    color: #c5c5c5 !important;
}

/*Twitch prime gifts */
/* bottom background */
.prime-offer-listing__content::after {
    background-image: none !important;
}

/* Notifications */
/* Top and bottom background */
.tw-c-background-alt {
    background-color: #1d1927 !important;
}
/* Read notifications background */
.persistent-notification__read {
    background-color: #1d1927 !important;
}
/* Unread notifications background */
.persistent-notification__unread {
    background-color: #3b3351;
}

/* ------------ Home page ------------ */
.anon-front__social-container {
    background-color: #19171c;
}

/* Log in */
/* Background */
.passport-modal, .tw-theme--dark .passport-modal, .tw-theme--light .passport-modal {
    background-color: #1d1927 !important;
}
/* Center background - Doesn't work because ???? */
.kraken-embed .card, .kraken-page .card, .twitch_subwindow_container .card {
    background-color: #1d1927 !important;
}
/* "Connexion" button */
.tabs > li.selected > a, .tabs > li > a.active > a, .tabs__item--active > a, .tabs__item.active > a, .tabs__item.selected > a {
    color: #c5c5c5 !important;
}
/* Labels "login" and "password" */
label {
    color: #c5c5c5 !important;
}

/* Channels followed */
.side-nav-card__avatar--offline {
    opacity: 1 !important; /* .8 by default */
    filter: none !important;
}


/* ------------ Streamer page ------------ */

/* Center Bloc */
.twilight-root {
	background-color: #110f17 !important;
    color: #c5c5c5 !important;
}
/* Text "The news feed does not contain any publications." */
.tw-c-text-alt-2 {
    color: #9e9e9e !important;
}

/* Stream title, game, viewers... bloc */
/* Background */
.channel-info-bar {
    background-color: #1d1927 !important;
}
/* Borders */
.tw-border-r {
    border-right-color: #2c2541 !important;
}
.tw-border-t {
    border-top-color: #2c2541 !important;
}

/* Horizontal menu (Streamer, videos, followers...) */
/* Background */
.channel-header {
    background-color: #1d1927 !important;
}
/* Text */
.channel-header__user {
    color: #c5c5c5 !important;
}
.channel-header__item {
    color: #c5c5c5 !important;
}
/* Log in bloc */
.tw-c-background {
    background-color: #1d1927!important;
}
/* Numbers of videos, followers and followed */
.channel-header__item-count {
    color: #855ad4 !important;
}
/* Bottom border */
.channel-header {
    border-bottom-color: #2c2541 !important;
}

/* CHAT */
/* Background chat */
.tw-c-background-alt-2 {
    background-color: #0e0c13 !important;
}
/* Messages when in Live */
.tw-c-text {
    color: #b8b5c0 !important;
    font-size: 14px !important; /* 14px ----------------------------------------------------- TEXT IN CHAT */
}

/* In normal mode when viewing a replay video (not studio) */
/* Messages */
.tw-c-text-alt {
    color: #b8b5c0 !important;
}
.vod-message.vod-message--focused, .vod-message:hover {
    background-color: #2c2541 !important;
}

/* Best cheer bloc from chat */
.pinned-cheer__bounding-box {
    background-color: #201c2b !important;
    color: #dad8de !important;
}
/* Input field */
.tw-textarea {
    border: 1px solid #392e5c !important;
    background: #0e0c13 !important;
    color: #faf9fa !important;
    font-size: 14px !important; /* 14px ----------------------------------------------------- TEXT IN CHAT */
}
/* Borders from Best cheer bloc*/
.tw-border-b {
        border-bottom-color: #2c2541 !important;
}
.pinned-cheer__bounding-box {
    border-bottom-color: #2c2541 !important;
}
.tw-border-l {
    border-left-color: #2c2541 !important;
}
/* User mention */
.chat-line__message--mention-sender {
    background-color: #4b367c !important;
}
/* Someone mentions someone (not you) */
.mention-fragment {
    background-color: #0e0c13;
}

/* ------------ Videos page ------------ */

/* Videos titles */
.video-preview-card__video-title {
    color: #c3c3c3 !important;
}
/* Sorting button */
.tw-select {
    background: #4b367c !important;
    color: #c5c5c5 !important;
}
/* Types of videos button */
.tw-button--hollow {
    background: #4b367c !important;
    color: #c5c5c5 !important;
}
/* Chat messages size */
.tw-c-text-alt {
    font-size: 14px !important; /* 14px ----------------------------------------------------- TEXT IN CHAT */
}

/* ------------ Clips page ------------ */

/* Clips titles */
.clips-preview-card__title {
    color: #c3c3c3 !important;
}
/* User who created the clip */
.clips-preview-card__display-name {
    color: #6441a4 !important;
}

/* Clips management */
/* Clips background */
.clmgr-table__row, .clmgr-table__row:nth-child(2n) {
    background: #1d1927;
}
.clmgr-table__row:hover {
    background: #302645;
}
/* Clip deleting confirmation */
.modal__content {
    color: #c3c3c3 !important;
}
/* Left menu item selected */
.dashboard-side-nav__link--active {
    background-color: #7D5BBE;
    color: white !important;
}

/* ------------ Clips creation ------------ */

/* Background */
body {
    background: #110f17;
}
/* Top background */
.editing-var-nav {
    background: #4b367c;
}
/* Top text */
.editing-var-nav__text {
    color: #c3c3c3 !important;
}
/* Imput title */
textarea.form__input {
    background-color: #1d1927;
    box-shadow: inset 0 0 0 1px #392e5c,0 0 0 transparent;
    color: #c3c3c3;
}

/* Clip published page */
/* Clips categories */
body {
    color: #9e9e9e !important;
}

/* Clips titles */
.card__title a, .theme--light .card__title {
    color: #6441a4 !important;
}
/* Clips titles HOVER */
.card__title a:hover {
    color: #472d75;
}
/* Streamer and game of the clip */
.card__info a {
    color: #a7a7a7;
    display: inline;
}
/* Top background */
.nav {
    background: #4b367c;
    border-bottom: 1px solid #392e5c;
}
/* Twitch logo */
.svg-logo_twitch {
    fill: #fff !important;
}
/* Username text */
.button--text {
    color: #dad8de !important;
}
/* Username text HOVER */
.button--text:focus, .button--text:hover {
    color: #d3cae8;
}
/* Menu on username hover */
.balloon {
    background: #1d1927;
    box-shadow: 0 0 0 1px #392e5c,0 1px 1px rgba(0,0,0,.25);
}
/* Sharing bloc (twitter, facebook...) */
.brick--block, .brick--faint, .brick--theme-white {
    background-color: #1d1927;
    border-color: #2c2541;
}
/* Top chat background */
.view-clip__meta-wrapper {
    background: #0e0c13;
}
/* Top chat bottom border */
.view-clip__bc {
    border-bottom: 1px solid #392e5c;
}
/* Bottom chat top border */
.view-clip__actions {
    border-top: 1px solid #392e5c;
}
/* Chat background */
.view-clip__main--darker {
    background: #0e0c13;
}
/* Chat messages */
.clip-chat-message {
    color: #b8b5c0 !important;
}
/* Clip title background */
.view-clip__smallTitle {
    background: #0e0c13;
	border-bottom: 1px solid #392e5c;
	color: #b8b5c0;
}
/* Chat borders */
.view-clip__meta-wrapper {
    border: 1px solid #392e5c;
}
/* Number of views */
.view-clip__views {
    color: #b8b5c0 !important;
}
/* Clip title */
.view-clip__title {
    color: #b8b5c0 !important;
}
/* Button "following messages" - Not wide enough by default in french */
.view-clip__scrollButton button {
    width: 13.5rem;
}

/* ------------ Browse ------------ */
/* Browse selection background */
.tw-c-background {
    background-color: #1d1927 !important;
}
/* Selected category */
.tw-interactable--selected {
    background: #4b367c !important;
}
/* Game title */
.tw-box-art-card__title {
    color: #6441a4 !important;
}

/* ------------ Game page ------------ */
/* Horizontal menu background */
.directory-tabs {
    background-color: #1d1927 !important;
}
/* Horizontal menu buttons : Live channels, videos, clips */
.directory-tabs__item {
    color: #9e9e9e !important;
}

/* ------------ Following ------------ */
/* Selected button : Overview, Channels, Hosts, Games, Communities */
.tw-tab-nav__item:not(.tw-tab-nav__item--disabled) a.active, .tw-tab-nav__item:not(.tw-tab-nav__item--disabled) button.active {
    color: #c3c3c3 !important;
}
/* Title, Live channels */
.live-channel-card__channel {
    color: #c3c3c3 !important;
}
/* Channel name, Live channels */
.live-channel-card__videos {
	color: #794bcc !important;
}
/* Most recent videos */
.video-preview-card__owner-display-name {
    color: #794bcc !important;
}

/* ------------ Subscriptions ------------ */
/* Channel bottom */
.ticket__benefits {
    border: 1px solid #392e5c;
	background: #17141f;
}
/* Benefits informations */
.balloon {
    color: rgba(255,255,255,.75);
}
}
