/* --------------------------------------------------------------- */
/*
 * Unofficial deviantART.com skin
 *
 * Author      : closetmonkey
 * Created     : March 6, 2009
 * Updated     : September 23, 2009
 * Version     : 1.1
 * Website     : http://closetmonkey.deviantart.com
 * Description : Modifies dAmn
 * Usage       : Use with Stylish Firefox extension (http://userstyles.org/) or copy to your Firefox userContent.css file
 */
/* --------------------------------------------------------------- */




@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("chat.deviantart.com")

{


a
{
color: #ff0000 !important;
}


#body 
{
background-image: none !important;
background-color: transparent !important;
}

.damn-lo-row-top
{
border: 0px !important;
background-color: #330000 !important;
}

.damnc-tabbar-outer
{
background-color: #330000 !important;
border-top: 0px solid red !important;
border-left: 0px solid red !important;
border-right: 0px solid red !important;
border-bottom: 1px solid #000000 !important;
}

#bar-deviant-ctrl3 
{
background-color: #000000 !important;
border: 0px !important;
}

.damncr-title
{ 
background-color: #440000 !important;
color: #990000 !important;
border: 0px !important;
}

.damncr-title a
{ 
color: #ff0000 !important;
}

.damncrc-topic
{ 
background-color: #330000 !important;
color: #dd2222 !important;
border: 0px !important;
}

.damncrc-topic a
{ 
color: #cc3333 !important;
}

.damncrc-chat-window 
{ 
border:none !important;
}


.damn .alt2, 
.damncrc-input input,
textarea.damncrc-input
{
background-color: transparent !important;
color: #cc44cc  !important;
border-color: #ff364b !important;
}

.damncrc-chat
{ 
background: #000000 !important;
color: #cc6666 !important;
border: 0px !important;
}

.damncrc-icon-roomname 
{
color: #FF0000  !important;
}


.damncr .self-hl .from span 
{
background-color: #330000 !important;
}


#bar-user, 
#bar-deviant
{ 
color:#990000 !important;
background-color: #660000 !important;
border: 0px !important;
}

.damncr-infoctr 
{ 
color: #CC3333 !important;
border: 0px !important;
}

#bar-deviant-ctrl2, 
#bar-deviant-ctrl1
{
background-image: none !important;
}

.damncri-member .bodyarea, 
.damncri-member
{
background-color: black !important;
color: #660000  !important;
border-color: red !important;
}

.damncri-member strong a
{
color: red !important;
}


.damncri-member .avatar
{
background-color: black !important;
border-color: black !important;
}


.damn-lo-col-right
{
color: #660000  !important;
}

.damn-lo-col-right dt
{
color: #880000 !important;
}

.damncr-members 
{
background-color: #660066 !important;
}


.alt0
{
background-color: #330000 !important;
}

.damncr .join, 
.damncr .part
{
color: red !important;
}

.damncr .other-hl .text span, 
.damncr .other-hl .from span
{
background-color: #660000 !important;
}

 
#content-ctrl1 .tabbar a, 
#content-ctrl1 .tabbar strong,  
#content-ctrl1 .tabbar a.active
{
background-color: black !important;
border-left-color:   #8e364b !important;
border-top-color:   #8e364b !important;
border-bottom-color:   #8e364b !important;
border-right-color:   #8e364b !important;
margin-right: 3px !important;
margin-bottom: 0px !important;
-moz-border-radius: 8px 8px 0px 0px !important;
}

#content-ctrl1 .tabbar a:hover
{
background: transparent !important;
color: white !important;
text-decoration: none !important;
}

#content-ctrl1 .tabbar,  
#content-ctrl1 .tabbar strong 
{
color: #662266  !important;
}


.damncr .msg .text a 
{
color: white !important;
}

.damncr .other-hl .text a 
{
color: white !important;
}


table.grid tr.beacon td, 
table.grid tr.beacon th
{
background-color: #2d142d !important;
}

table.grid th, table.grid td
{
background-color: #422738 !important;
border-color: #0b060d !important;
}

h2.section-head
{
background-color: transparent !important;
border: 0 !important;
border-bottom: 1px solid #8e364b !important;
}


.chatroom #top
{
margin-top: 0px !important;
background: #440000 !important;
}

.chatroom .ch-ctrl
{ margin: 0 10px !important;
padding: 0 0 0 7px !important;
background: none !important;
border: none !important;
}

#logindock .text
{
color: #440000 !important;
margin-top: 1px !important;
}

#logindock .text a
{
text-decoration: none !important;
}

#logindock .loginmenu,
#logindock .text a.blend,
#logindock .loginmenu,
#logindock .text a.bling
{
position: absolute !important;
margin-top: -9999% !important;
}

#logindock .text a,
#logindock .text span.a
{ color: #ff0000 !important;
}

.chatroom #logindock .text span:first-child
{
display: none !important;
}

#logindock .text span.a:before
{
content: "Hello " !important;
}

#logindock .text span.a:after
{
content: ", you have " !important;
}

#logindock .text #rockdock-message-count
{
margin-left: -24px !important;
}

#logindock .text #rockdock-message-count a
{
text-transform: lowercase !important;
}

#logindock .text a:hover
{
color: #d6ded4 !important;
}

#logindock .text #rockdock-message-count a:first-child:after
{
content: " and" !important;
}

#logindock .text #rockdock-message-count a:last-child:after
{
content: "." !important;
}

#logindock .text #rockdock-message-count a[title*="N"]:after,
#logindock .text #rockdock-message-count a[title*="C"]:after,
#logindock .text #rockdock-message-count a[title*="A"]:after,
#logindock .text #rockdock-message-count a[title*="R"]:after,
#logindock .text #rockdock-message-count a[title*="P"]:after,
#logindock .text #rockdock-message-count a[title*="NW"]:after,
#logindock .text #rockdock-message-count a[title*="J"]:after 
{
content:" [ "attr(title)" ] " !important;
}

}
