css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("slashdot.org")
{
	html, body, .noslashboxes {
		background-color: #222 !important;
		color: #ddd !important;
	}
	input {
		color: #fff !important;
	}
	a, .btn.link, #logo a {
		background: none !important;
		color: #aaa !important;
		text-decoration: none !important;
	}
	a:hover,
	a:visited,
	.btn.link:hover {
		color: #fff !important;
	}
	.actions {
		background-image: url("http://a.fsdn.com/sd/classic/img/glyphish-icons-16.png") !important;
		margin-top: -15px !important;
		height: 32px !important;
	}
	aside h3 {
		color: #666 !important;
	}
	.article {
		border-bottom: 1px solid #333 !important;
		border-left: 1px solid #333 !important;
		border-right: 1px solid #333 !important;
		border-radius: 5px !important;
	}
	#slashboxes section ul#dice_widget li:hover {
		background-color: #333 !important;
	}
	#slashboxes article header {
		background-color: #333 !important;
		border-left: 1px solid #333 !important;
		border-right: 1px solid #333 !important;
	}
	#slashboxes article, #jobBoardHeader {
		color: #ddd !important;
		border: 1px solid #333 !important;
		background-color: #111 !important;
	}
	#jobBoardHeader fieldset {
		border: none !important;
		background-color: #111 !important;
	}
	a.prevnextbutdis {
		border:1px solid #2a2c2f;
		color: #ddd !important;
		background-color: #45484d; background-image: -webkit-gradient(linear, left top, left bottom, from(#45484d), to(#000000));
		background-image: linear-gradient(to bottom, #45484d, #000000);
	}
	.details {
		background-color: #111 !important;
		color: #ccc !important;
	}
	#firehose article header .details a {
		color: #ccc !important;
	}
	header.h,
	body > section,
	#jobBoardWidgetSearchForm,
	.thisday-tb,
	#d2header {
		background-color: transparent !important;
	}
	#slashboxes article a,
	#slashboxes article#thisday a {
		color: #ddd !important;
	}
	#slashboxes article header,
	#slashboxes article header a {
		color: #fff !important;
	}
	#job_title,
	#firehose article,
	#firehose article header .details a:hover {
		color: #fff !important;
	}
	a.prevnextbutact {
		border: none !important;
	}
	nav.left_menu li.active a,
	.daybreak a {
		color: #333 !important;
	}
	.article .body .p > i {
		border-left-color: #555 !important;
	}
	section#besttabs {
		background-color: #111 !important;
	}
	ul.besttabslist li.active, #embbeded_login_modal, #modal_box, #modal_box:not(.no_scrollbars) .n .fadeout, #content {
		background-color: #333 !important;
		box-shadow: none !important;
		border-top: 0;
	}
	div#users.wide div.generalbody form fieldset, form.loginnm fieldset, div .generalbody fieldset {
		width: 100% !important;
	}
	#d2header .ccw-score-label .sc,
	#d2header .ccw-score-label > div {
		background-color: transparent !important;
		color: #fff !important;
	}
	#newa2footerv2 .ns h3 a:hover,
	#newa2footerv2 .pr h3 a:hover,
	#newa2footerv2 .ns h3 a:visited,
	#newa2footerv2 .pr h3 a:visited {
		color: #f00 !important;
		text-decoration: none !important;
	}
	h2.story, h2.wnodnixw, h2.firehose, label, #newa2footerv2 h3, .panel, .well, .h3, .sc-bundles-sold-digit {
		background: none !important;
		background-color: #333 !important;
	}
	.message-bar {
		background-color: #111 !important;
		border-right: 1px solid #333 !important;
		border-bottom: 1px solid #333 !important;
		border-left: 1px solid #333 !important;
		margin-bottom: 25px !important;
	}
	.nav-secondary-wrap, blockquote.msg > p {
		background-color: #333 !important;
		color: #ddd !important;
	}
	blockquote.msg .slant {
		border-color: transparent transparent #333 !important;;
	}
	.message-bar::before {
		border-bottom: 10px solid #111;
	}
	.nav-label {
		color: #fff !important;
	}
	.nav-wrap, #d2header > div > nav, .collapse, .navbar {
		background: none !important;
		background-color: #555 !important;
		border-top-left-radius: 10px !important;
		border-top-right-radius: 10px !important;
		box-shadow: none;
	}
	#comments h4 {
		background: none !important;
		background-color: #555 !important;
		border-top-left-radius: 0 !important;
		border-top-right-radius: 0 !important;
	}
	body > section {
		border-left: 1px solid #333 !important;
		border-right: 1px solid #333 !important;
	}
	section.bq {
		border: none !important;
	}
	footer.nf {
		background: none !important;
		background-color: #555 !important;
		border-bottom-left-radius: 10px !important;
		border-bottom-right-radius: 10px !important;
		box-shadow: none;
	}
	form input[type="text"], form input[type="tel"], form input[type="email"], form input[type="password"], form textarea {
		background: none !important;
		background-color: #333 !important;
		color: #fff !important;
		border: 1px solid #ccc !important;
	}
	form input[type="text"]:focus, form input[type="tel"]:focus, form input[type="email"]:focus, form input[type="password"]:focus, form textarea:focus {
		box-shadow: none !important;
		border: 1px solid #fff !important;
	}
	#comments .full, .d2 .oneline .ind {
		border-color: #444 !important;
	}
	.comment-bubble {
		background-color: #4a4a4a !important;
	}
	span.comment-bubble::after {
		border-color: #4a4a4a transparent transparent !important;
	}
	.nav-site > li::before, .nav-site > li::after {
		color: #555 !important;
		border-left: none !important;
		border-right: none !important;
	}
	.nav-wrap a:hover, .nav-site > li .filter-firehose:hover, .publisher-slashdot .navbar-default .navbar-nav > .nav-item > a:hover {
		background-color: #222 !important;
	}
	.nav-label {
		color: #ccc;
	}
	.btn-success, .dicegreenbutton, .btn-view-more, .prevnextbutact, .btn, .btn-default, .polls-results a, div.poll-controls button.btn-polls, div#modal_box_content form#modal_prefs fieldset input.fno, input[type="submit"] {
		box-shadow: none !important;
		background: none !important;
		background-color: #333 !important;
		border-color: #aaa !important;
		color: #fff !important;
	}
	button[disabled="disabled"], input:disabled {
		background: #4d1100 none repeat scroll 0% 0% !important;
		color: #ff5327 !important;
		border: 1px solid #ff5327 !important;
	}
	button[disabled="disabled"]:hover, input:disabled:hover {
		background: #ff5327 none repeat scroll 0% 0% !important;
		color: #4d1100 !important;
		border: 1px solid #4d1100 !important;
	}
	.input-group {
		margin-left: -50px !important;
		margin-right: 50px !important;
		width: auto !important;
	}
	.btn-success.active, .btn-success.focus, .btn-success:active, .btn-success:focus, .btn-success:hover, .polls-results a:hover, div.poll-controls button.btn-polls:hover, .btn:hover, .selected:hover {
		background-color: #333 !important;
		border-color: #fff !important;
	}
	.menu-trigger {
		background-color: #4a4a4a !important;
		color: #ccc !important;
		border: #111 !important;
		border-radius: 3px !important;
	}
	.menu-trigger:hover {
		color: #fff !important;
	}
	.share-icons {
		background-color: rgba(17, 17, 17, 0.8) !important;
		color: #ccc !important;
		border-radius: 4px !important;
	}
	.menu-share::before, .menu-share::after, #bottomadspace {
		display: none !important;
	}
	table, thead tr th, .diceheader {
		background: transparent none repeat scroll 0% 0%;
		background: #3b3b3b !important;
	}
	tbody tr {
		border: none !important;
	}
	td {
		padding-left: 5px !important;
	}
	.dicebutton, .dicebottom {
		background-color: #333 !important;
	}
	table.thisday-tb tbody tr td.thisday-yr {
		color: #aaa !important;
		padding-right: 5px !important;
		padding-left: 5px !important;
	}
	.thisday-tb tr td, table tr td {
		background: none repeat scroll 0% 0% #3b3b3b !important;
		border: none !important;
	}
	.thisday-tb tr td.thisday-yr, table tr td {
		background: none repeat scroll 0% 0% #3b3b3b !important;
	}
	.thisday-tb tr:nth-child(even) td.thisday-yr, table tr:nth-child(even) td {
		background: none repeat scroll 0% 0% #222 !important;
	}
		.thisday-tb tr:nth-child(odd) td, table tr:nth-child(odd) td {
		 background: none repeat scroll 0% 0% #333 !important;
	}
	.thisday-tb {
		border-radius: 3px !important;
		border: 1px solid transparent !important;
	}
	#announcement {
		background-color: #111 !important;
		border: none !important;
	}
	.before .slant {
		background: none !important;
		background-color: #222 !important;
		border-top: 1px solid #aaa !important;
		border-right: 1px solid #aaa !important;
	}
	.after .slant {
		background: none !important;
		background-color: #222 !important;
		border-bottom: 1px solid #aaa !important;
		border-left: 1px solid #aaa !important;
	}
	.tcenter {
		background-color: #222 !important;
	}
	.tcenter a {
		color: #aaa !important;
		font-size: 1.3em !important;
	}
	.tcenter:hover {
		color: #fff !important;
	}
	li.selected {
		background: none !important;
		background-color: #333 !important;
		border-radius: 5px !important;
		padding: 4px !important;
		margin-top: 3px !important;
		box-shadow: none !important;
		border: 1px solid #aaa !important;
		color: #fff !important;
	}
	.poll-bar-bg {
		background-color: #555 !important;
	}
	.poll-bar-fill {
		background-color: #111 !important;
	}
	.horizontal #ccw-full-panel {
		background: #000 none repeat scroll 0% 0% !important;
	}
	.horizontal #ccw-abbr-panel {
		background: #808080 none repeat scroll 0% 0% !important;
	}
	.horizontal #ccw-hide-panel {
		background: #fff none repeat scroll 0% 0% !important;
	}
	#modal_box:not(.no_scrollbars) #modal_box_content {
		margin: 20px !important;
	}
	#modal_box.join {
		overflow-y: hidden !important;
	}
	.janrainEngageEmbed, .janrainContent, .janrainHeader, .janrainProviderPages, .janrainPage ul li {
		background-color: #333 !important;
		color: #aaa !important;
		border: 0 !important;
	}
	div.busy.genericspinner > span {
		box-shadow: rgba(66,66,66,.75) 0 0 5px !important;
		background:rgb(66,66,66) url(https://a.fsdn.com/sd/spinner2.gif) no-repeat 8px 3px !important;
	}
	.poll-group-form {
		padding: 0px !important;
	}
	.units-6 {
		width: 100% !important;
	}
	#dice_logo_sm, #sourceforge2, #slashdot_deals, #firehose-000, .prevnextbutdis, .fadeout, #modal_box #logo a {
		display: none !important;
	}
	.poll-feed .half article .feed-header {
		background-color: #111 !important;
	}
	.info-meta, .info-byline {
		background-color: #333 !important;
		color: #ccc !important;
	}
	.notice {
		background: #4d1100 none repeat scroll 0% 0% !important;
		color: #ff5327 !important;
		border-color: #ff5327 !important;
	}
	#fhft .btn {
		margin-top: 1px !important;
	}
	.story-sourcelnk {
		color: #aaa !important;
		font-weight: bold !important;
	}
	.story-sourcelnk:hover:after {
		content: "↗" !important;
		color: #fff !important;
	}
}
