css:
@-moz-document domain("freefeed.net") {
@font-face 
{
    font-family: Vazir;
    src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/61a73a5c/dist/Farsi-Digits/Vazir-FD.eot?raw=true');
    src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/61a73a5c/dist/Farsi-Digits/Vazir-FD.eot?#iefix') format('embedded-opentype'), url('https://cdn.rawgit.com/rastikerdar/vazir-font/61a73a5c/dist/Farsi-Digits/Vazir-FD.woff') format('woff'), url('https://cdn.rawgit.com/rastikerdar/vazir-font/61a73a5c/dist/Farsi-Digits/Vazir-FD.ttf') format('truetype');
    font-weight: normal;
}

body
{
    font-family: Vazir;
    background: #141d26;
    background-size: auto;
    background-attachment: fixed;
    color: #fff;
}

.sidebar a,
.send-to a
{
    color: #8899a6;
}

.logged-in .avatar img
{
    box-sizing: content-box;
    background-color: #1b2836;
    border-radius: 50%;
    margin: -30px 0 0 8px;
    padding: 1px;
    vertical-align: bottom;
}

.sidebar .logged-in
{
    border: 1px solid #000;
    background: #1b2836;
    margin-bottom: 10px;
}

.sidebar .logged-in .user .author a
{
    color: #fff;
}

.site-logo-link:active,
.site-logo-link:focus,
.site-logo-link:hover,
.site-logo-link:visited
{
    color: #1da1f2;
}

.search-form .search-input
{
    background-color: #182430;
    border-radius: 21px;
    border: 1px solid #141d26;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    color: #fff;
    display: block;
    font-size: 12px;
    height: 32px;
    line-height: 16px;
    padding: 8px 32px 8px 12px;
    transition: all .2s ease-in-out;
    width: 100%;
}

.search-form .search-input:focus
{
    outline: none;
    border: 2px solid #1da1f2;
}

.search-form .search-button
{
    background-color: transparent;
    color: #8899a6;
    height: auto;
    width: auto;
    cursor: pointer;
    position: absolute;
    left: 20px;
    top: 37px;
    border: none;
}

.box
{
    background-color: #1b2836;
    background-clip: padding-box;
    border: 1px solid #000;
    line-height: inherit;
    margin-bottom: 10px;
    border-radius: 0;
}

.sidebar .box-body ul li a
{
    color: #8899a6;
}

.sidebar .box-body ul li a:hover,
.send-to a:hover,
.Select-value-label-standalone
{
    color: #1da1f2;
}

.sidebar .box-footer a
{
    color: #8899a6;
}

.box-header-info
{
    float: left;
}

.sidebar .box-body .year-links-row a
{
    color: #8899a6;
}

.sidebar .box-body .year-links-row a:hover,
ul.tile-list li .user-actions a
{
    color: #1da1f2;
}

.box-footer p
{
    color: #8899a6;
}

.post-editor .post-textarea
{
    background-image: none;
    width: 100%;
    max-width: 100%;
    margin-bottom: 0;
    margin-top: 5px;
    background: #141d26;
    border: 1px solid #000;
    border-radius: 8px;
    line-height: 20px;
    border-color: #1B4665;
    color: #fff;
    padding: 8px;
}

.content .box .box-message
{
    padding: 0;
    border-radius: 0;
}

.create-post
{
    background-color: #1B3448;
    border-bottom: 1px solid #000;
}

.post
{
    background: #1b2836;
    background-clip: padding-box;
    border-bottom: 1px solid #000;
}

.post .post-header a
{
    word-break: break-all;
    color: #fff;
    font-size: 14px;
    font-weight: bold;
}

.post a
{
    color: #8899a6;
}

.post .post-body
{
    color: #fff;
}

.comment .comment-textarea
{
    width: 100%;
    max-width: 100%;
    margin-bottom: 0;
    background: #141d26;
    border: 1px solid #000;
    border-radius: 8px;
    line-height: 20px;
    border-color: #1B4665;
    color: #fff;
    padding: 8px;
}

.post-userpic a img
{
    border: none;
    width: 48px;
    height: 48px;
    border-radius: 50%;
    -moz-force-broken-image-icon: 1;
}

button.btn.btn-default.btn-xs.comment-post
{
    border-radius: 100px;
    box-shadow: none;
    cursor: pointer;
    font-size: 14px;
    font-weight: bold;
    line-height: 20px;
    padding: 6px 16px;
    position: relative;
    text-align: center;
    white-space: nowrap;
    background-color: #1da1f2;
    border: 1px solid #1da1f2;
    color: #fff;
}

.post-edit-actions button.btn.btn-default.btn-xs
{
    border-radius: 100px;
    box-shadow: none;
    cursor: pointer;
    font-size: 14px;
    font-weight: bold;
    line-height: 20px;
    padding: 6px 16px;
    position: relative;
    text-align: center;
    white-space: nowrap;
    background-color: #1da1f2;
    border: 1px solid #1da1f2;
    color: #fff;
}

.post .post-body .post-text a
{
    color: #1da1f2;
    ;
}

.sidebar .box
{
    padding: 15px;
}

.box-header,
.box-header-empty,
.box-header-timeline,
.box-header-friends,
.box-header-groups,
.box-header-search
{
    border-bottom: none;
}

.profile-stats-item a,
.subscribe-controls a
{
    color: #1da1f2;
}

.post a:hover
{
    color: #1da1f2;
}

.profile .description
{
    color: #fff;
}

.profile,
.profile-controls
{
    border-bottom: 1px solid #000;
}

.timeline-post.direct-post
{
    background-color: #eee0;
    margin: 0;
    border-radius: 0;
    border-bottom: 1px solid #000;
    background-position: top .5em left .5em;
}

.post-editor div
{
    padding: 0px 12px;
}

.Select-placeholder,
:not(.Select--multi) > .Select-control .Select-value
{
    background-color: #141d26;
    color: #8899a6;
    border: 1px solid #000;
}

ul.tile-list li .user-name-wrapper > a,
ul.tile-list li .avatar > a
{
    color: #fff;
}

footer a,
footer a:hover,
footer a:focus
{
    color: #8899a6;
}

.filter a
{
    color: #8899a6;
}

.user-name-wrapper a,
.Linkify a,
.single-notification a,
.mention a,
.box-header-timeline a,
.p-pagination-controls a,
.expandable .expand-panel .expand-button,
.box-message a
{
    color: #1da1f2;
}

.user-name-wrapper a:hover,
.Linkify a:hover,
.single-notification a:hover,
.mention a:hover,
time,
.box-header-timeline a:hover,
.p-pagination-controls a:hover,
.expandable .expand-panel .expand-button:hover,
.box-message a:hover
{
    color: #8899a6;
}

.box.notifications
{
    padding: 0 20px;
}

.box-header-timeline .sidelinks
{
    float: left;
}

.content .box-footer
{
    background-color: #fff0;
}

.search-memo
{
    background-color: #f2f2f200;
}

.expandable .expand-panel
{
    right: 20px;
    background-color: #1c2836;
}

.profile,
.profile-controls
{
    padding-left: 14px;
    padding-right: 14px;
}

.post-editor .post-edit-options .post-edit-more-trigger
{
    color: #fff;
}

.alert-info
{
    background-color: #f2f2f200;
    border-top: #000 1px solid;
    border-bottom: 1px #000 solid;
    color: #1ba1f2;
}

.col-md-1,
.col-md-2,
.col-md-3,
.col-md-4,
.col-md-5,
.col-md-6,
.col-md-7,
.col-md-8,
.col-md-9,
.col-md-10,
.col-md-11,
.col-md-12
{
    float: right;
    direction: rtl;
}

.sidebar .logged-in .avatar
{
    float: right;
    margin-left: 8px;
}

.sidebar .logged-in
{
    padding-bottom: 10px;
}

.logged-in .user
{
    margin-top: 4px;
}

.box-header-timeline
{
    font-size: 14px;
    color: #ffffff;
    text-align: right;
    padding: 0 20px;
}

.comments
{
    font-size: 12px;
}

.post .post-body .post-text
{
    font-size: 14px;
    color: #fff;
}

.post .post-body .post-header a
{
    padding-left: 5px;
    padding-right: 5px;
    direction: rtl !important;
}

.pull-right
{
    float: left!important;
}

.comment-likes-container
{
    position: absolute;
    right: -35px;
    left: 0 !important;
}

.box-message
{
    margin: 0 0 0px 0px !important;
    padding: 10px 10px !important;
}

.post .post-body .post-footer .post-lock-icon
{
    margin-left: 5px;
}

.post-likes .icon
{
    float: right !important;
    color: #ff0000 !important;
    padding-left: 5px;
}

.content .box-body
{
    padding: 0;
    background-color: #fff0
}

.post .post-userpic
{
    float: right;
    margin-right: 5px;
    border: none;
}

.box-header-friends
{
    color: #ffffff;
    text-align: right;
    border: none;
}

.profile .description
{
    font-family: Vazir;
}

.sidebar .box-body
{
    background-color: #1b2836;
}

.box-header-groups
{
    color: #ffffff;
    text-align: right;
    border: none;
}

.box-header-search
{
    color: #ffffff;
    border: none;
}

.post .post-body
{
    margin-right: 63px;
}

.post .post-body .post-text
{
    direction: rtl;
}

.post .post-body .post-header
{
    direction: rtl !important;
}

.posts
{
    direction: rtl;
}

.likes .icon
{
    float: right;
    color: #ff1414;
}

.comment .comment-icon
{
    float: right;
    margin-left: 3px;
    margin-right: 0px !important;
    text-decoration: none;
}

.post-editor .post-textarea
{
    direction: rtl;
}

.post-editor .post-edit-actions
{
    text-align: left;
    margin-bottom: 8px;
}

.post-editor .post-edit-options
{
    float: right;
    margin-bottom: 11px;
}
}
