    position: fixed;
    top: 20px;
    left:calc(20% + 135px);
    width: 16px;
    height: 16px;
    background: #fff ;
    z-index: 2;
}
#nav ul.menu {
    text-align: right;
    width: 90%;
    margin-left: 5%;    
    padding-top: 0;
    border-bottom: 7px solid #E8FF8C;
    text-shadow: none;
}
#nav ul.menu li a, #nav ul.menu li.selected a{  
    color:#008891;    
    height: 50px;
    font-size:16px;
    font-weight: normal; 
    border:none;
    box-sizing: border-box; -moz-box-sizing:border-box;
    background: none;
    padding: 15px 2% 0 ;    
    transition: .2s all ease-in-out;
    text-decoration: none;
}
#nav ul.menu li.selected a{
    color:#008891;
    font-weight: normal;    
    border-bottom: 7px solid #E8FF8C;
}
#nav ul.menu li a:hover, #nav ul.menu li.selected a:hover{
    background: #E8FF8C;
    color:#008891;
    font-weight:normal;
}


