/*  InfoBlock */
#info_block{
    border-bottom: solid 2px rgba(128,128,128,0.4);
    padding-bottom: 10px;
    text-align:center;
}
#info_block td{	 
    border: none;    
    filter: saturate(0.2) opacity(0.8); -webkit-filter: saturate(0.2) opacity(0.8);
}
#info_block td:hover{	 
  
    filter: saturate(0.8) opacity(1); -webkit-filter: saturate(.8) opacity(1);
}
font.color_bonus:before, font.color_invite:before, font.color_uploaded:before, font.color_downloaded:before, font.color_slots:before, font.color_connectable:before, font.color_active:before, a#task_progress:before {
