/*  Torrents */
#outer{
	border: none;
	padding: 20px;
   margin-top: 0;
    padding-top: 0 !important;
}
table.torrents, table.torrentname{	
    background-color: #C7E1F0;
}
td.colhead{
	background-color: #E8FF8C;
    border: none;
    color: #008891;
}
table.torrents>tbody>tr{  
    display: table-row;
    height:70px;
}
table.torrents>tbody>tr>td{          
	border-top: solid 2px rgba(128,128,128,0.4);
    border-bottom: solid 2px rgba(128,128,128,0.4);
}


/*  Hide */
table.torrents>tbody>tr:first-child{             /*   hide colhead   */
	display: none;
}

table.searchbox{                                   /*   hide content of searchbox   */
    height:0;
    display: block;
    overflow: hidden;
}

table.searchbox td.rowfollow table tbody tr:last-child td.embedded{           /*   recommend   */
    position: absolute;
    display: table;
    width: 100%;
    top: 98px;
    left: 0;
    white-space: nowrap;
    font-size: .5em;
}
table.searchbox td.rowfollow table tbody tr:last-child td.embedded{
    text-align: center;
}
table.searchbox td.rowfollow table tbody tr:last-child a{
    text-decoration: none;
    color: gray;
    border: solid 1px gray;    
    border-radius: 10px;
    padding-left: 4px;
    padding-right: 4px;
}
table.searchbox td.rowfollow table tbody tr:last-child a u{
    text-decoration: none;
}

td.embedded p:nth-child(6){                                /*   hide buttons of next page   */
    display: none;
}



/* ToTOP */
#gotop {
    top: 368px;    
    width: 60px;
    right: calc(6% - 50px);
    background-color: #b7daa1;
    height: 35px;
    border-radius: 0;
    z-index:1000;
    box-shadow: 0 2px 20px rgba(0, 0, 0, 0.4);
    overflow:hidden;
}

a#gotop:before{
	 content:"ᕙ(⇀‸↼”)ᕗ";
    display:block;
    height:50px;
    font-size:8px;
    color: #008891;
    padding-top: 10px;
    margin-bottom:10px;
    margin-left: 0;
}
a#gotop:hover{
