css:
@-moz-document domain(tjupt.org) {
/*  Background */
body {
    font-family: 'Lucida Grande', Lucida Sans Unicode, Hiragino Sans GB, WenQuanYi Micro Hei, Verdana, Aril, sans-serif;
    background-color: #B3D1C1;
    background-position: center 80px;
    background-image: none;
    background-repeat: no-repeat;
    background-attachment: fixed;
}
table.head {
    margin-top: 260px;
    width: 1080px;
}

/*  Mainouter */
table.mainouter {
    margin-top: -5px;
    padding: 10px;
    background: #C7E1F0;
    width: 91%;
    position: relative;
    box-shadow: 2px 2px 5px rgba(37, 109, 192, .4);
    border: none;
}

.logo_img img {
    width: 100%;
    position: fixed;
    max-width: 1120px;
    margin: 0 auto;
    left: 0;
    right: 0;
    top: 50px;
}

/*  Ad */
#ad_belownav span font {
    font-size: 12px !important;
    color: gray;
    font-weight: normal;
}

/*  Navbar */
#nav {
    box-shadow: 2px 2px 20px rgba(37, 109, 192, .4);
}

#nav ul.menu:after {
    content: "北洋园PT";
    /* some words here */
    font-size: 1.8em;
    position: fixed;
    color: #008891;
    top: 10px;
    left: 6%;
    z-index: 1;
    font-weight: bold;
}

#nav_block {
    display: block;
}
#nav_block table.main {
    position: fixed;
    top: 0;
    left: 0;
    background: #C7E1F0;
    text-align: center;
    z-index: 1;
}
input#searchinput {
    /* move searchbox to the navbar    */
    height: 17px;
    border: solid 2px #E8FF8C;
    background-color: white;
    position: fixed;
    top: 17px;
    left: 20%;
    border-radius: 10px;
    width: 150px !important;
    padding: 0 7px;
    margin: 0;
    outline: none;
    z-index: 2;
}
#suggcontainer {
    position: fixed;
    top: 38px;
    left: calc(20% + 7px);
    z-index: 2;
}
#suggestions {
    border: none !important;
    width: 155px !important;
}


#nav ul.menu {
    text-align: right;
    width: 90%;
    margin-left: 5%;
    padding-top: 0;
    border-bottom: 7px solid #E8FF8C;
    text-shadow: none;
}
#nav ul.menu li a,
#nav ul.menu li.selected a {
    color: #008891;
    height: 50px;
    font-size: 16px;
    font-weight: normal;
    border: none;
    box-sizing: border-box;
    -moz-box-sizing: border-box;
    background: none;
    padding: 15px 2% 0;
    transition: .2s all ease-in-out;
    text-decoration: none;
}
#nav ul.menu li.selected a {
    color: #008891;
    font-weight: normal;
    border-bottom: 7px solid #E8FF8C;
}
#nav ul.menu li a:hover,
#nav ul.menu li.selected a:hover {
    background: #E8FF8C;
    color: #008891;
    font-weight: normal;
}


/*  Menu */
:root {
    --top: 80px;
    --space: 35px;
}

#nav #mainmenu li:nth-child(n+7) {
    position: fixed;
    display: block;
    background-color: #b7daa1;
    height: 25px;
    width: 60px;
    top: var(--top);
    right: calc(5% - 50px);
    border-radius: 5px;
    box-shadow: 0 0px 10px rgba(37, 109, 192, .4);
}

#nav #mainmenu li:nth-child(n+7) a {
    display: block;
    font-size: 12px;
    height: 25px;
    width: 60px;
    padding: 6.5px 3px;
    color: #008891;
}
#nav #mainmenu li.selected:nth-child(n+7) a {
    display: block;
    height: 35px;
    width: 60px;
    padding: 10px 3px;
    background-color: #E8FF8C;
}
#nav #mainmenu li:nth-child(8) {
    top: calc( var(--top) + var(--space));
}
#nav #mainmenu li:nth-child(9) {
    top: calc( var(--top) + var(--space) * 2);
}
#nav #mainmenu li:nth-child(10) {
    top: calc( var(--top) + var(--space) * 3);
}
#nav #mainmenu li:nth-child(11) {
    top: calc( var(--top) + var(--space) * 4);
}
#nav #mainmenu li:nth-child(12) {
    top: calc( var(--top) + var(--space) * 5);
}
#nav #mainmenu li:nth-child(13) {
    top: calc( var(--top) + var(--space) * 6);
}


#nav_block {
    border: none;
    padding: 10px 20px;
    margin-bottom: 15px;
}
#nav_block table,
table.main {
    background: none;
    width: 100%;
    border-collapse: separate;
}



/*  InfoBlock */
#info_block {
    border-bottom: solid 2px rgba(128, 128, 128, 0.4);
    padding-bottom: 10px;
    text-align: center;
}
#info_block td {
    border: none;
    filter: saturate(0.2) opacity(0.8);
    -webkit-filter: saturate(0.2) opacity(0.8);
}
#info_block td:hover {

    filter: saturate(0.8) opacity(1);
    -webkit-filter: saturate(.8) opacity(1);
}
font.color_bonus:before,
font.color_invite:before,
font.color_uploaded:before,
font.color_downloaded:before,
font.color_slots:before,
font.color_connectable:before,
font.color_active:before,
a#task_progress:before {
    content: '\00a0 \00a0 ';
}



/*  Torrents */
#outer {
    border: none;
    padding: 20px;
    margin-top: 0;
    padding-top: 0 !important;
}
table.torrents {
    margin-top: -30px;
}
table.torrents,
table.torrentname {
    background-color: #C7E1F0;
}
td.colhead {
    background-color: #E8FF8C;
    border: none;
    color: #008891;
}
table.torrents > tbody > tr {
    display: table-row;
    height: 70px;
}
table.torrents > tbody > tr > td {
    border-top: solid 2px rgba(128, 128, 128, 0.4);
    border-bottom: solid 2px rgba(128, 128, 128, 0.4);
}


/*  Hide */
table.torrents > tbody > tr:first-child {
    /*   hide colhead   */
    display: none;
}

table.searchbox {
    /*   hide content of searchbox   */
    height: 0;
    display: block;
    overflow: hidden;
}

table.searchbox td.rowfollow table tbody tr:last-child td.embedded {
    /*   recommend   */
    position: absolute;
    display: table;
    width: 100%;
    top: 80px;
    left: 0;
    white-space: nowrap;
    font-size: .5em;
}
table.searchbox td.rowfollow table tbody tr:last-child td.embedded {
    text-align: center;
}
table.searchbox td.rowfollow table tbody tr:last-child a {
    text-decoration: none;
    color: gray;
    border: solid 1px gray;
    border-radius: 10px;
    padding-left: 4px;
    padding-right: 4px;
}
table.searchbox td.rowfollow table tbody tr:last-child a u {
    text-decoration: none;
}

td.embedded p:nth-child(6) {
    /*   hide buttons of next page   */
    display: none;
}


/*  Footer */
#footer {
    padding-top: 30px;
    color: gray;
    opacity: .5;
}


/* details */
td.rowfollow,
td.embedded,
td.embedded table {
    /*   main content    */
    border-left: transparent;
    border-right: transparent;
    background-color: #C7E1F0;
}
h2 {
    background: none;
    background-color: #efffb1;
    border: none;
}
td.text {
    padding-left: 0;
    padding-right: 0;
}
td.text > div {
    margin-left: 0 !important;
    margin-right: 0;
}
td.rowfollow {
    border: none;
}
table {
    background-color: #c7e1f0;
}
input.btn {
    width: 60px;
    height: 40px;
    background-image: none;
    background-color: #efffb1;
    border: none;
    color: #008891;
}
}
