@-moz-document domain(new.pikabu.ru), domain(pikabu.ru)
{
	body, .calendar-head__buttons, .calendar-head, .b--toggle__icon, .b-comments, .b-comment, .comment-reply, .comment__children, .b-story-blocks, .b-comments_type_main, .comments, .story__footer, section, .section, tbody, table, tr, td, .section_gray, .section_none, .comment-reply__controls, .comment__children::before, .settings-saves__categories, .settings-saves__remove, .collapse-button, .input__icon, .settings-saves__form, .cedit-story__head, .comments-navigator__refresh, .comments__toggle-answers, .footer, .input_comment-reply, .settings-saves__categories, .section-group, .footer, .community-info-block__rules, .tags-search__form, .b-comment__body_highlight_newest:before, .b-user-menu-list__count, table#header_t .menu li, .menu, .comment-toggle-children__icon, .comment-reply__attach-files:hover, .button-group_green>button:last-of-type:hover, .button-group_green>button:hover, .header__main, header > .header__main, .user_right-sidebar .avatar, .user_right-sidebar .avatar img, .pagination__page, .notifications-hint__more, .popup__container,  .profile__communities .communities-list .community, .story .yandex-direct,  .sidebar-block__header, .sidebar-block_border, .page-story__story .story__title-link_visited, h3, .sidebar-toggle__title, .communities-feed .community__title a, .static-page__container>div, .static-page__container>h1, .static-page__container>a, .toast, .story-image, .img, img, .story-editor__blocks, .footer__main, .profile__award, aside>div, aside>div>div, h4 {
		background-color: #1f1f1f !important;
		color: #999 !important;
	}

	blockquote {
    	background-color: #262626 !important;
        color: #666 !important;
	}

	.b-community-info .community-rules,  #blk-upcoming .a-selected, .pager ul li.no_border a:hover, .pager ul li a:hover, .pager ul li.no_border a, .pager ul li a, table.footer-table, .footer_links2, .b-feed-panel, .b-story__show-all:hover, .b-story__content_type_text, div.menu-block-branding td.menu-best-comm, tbody, div.menu-block-branding td.menu-block-title, td.menu-block-title, .b-social, .user .avatar_op, .avatar_note .avatar__inner, .story__user .avatar img, .profile_short, .page-settings__table-email, .select__icon, .static-page__more, html, .static-page__more, body, .button, .header-right-menu__search, .header-right-menu__item, .button-group>button, .left>button {
		background-color: #2e2e2e !important;
		color: #999 !important;
	}
	
	.section_header h4, .rating_bl, .select select,  .profile__user-name a, .profile__digital b, .story__title-link, .sidebar-block__header h4, .comment__header, .community-header__title>a, button, .profile__nick a, .static-page__heading, .community__title, .button_link, a.tags__tag:hover, a.tag:hover, .story-save__name {
		color: #999 !important;
	}

	.radio_checked {
    	color: #1f1f1f !important;
	}

	.comments-navigator__count {
		background-color: #1f1f1f !important;
		color: #6c9b45;
		border: 1px solid #333 !important;
    }
	.comments-navigator__count::after {
        border-left-color: #1f1f1f;
	}

	.tags__tag_highlight, .tag_highlight, .page-story__comments .section__right[data-role=order], .story__rating-count, .link, .user__nick {
		color: #ddd !important;
	}
	
	.stories-feed__load-before:hover{
    	background-color: #555 !important;
	}

	table#header_t .menu .active2 a, table#header_t .menu .active2 a:hover, .b-community-info>.community-buttons a[data-role=subs-], .comment__tool_active, .comment__tool:hover, .button_green, .comment__user[data-own-story=true] .user__nick, .button-group_green>button:last-of-type, .button-group_green>button,  button:hover, .button:hover, .comments_show>button, .stories-feed__load-before {
		background-color: #333 !important;
		color: #999;
	}
	
	.button_green:hover {
		background-color: #6c9b45 !important;
		color: #fff !important;
	}

	.communities-list__line {
		background-color: #333;
	}

	.section-hr__title::before, .section-hr__title::after, .saved-stories__categories>button {
		background-color: #555;
	}
	
	table#header_t .menu .active2 a, table#header_t .menu .active2 a:hover {
		background: #333 !important;
	}
	
	.story__sponsor {
		background: rgba(255,213,79,.4);
		color: #fff;
	}
	
	.comments__container_highlight_yellow:before, .comment__body_highlight_yellow:before, .comment-toggle-children_collapse.comment-toggle-children_highlight_yellow:before {
		background: rgba(255,255,255,0.05);
		color: #999;
		border-left: 2px solid #6c9b45;
	}
	
	.story__tag {
		background: linear-gradient(to top,#000 100%,#000 100%) !important;
	}
	
	.b-page-header-v2>span>a, .story__left img, .b-user-menu__logo:after, .b-user-menu__logo:before, .popup__arrow {
		display: none !important;
	}
	
	.comment-toggle-children__icon {
		border: 1px solid #333 !important;
		color: #999 !important;
	}

	.comment-toggle-children_collapse>.comment-toggle-children__icon {
		border-color: #6c9b45 !important;
		color: #fff !important;
	}
	
	.story__scroll-wrapper_enabled:before, .story__scroll-next-btn:before, .story__read-more:before, .b-story__show-all:before {
        background-image: none !important;
		background: linear-gradient(to bottom,transparent 50%,#111 100%)  !important;
	}

	.scroll-up-button:hover{
		background: linear-gradient(to top,transparent 50%,#111 100%)  !important;
	}

	.input, .input__input, .awards-feed__caption, .table_standard, tr:before, tr:after, .story__read-more, .story .b-story__show-all {
		background-color: rgba(50,50,50,0) !important;
        color: #ddd !important;
	}

 	.input_section, .story__content-inner, .story__main, .b-story__show-all, .stories-search-group__button>a {
		background-color: #1f1f1f !important;
	}

	.sidebar-block-discussed button, .b-comment__control {
		background-color: rgba(50,50,50,0) !important;
		color: #999 !important;
	}

	.menu .active2 a, .menu .active2 a:hover, .b-community-info>.community-buttons a[data-role=subs-], .story__tag, yatag, .pager ul li a:hover, .pager ul li.no_border a:hover, .pager ul li a, .b-story__content_type_text, div.menu-block-branding td.menu-block-title, div.menu-block-branding, .b-community-line+hr, .rating_bl, div.menu-block, td.menu-block-title, .b-story-info, .b-story-blocks_with-border, .b-user-menu__avatar, .b-user-menu__header, .b-user-menu__main, .pagination__page_more, .notifications-hint__more, .section-group>section[style*="display: none"]:first-of-type+section, .section-group>section[style*="display: none"]:first-of-type+section[style*="display: none"]+section, .story__main, .static-page__container>h1, .static-page__container>a, .static-page__container>div, .static-page__more, .sidebar-block, .settings-saves__add-b, .select, .collapse-button, .comment-reply_border, .input, .settings-saves__form, .cedit-story__head, .sidebar-block_border, .comments-navigator__refresh, section, .section_gray, .section_padding_none, .page-story__cedit , .tags-search__section, .input_section, .section_padding_left, .cedit-editor__form, .yandex-direct.section, .story-editor__blocks, .profile__award, aside>div {
		border: 1px solid #333 !important;
	}

	table#header_t, table, tr, td, table.menu-block, table, .profile__section, .story__footer, .sidebar-block__content_hr, .sidebar-block__header, .comment-reply__controls, tbody, .input_comment-reply, .input__box, .input_editor, .settings-saves__category, .settings-saves__categories, .settings-saves__add-b, .button_green:hover {
		border: none !important;
	}

	.notification, .settings-saves__category, .settings-saves__categories::after, .calendar-head__buttons, .table_standard thead td, .table_standard thead th, .table_standard thead tr:before, .table_standard thead tr:before, .table_standard thead tr:after, .table_standard thead tr:after, aside>div>div {
		border-bottom: solid 1px #333 !important;
	}

	.b-comments, .comment__children, .comments__answers {
		transition: border-left-color 200ms ease-out;
		border-left: solid 1px #333 !important;
	}

	.story-block_type_text b {
	    color: #DDD !important;
	}

	.comment__children_next-floor {
	    border-left: none !important;
	}
	
	.comment__children_next-floor::before {
	    border-top-color: #333 !important;
	    border-left-color: #333 !important;
	}

	.comment__children_next-floor::after {
		border-right-color: #333 !important;
	    border-bottom-color: #333 !important;
	    border-left-color: #333 !important;
	}

	.stories-search-group+.stories-search-group, .comments+.comments, .footer {
		border-top: solid 1px #333 !important;
	}

	.community-info-block__rules 
	{
		border-top: solid 1px #333 !important;
		border-bottom: solid 1px #333 !important;
	}

	::-moz-selection {
	    background-color:#666 !important;
        color: #ccc !important;
	}

	::selection{
	    background-color:#999 !important;
		color: #ccc !important;
	}

	@keyframes animate__story-highlight{
		0%{
	        background-color: #2e2e2e;
	    }
	    to{
	        background-color:#1f1f1f
	    }
	}
}
