css:
@-moz-document regexp("chrome-extension://.*"), regexp("moz-extension://.*") {
#header {
    color: #e1e1e1 !important;
    background: #262626 !important;
    border-style: none !important;
}
input {
    background-color: #161616 !important;
    color: #CCCCCC !important;
    border: 1px solid #000000 !important;
}
select {
    background-color: #161616 !important;
    color: #CCCCCC !important;
    border: 1px solid #000000 !important;
}
#installed::after {
    background-color: #1c1c1c !important;
}
button {
    background: #202020 !important;
    border: 4px solid #202020 !important;
    margin: 2px !important;
    color: #fff !important;
    border-radius: 5px !important;
    box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 0.82) !important;
}
button:hover {
    background: #161616 !important;
    border: 4px solid #161616 !important;
    color: #fff !important;
    border-radius: 5px !important;
}
body {
    color: #e1e1e1 !important;
    background: #1c1c1c !important;
}
a,
a:visited {
    color: #e1e1e1 !important;
}
select {
    width: 100px !important;
}
#save-button:after {
    content: 'd' !important;
}
body.dirty #save-button:after {
    content: '?' !important;
}
#message-box > div,
#message-box-buttons,
#message-box-title {
    background-color: #1d1d1d !important;
}
.newUI .entry:nth-child(2n) {
    background-color: rgb(31, 31, 31) !important;
}
.svg-icon:hover,
.newUI .entry:hover .svg-icon:hover {
    fill: #fff !important;
}
#message-box.danger #message-box-title {
    background-color: firebrick !important;
}
.newUI .no-update:not(.update-problem):not(.update-done) .up-to-date,
.newUI .can-update .update {
    color: black;
}
#help-popup {
    background-color: #1c1c1c !important;
}
.CodeMirror {
    -moz-box-shadow: inset 0 0 20px rgba(0, 0, 0, 0.35) !important;
    -webkit-box-shadow: inset 0 0 20px rgba(0, 0, 0, 0.35) !important;
    box-shadow: inset 0 0 20px rgba(0, 0, 0, 0.35) !important;
    background: #272727 !important;
    color: #8F938F !important;
    line-height: 1.4em !important;
    font-size: 13px !important;
    height: 70%;
    outline: transparent !important;
    border: 1px solid #505050 !important;
}
#confirm > div {
    background-color: #202020;
    border: solid 2px rgba(0, 0, 0, 0.52);
}

/*MOD*/
.search-result {
    border: 1px solid #131313;
    background-color: #1c1c1c;
}
.search-result:hover {
    background-color: #222;
}
.search-result .search-result-meta {
    background-color: hsla(0, 0%, 13.3%, 0.8) !important;
}
.onoffswitch input {
    display: none !important;
}
.onoffswitch span {
    background-color: #333;
}
.onoffswitch span::before {
    background: #111;
}
.onoffswitch input:checked + span,
.onoffswitch input:checked + span::before {
    border-color: #233231 !important;
}
.onoffswitch input:checked + span {
    background-color: #233231 !important;
}
.svg-icon.checked {
    fill: #e1e1e1 !important;
}
input[type="checkbox"]:not(.slider) {
    border: 1px solid hsl(0, 0%, 40%) !important;
}
a:hover .svg-icon {
    fill: #fff;
}
.newUI .can-update:not([data-details$="locally edited"]) .update:after,
.newUI .updater-icons > :not(.check-update):after {
    color: black;
}
}
