css:
@-moz-document url-prefix("http://templeofweirdstuff.space") {

#login {
 background-image: url('https://d2v9y0dukr6mq2.cloudfront.net/video/thumbnail/hMH8tD4/abstract-animation-with-glittering-triangle-pattern-growing-and-disappearing-in-the-dark-seamless-loop_4y9qbp70__F0001.png');   
}


h1 {
  text-indent: -9999px;
  line-height: 0; /* Collapse the original line */
}

h1::after {
  content: "Temple of Weird Stuff";
  text-indent: 0;
  display: block;
  line-height: initial; /* New content takes up original line height */
    color:white;
}


input[type=submit]{
    background-color:#00aeff;
    -webkit-animation-name: example; /* Safari 4.0 - 8.0 */
    -webkit-animation-duration: 8s; /* Safari 4.0 - 8.0 */
    animation-name: example;
    animation-duration: 8s;
    animation-iteration-count: infinite;
}

/* Safari 4.0 - 8.0 */
@-webkit-keyframes example {
     0%   {background-color:#00a2ff;}
    50%  {background-color:#4c5155;}
	100% {background-color:#00a2ff;}
}

/* Standard syntax */
@keyframes example {
    0%   {background-color:#00a2ff;}
    50%  {background-color:#4c5155;}
    100% {background-color:#00a2ff;}
}


input {
 border-radius:5px;   
}

#login input[type=password] {
 color:white;   
}
 #login input[type=text] {
     color:white;
}
}
