css:
@-moz-document url("https://typing-tube.net/"), url-prefix("https://typing-tube.net/dustbox"), url-prefix("https://typing-tube.net/search"), url-prefix("https://typing-tube.net/?loca"), url-prefix("https://typing-tube.net/comments"), url-prefix("https://typing-tube.net/playlist"), url-prefix("https://typing-tube.net/?c=-30&loca"), url-prefix("https://typing-tube.net/requests") {
body {
    background: #212121;
    overflow-x: hidden;
}
#right_ads,
#center_ads,
#bottom_ads,
footer ul,
.createdate img[src*="bn02.gif"],
.contents-top{
    display: none !important;
}
.createdate p{
    color:#d3d3d3;
}
.sideber section {
    margin: 0 0 0 0;
}
.request .searchbox {
    background:#101010;
    border: solid 0.1vw #000000;
        color: #d0d0ca;
}
.sideber .createdate .searchbox {
    padding: 0.5vw;
    font-size: 1.3em;
    font-family: Arial, sans-serif;
    color: #d0d0ca;
    border: solid 0.1vw #000000;
    margin: 0;
    width: 20vw;
    background:#101010;
}
.search .searchbox {
    background:#101010;
    color:#d0d0ca;
    border: solid 0.1vw #000000;
}
li,
h1,
a
{
    color: #E2E2D6;
}
.logo {
    float: left;
    margin: 0.8vw 0 0 0;
    filter: invert(90%);
}
::-webkit-scrollbar-corner{
    background: #414141;
}
::-webkit-scrollbar {
    width: 10px;
    background: #212121;
}
::-webkit-scrollbar-thumb {
    background: #555;
}
}

@-moz-document url-prefix("https://typing-tube.net/movie/show/"), url-prefix("https://typing-tube.net/movie/add_comment") {
.moviepage p,
.wrapper >.report p,
.wrapper >.report br,
.pull-left,
footer ul
{
 display: none !important;
}
.search .searchbox {
    background:#101010;
    color:#d0d0ca;
    border: solid 0.1vw #000000;
}
