    width: 100%;
    position: static;
}
#movie-tags li {
    font-weight: bold;
    color: #FF5917;
}
.movietitle{
    color:#e3e3dd;
}
.logo {
    float: left;
    margin: 0.8vw 0 0 0;
    filter: invert(90%);
}
body>.wrapper>.taglist {
    background: #9E9E9E;
