    background: #212121;
}
.wrapper{
    display: flex;           /* flexコンテナ化 */
   flex-direction: column;
}
.pull-right {
 color:#b3b3b3;
    position:absolute;
}

#controlbox {
    background:#101010;
}
#status {
    background:#101010;
    color:#b9b9b9;
    font-size: 24.9px;
    margin-top: 3px;
}
#bar_input_base,
#bar_base
{
    background: #2d2d2d;
}
#kashi_roma span{
 color:#FFB74D !important;  
