    border-radius: 0.5vw;
}
h1 a {
    color: #71A791;
    font-weight: bold;
}
.wrapper .report li {
    color: #dfdfda;
}
button, input, optgroup, select, textarea {
    margin: 0;
    color: #dfdfda;
    background: #101010;
    border:  groove #444444;
    
}
.ui-widget-content {
    border: 1px solid #dddddd;
    background: #c2c2c2;
    color: #444444;
}
p {
    color:#E2E2D6;
}
.report h1 {

    padding: 0vw;

}
