css:
@-moz-document url("https://www.wuxiaworld.com/"), url-prefix("https://www.wuxiaworld.com/"), regexp("https://www.wuxiaworld.com/") {

/*===================================================================================*/
/* HEADER & NAVBAR MODIFS
/*----------------------------------------------------------*/
body .header{
	height : 85px;
}
body .header .logo{
	padding : 5px;
}
body #navbar a{
  text-shadow : -1px 0 black, 0 1px black, 1px 0 black, 0 -1px black, 0px 0px 8px SkyBlue;
  color : white ! important;
}
body #navbar a:hover{
  text-shadow : -1px 0 black, 0 1px black, 1px 0 black, 0 -1px black, 0px 0px 8px blue;
  color : white ! important;
}
body .main .content{
    background : #e6fff7;
    }
    body.darkmode .main .content{
    background : #1d1c1c;
    }
    .panel.panel-default{
     background : #e6fff7;
    }


/*===================================================================================*/
/* LIGHT	//// 	COLORS & SHADOW MODIFS
/*----------------------------------------------------------*/
body{
    color : MidnightBlue ! important;
}
.section-content p span{
	color : MidnightBlue ! important;
}
strong{
    color : #790000  ! important;
    text-shadow : 0px 0 20px FireBrick;
}
a:link{
	color : #b38600  ! important;
    text-shadow : 0px 0 20px #ffedb3;
    text-decoration : none;
}
a:hover{
	color : #b38600  ! important;
    text-shadow : 0px 0 20px #ffc400;
    text-decoration : underline;
}
/*-----------------------------------------------------------------------*/
/* DARK		//// 	COLORS & SHADOW MODIFS
/*----------------------------------------------------------*/
body.darkmode{
  color : Gainsboro  ! important;
  text-shadow : -1px 0 black, 0 1px black, 1px 0 black, 0 -1px black, 0px 0px 8px blue;
}
body.darkmode strong {
  color : Gainsboro  ! important;
  text-shadow : -1px 0 black, 0 1px black, 1px 0 black, 0 -1px black, 0px 0px 8px red;
}
body.darkmode a:link{
  color : Gainsboro  ! important;
  text-shadow : -1px 0 black, 0 1px black, 1px 0 black, 0 -1px black, 0px 0px 8px GoldenRod;
  text-decoration: none;
}
body.darkmode a:hover{
  text-shadow : -1px 0 black, 0 1px black, 1px 0 black, 0 -1px black, 0px 0px 8px SkyBlue;
  text-decoration: underline;
}
body.darkmode .section-content p span{
	color : Gainsboro ! important;
}

.pp .genres-list .media-heading h4{
	color : #ffc34d;
}
.pp .genres-list .media{
	color : MidnightBlue;
    background-color: #ccffcc;
}
/*===================================================================================*/
/* LIGHT	////	READER CHAPTER NAME MODIFS
/*===================================================================================*/
.caption.clearfix .font-resize{
 	margin-top: 5px;
}
.caption.clearfix img {
  	margin-top:-10px;
}
.caption.clearfix h4{
 	margin-left : 60px;
  	font-size : 1.3em;
}
.caption.clearfix{
 	border-bottom : 1px solid black;
 	min-height : 55px;
    margin-bottom : 60px;
}
/*-----------------------------------------------------------------------*/
/* DARK		//// 	READER CHAPTER NAME MODIFS
/*----------------------------------------------------------*/
body.darkmode .caption.clearfix .font-resize{
  margin-top: 5px;
}
body.darkmode .caption.clearfix img {
  margin-top:-10px;
}
body.darkmode .caption.clearfix h4{
  margin-left : 60px;
  font-size : 1.3em;
  text-shadow : -1px 0 black, 0 1px black, 1px 0 black, 0 -1px black, 0px 0px 8px SkyBlue;
}
body.darkmode .caption.clearfix{
  border-bottom : 1px solid #383838;
  min-height : 55px;
}



/*===================================================================================*/
/* LIGHT	////	MAIN SECTION TITLE MODIFS
/*----------------------------------------------------------*/
.tabbable .nav-tabs li a {
	background-color : #b3ffb3;
}
.tabbable .nav-tabs > li.active > a{
background-color : #ccffff;
}
.owl-theme .owl-controls .owl-page.active span{
background-color : #ffc34d;
}
.owl-theme .owl-controls .owl-page span{
background-color : #ffe6b3;
}
.fp-carousel .item .caption h3{
	color : MidnightBlue;
}
.tabbable .nav.nav-tabs{
	background-color: #ccffcc;
}
#tab-editors-choice, #tab-completed{
	background-color: #ccffff;
}
.pp .panel-footer, .panel-footer{
 	background-color: #ccffcc;
}
.well{
	background-color: #ccffff;
}
.section-heading{
  	background-color: #ccffcc;
 	height : 50px;
 	margin-top : -20px;
 	margin-left : -15px;
	margin-right : -15px;
  	margin-bottom : 15px;
  	text-align: center;
  	padding-top : 5px; 
}
.section-heading h3{
	margin-top : 10px;
	color : #790000  ! important;
    text-shadow : 0px 0 20px FireBrick;
}
.title a:link{
  	color : #206040  ! important;
	text-shadow : 0px 0 20px #339966;
}
.up .table thead > tr > th {
	background-color: #e6fff7;
}
/*-----------------------------------------------------------------------*/
/*	DARK	/////	MAIN SECTION TITLE MODIFS
/*----------------------------------------------------------*/
body.darkmode .tabbable .nav-tabs li a {
	background-color : #2d2d2d;
}
body.darkmode .tabbable .nav-tabs > li.active > a{
background-color : #404040;
}
body.darkmode #tab-editors-choice, body.darkmode #tab-completed{
	background-color: #404040;
}
body.darkmode .section-heading{
  background-color: #2d2d2d;
  height : 50px;
  margin-top : -20px;
  margin-left : -15px;
  margin-right : -15px;
  margin-bottom : 15px;
  text-align: center;
  padding-top : 5px; 
}
body.darkmode .section-heading h3{
	margin-top : 10px;
	color : Gainsboro  ! important;
  	text-shadow : -1px 0 black, 0 1px black, 1px 0 black, 0 -1px black, 0px 0px 8px red;
}
body.darkmode .title a:link{
  color : Gainsboro  ! important;
  text-shadow : -1px 0 black, 0 1px black, 1px 0 black, 0 -1px black, 0px 0px 8px green;
}


/*===================================================================================*/
/* LIGHT	////	READER TOP & BOTTOM BAR MODIFS
/*===================================================================================*/
.pp .top-bar-area{
	background-color: #ccffcc;
}
.pp .top-bar-area h4{
	color : MidnightBlue;
}
.pp .top-bar-area .btn-link{
	background-color: #00a3cc;
}
.nav-bar-area{
  margin-top : -5px;
  margin-bottom : -30px;
  height : 80px;
  background-color: #ccffcc;
}
.pp .nav-bar-area .btn-link{
  margin-left : 10px;
  margin-right : 10px;
  background-color: #00a3cc;
}
/*-----------------------------------------------------------------------*/
/* DARK		////	READER TOP & BOTTOM BAR MODIFS
/*----------------------------------------------------------*/
body.darkmode .top-bar-area{
	background-color: #313131;
}
body.darkmode .top-bar-area h4{
	color : Gainsboro;
}
body.darkmode .top-bar-area .btn-link{
	background-color: #252525;
}
body.darkmode .nav-bar-area {
  border : 1px solid black; 
  margin-top : -5px;
  margin-bottom : -30px;
  height : 80px;
  background-color: #313131;
}
body.darkmode .nav-bar-area .btn-link{
  margin-left : 10px;
  margin-right : 10px;
  background-color: #252525;
}



/*===================================================================================*/
/* LIGHT	////	READER SIDELINKS MODIFS
/*===================================================================================*/
body .panel-body a:link{
    color : #206040  ! important;
	text-shadow : 0px 0 20px #339966;

}
body .panel-body a:hover{
  text-shadow : 0px 0px 8px SkyBlue;
  text-decoration: underline;
}
/*-----------------------------------------------------------------------*/
/* DARK		////	READER SIDELINKS MODIFS
/*----------------------------------------------------------*/
body.darkmode .panel-body a:link{
  color : Gainsboro  ! important;  
  text-shadow : -1px 0 black, 0 1px black, 1px 0 black, 0 -1px black, 0px 0px 8px green;
  text-decoration: none;
}
body.darkmode .panel-body a:hover{
  text-shadow : 0px 0px 8px SkyBlue;
  text-decoration: underline;
}




/*===================================================================================*/
/* BACKGROUND AND READER MODIFS DESKTOP
/*===================================================================================*/
@media screen and (min-width: 1024px){

    body .main{
        background: url(https://image.noelshack.com/fichiers/2018/11/6/1521321362-fondchine.png);
		background-repeat :	no-repeat;
		background-size: 100% 100%;
   		background-attachment: fixed;
    	background-position: center; 
	}
    body.darkmode .main{
    	background: url(https://image.noelshack.com/fichiers/2018/11/6/1521252819-dragon.png);
		background-repeat :	no-repeat;
		background-size: 100% 100%;
   		background-attachment: fixed;
    	background-position: center; 
	}
    
    
    body .fr-view p {
        padding-left : 10%;
        padding-right : 10%;
        margin-bottom: 30px;
    }
    body .fr-view p:hover {
        text-shadow : 0px 0px 8px #339966;
    }
    body.darkmode .fr-view p:hover {
        text-shadow : -1px 0 black, 0 1px black, 1px 0 black, 0 -1px black, 0px 0px 8px SkyBlue;
    }
    
    .form-wrap{
    	padding-left : 2.5%;
        padding-right : 2.5%;
        padding-top : 20px;
    }
    .media-list.comment-list{
    	padding-left : 5%;
        padding-right : 5%;
    }
    .panel.panel-footer{
     	width : 100%;
    	display : flex;
        flex-direction : row;
        justify-content : center;
    }
/*--->	READER	------------------------------------>
	change layout for fullspace reader.
---*/    
    .content.space-0 .row{
    	display : flex;
        flex-direction : column-reverse;
        flex-wrap : wrap;
    } 
    .col-lg-9.col-md-8{
    	width : 100%;
 		margin-left : auto;
    	margin-right : auto;
    }
/*--->	READER FOOTER of NEWS	------------------------------------>
	stop inhertit from .row	
---*/
    .content.space-0 .panel-footer .row{ 
    	display : flex;
    	flex-direction : row;
    	flex-wrap : nowrap;
    }

/*--->	SIDE WIDGET SPACE	------------------------------------>
	Line them, Hide ADDS and useless WIDGET.
    Active on Hover.
    Modify Widgets for correct display.
    
    Widget_0 : Adds;
    Widget_1 : Chapter List;
    Widget_17 : Rebirth of the Thief - Patreon
    Widget_22 : RMJI - Patreon
    Widget_41 : Editor's Choice
    widget_58 : Martial World - Patreon
    50, 36, 38, 39, 40, 52 Art LDK
    35 patreon
    widget_8 etvo tweet
    widget_4 etvo link
    
---*/
    .content.space-0 .row .col-lg-3.col-md-4{
        display : flex;
        flex-direction : row;
        flex-wrap : nowrap;
        justify-content : space-between;
        margin-top : 0px;
        max-width : 100%;
        width : 100%;
        height : 38px;
    	overflow : hidden;
        border-bottom : 1px solid #252525;
    }
    .content.space-0 .row .col-lg-3.col-md-4:hover{
    	min-height : 250px;
    }

    .content.space-0 .row .col-lg-3.col-md-4 .panel.panel-default{
    	width : 25%;
    	min-height : 250px;
    }
    .col-lg-3 #widget_0, #widget_35, #widget_36, #widget_38, #widget_39, #widget_40, #widget_41, #widget_50 {
    	display : none;
    }
    .col-lg-3 #widget_1{
    	width : 100%; 
        height : 100%;
        text-align : center;
    }
    .col-lg-3 #widget_8{
    	min-width : 250px; 
        height : 100%;
        text-align : center;
    }
    .col-lg-3 #widget_52{
    	width : 50%; 
        height : 100%;
        text-align : center;
    }
    .col-lg-3.col-md-4 .panel-body{
    	height : 100%;
    	max-height : 208px;
        overflow : auto;
    }
	.col-lg-3.col-md-4 .panel-heading{
    	background-color : #ccffff;
        color : MidnightBlue;
    }
    body.darkmode .col-lg-3.col-md-4 .panel-heading{
    	color : Gainsboro;
    	background-color : #313131;
    }
    #wuxiaworld_ATF1, #wuxiaworld_BTF5{
    	display : none;
    }
    
}
/*===================================================================================*/
/* READER MODIFS MOBILS
/*===================================================================================*/
@media screen and (max-width: 1024px){
	
    body .fr-view p {
    	padding-left : 10px;
        padding-right : 10px;
        margin-bottom: 30px;
    }
    #wuxiaworld_ATF1, #wuxiaworld_BTF5, #widget_0{
    	display : none;
    }
}

}
