css:
@-moz-document domain("novelplanet.com") {
body {
    font-size: 1.4em;
}
a {
    color: #FFB313;
}
#header {
    background-color: #211907;
    color: #ffffff;
    height: 70px;
}
#header img {
    mix-blend-mode: luminosity;
}
#header > div {
    position: absolute !important;
    line-height: 70px !important;
}
#header nav {
    height: 70px;
    line-height: 70px;
    position: absolute;
    right: 1em;
    vertical-align: middle;
}
.button {
    height: unset !important;
    white-space: normal;
    border-radius: 5px;
}
input[type="submit"],
input[type="reset"],
input[type="button"],
.button {
    background: #FFB313;
}
input[type="submit"]:active,
input[type="reset"]:active,
input[type="button"]:active,
.button:active {
    background: #eb9f00;
}
input[type="submit"]:hover,
input[type="reset"]:hover,
input[type="button"]:hover,
.button:hover {
    background: #d18e00;
}
section#main {
    background: #fbf7e5;
    color: #694A08;
    font-weight: 500 !important;
    padding: 20px;
}
#divReadContent {
    max-width: 1280px;
    margin: 0 auto;
    text-align: justify;
    font-weight: 400;
    font-size: 1.5em !important;
    line-height: 2em !important;
}
#divReadContent p {
    margin: 0 auto;
    text-align: justify;
    font-weight: 400;
}
#navButton .toggle:before {
    background: rgba(144, 144, 144, 0.25);
}
.fillFloatBar {
    background: rgba(144, 144, 144, 0.15);
    color: #fff;
    font-size: 16px;
    height: 2.25em;
    line-height: 2.25em;
    top: 0em;
    width: 100vw;
    overflow: hidden;
    position: absolute;
}
.post-content .post-message,
.post-content .post-message p {
    font-size: 1.1em;
    line-height: 1.1em !important;
}

#footer {
    padding: 10px 30px;
      flex-direction: row;

}
#footer .container .links {
    margin: 0 !important;
    padding: 0 !important;
    border: none;
    display: flex;
}
#footer .container {
    display: flex;
    flex-direction: row;
    width: 100%;
}
#footer .container .links .row {
    margin: auto !important;
}

#footer .container .links .row section {
    width: 100%;
}
#footer .container .links h3 {
    display: none;
}
#footer .links {
    width: 60%;
}
#footer .container > .row {
    width: 40%;
}
#footer .container > .row div {
    margin: auto !important;
}
#footer  .row div {
    width: 50%;
}
#footer ul {
    margin: 0;
}
#footer .links  ul li {
    display: inline;
    margin-right: 20px;
    padding: 0 !important;
}
#footer .copyright {
    margin: 0;
}
#footer a.icon {
    color: #ffffff;
    font-size: 0.5em;
    height: 4em;
    line-height: 4em;
}
}

@-moz-document url-prefix("https://novelplanet.com/ReadingList") {
section#main .container:nth-child(3) {
    width: 100%;
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    flex-flow: wrap;
}
section#main > .container:nth-child(3) > h3, section#main > .container:nth-child(3) > hr, section#main > .container:nth-child(3) > #divEmailNotify, section#main > .container:nth-child(3) > #divListCategories {
    width: 33%;
}
section#main #selectListCategories {
    width: 100%;
}
section#main > .container:nth-child(3) > hr {
    display: none;
}
.aRecordReading {
    min-height: 230px;
    width: 32.8%;
    margin: .25%;
    border-bottom: 1px dashed #ccc;
    border-right: 1px dashed #ccc;
}
.aRecordReading hr {
    display: none;
}
.aRecordReading .post-content a.title {
    font-size: 1em;
}
.aRecordReading .post-content a.button {
    font-size: .6em;
    line-height: 1.5em;
    padding: 5px 10px;
    border-radius: 5px;
}
.aRecordReading .post-preview {
    width: 25%;
}
.aRecordReading .post-preview a {
    max-height: 230px;
    overflow: hidden;
}
.aRecordReading .post-content {
    width: 73%;
}
}

@-moz-document url-prefix("https://novelplanet.com/Novel/") {
section#main .container:nth-child(3) div:nth-child(1) {
    /*width: 100%;*/
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    flex-flow: wrap;
}
section#main .container:nth-child(3) div > .post-previewInDetails {
    width: 15%;
}
section#main .container:nth-child(3) div > .post-contentDetails {
    width: 40%;
    font-size: .8em;
    line-height: 1.1em;
}
section#main .container div > div:nth-child(4) {
    display: inline-block;
    font-size: .8em;
    line-height: 1.5em;
    width: 45%;
}
section#main .container:nth-child(3) div > div:nth-child(5) {
    display: inline-block;
}
section#main .container:nth-child(3) > div:nth-child(7) {
    display: block;
}
section#main .container div h3 {
    width: 100%;
}
.rowChapter {
    width: 33% !important;
    display: inline-block !important;
    border: 1px solid rgba(0,0,0,.1);
    margin: 0 !important;
    padding: 5px 5px 5px 10px;
    font-size: .8em;
    line-height: 1.2em !important;
}
}
