css:
@-moz-document domain("teamwork.com") {
.app-header__top {
    min-height: 0;
}
.app-header__top > div {
    padding-top: 5px;
    padding-bottom: 5px;
    height: auto;
}
.app-header__base {
    height: auto;
    padding-top: 10px;
    font-size: 0.625em;
    padding-bottom: 10px;
}
.mt--medium {
    margin-top: 0 !important;
    margin-bottom: 0;
    min-height: 0;
}
.new__main-header__left img {
    width: 40px !important;
    height: 40px !important;
}
h2.new__main-header__title {
    font-size: 1.625em;
}
.lhs {
    float: none;
}
.lhs div {
    display: inline-block;
}
.people-page-wrapper .main-tabs {
    margin-top: 10px;
}
.taskList--container.board {
    justify-content: space-between;
    overflow: hidden;
    padding: 10px 19px;
}
.boards .column {
    width: calc(25% - 15px);
    padding: 0;
}
.boards .column:first-child {
    width: calc(50% - 15px);
    padding-left: 0;
}
.boards .column:last-child {
    padding-right: 0;
}
.boards-column--empty-state {
    width: calc(100% - 40px) !important;
}
.task-card .task-footer,
.task-card .avatar-list,
.task-card .task-name .tagHolder,
.task-controls,
.sub-detail {
    display: none;
}
.task-card .task-name span.name {
    line-height: normal;
    max-width: 100%;
}
.project-card,
.task-card,
.project-card.has-active-task-options,
.task-card.has-active-task-options {
    padding-bottom: 10px;
}
.project-card .project-name,
.project-card .task-name,
.task-card .project-name,
.task-card .task-name {
    max-width: 100%;
}
}
