css:
@-moz-document domain("myphone.gr") {
    body{
        background-color: #222 !important;
        background-image: url(data:image/png;base64,) !important;
        background-clip: border-box !important;
        background-origin: padding-box !important;
        background-attachment: scroll !important;
        background-repeat: repeat !important;
        background-size: auto !important;
        background-position: left top !important;
    }

    .page,
    #hdlogo,
    .topCyanFade,
    .topic_controls,
    .showthread,
    .mainpage,
    .content,
    #footer,
    #subfooter,
    #right_column,
    .splitter,
    .splitter strong, .splitter b{
        background: none !important;
        background-color: transparent !important; 
        background-image: none !important;
    }

    .topDarkerFade, 
    .forumdisplay,
    .forumdisplay{
        background: transparent;
        padding-top: 10px;
    }

    .content {
        border-top: 0px solid #000;
        border-left: 0px solid #14306f;
        border-right: 0px solid #14306f;
        border-bottom: 0px solid #14306f;
        overflow: hidden;
    }

    .dark, .pnxtcat {
        border: 0px solid #14306f;
        box-shadow:0px 0px 20px 3px black;
        background: #ffffff00;
    }

    .topic_buttons h1 {
        font-size: 1.8em;
        text-shadow: 0px 0px #fff;
    }

    table.thread_table th {
        background: #33333333;
        border-top: 0px solid #152851;
        border-bottom: 0px solid #152851;
        font-size: 11px;
        text-align: left;
        color: #fff;
        white-space: nowrap;
        font-family: 'Lucida Grande', Tahoma, sans-serif;
        line-height: 2;
        margin: 0px;
        padding: 5px 5px 5px 10px;
        font-weight: normal;
    }

    table.thread_table,
    .row2, table.thread_table tr.row2 td,
    .row2 .altrow, table.thread_table tr.row2 td.altrow,
    table.thread_table tr.row2 td.threadforum{
        background-color: #171717; 
    }

    .row1, table.thread_table tr.row1 td,
    .row1 .altrow, table.thread_table tr.row1 td.altrow,
    table.thread_table tr.row1 td.threadforum{
        background-color: #171717; 
    }

    .content,
    .message,
    .article_text{
        color: #cdcdcd;
        line-height: 1.8;
    }

    .topictitle a, .topictitle_new a {
        color: #bbb;
    }

    .thread_replies a,
    .thread_replies,
    .thread_views{
        color: #789;
    }

    #hdlogo .logo, #hdlogo .logobeta {
        filter: invert(100%) hue-rotate(180deg) saturate(100%) brightness(1) !important;
    }

    .barmenu_parent {
        border-top: 0px solid #26416a;
        border-bottom: 0px solid #26416a;
        background: #3334;
        height: 33px;
        overflow: hidden;
    }

    #breadcrumb {
        filter: invert(100%) hue-rotate(180deg) saturate(50%) brightness(0.6) !important;
    }

    .topic_buttons,
    .topic_options,
    .blue,
    .post_footer > .right,
    #collapseobj_quickreply fieldset,
    .threadbox > h3 > a > img,
    .forumjump{
        filter: invert(100%) hue-rotate(180deg) saturate(100%) brightness(1) !important;
    }
    

    #breadcrumb a.current {
        color: #8D9Db7;
    }

    .post{
        box-shadow:0px 0px 20px 3px black;
        background: #ffffff00;   
    }

    .post_member h3 a {
        color: white;
        text-shadow: none;
        border: none;
        background-color: #333;
    }

    .status {
        background: url('https://i.imgur.com/amxn5oO.png') no-repeat;
    }

    .alt1,
    .alt2,
    .post_header,
    .post_footer,
    .extra_box .box_trending a,
    .alt0{
        background-color: #171717; 
        color: #ccc; 
    }

    .bbquote blockquote,
    .post_avatar,
    .post_member_details,
    .small_controls a,
    #right_column .box_item.top5 a.top5link,
    #right_column .box_item.top5 .top5title{
        color: #bbb;
        background: #272727; 
    }
    
    #main_column  {
        color: #bbb;
        background: #171717; 
    }

    .bbblock .attribution {
        color: #322311;
        background: #aaaa76;
        border-bottom: 1px solid #CFA876;
    }

    .bbquote, .bbmedia {
        border: 2px solid #aa9351;
    }

    .post_member_tag {
        background: #272727;
        border: 1px solid #acc2e3;
    }

    .lastone,i_thread_unread{
        display: none;   
    }
    
    .bluecontent,
    .light{
		background: #272727;
		border: none;
    }
    
    textarea:disabled {
        background: #272727;
        color: #aabbd0;
        text-align: center;
        padding: 10px;
    }
    
    textarea,
    textarea:focus,
    tr > .alt1,
    #tagcloud a{
     	background: #393939 !important;
        color: #ddd;
        text-shadow: none;
    }
    
    .lastcontrol,
    #footer,
    #subfooter,
    .showthreadarticle {
        border: none;
        box-shadow: none;
    }
    
    .bar a,
    .bar > h1,
    .bar > .right{
        color: #ddd;
    }
    
    #featured {
        background: none;
        border: none;
	}
}
