/* Main Table - Month View - Replace Background*/
.Bd2qQe{
	background: url(http://www.desertjewelmusic.com/wp-content/uploads/2018/03/225-butterfly-around-the-ball-in-hand-3d-wallpaperbutterfly-wallpaper.jpg) !important;
	 background-size: 100% !important;
	background-repeat: repeat !important;
  	background-attachment: fixed !important;
					background-position: center !important;
}
.EfQccc, .g3dbUc.jKgTF {
	color: #FFFFFF;
					}
.NkChXe>content {
	background-color: midnightblue;
}
.yzYBvd {
	color: antiquewhite;
}
.ef2wWc {
	background-color: midnightblue;
}

/*Main Table - Replace Background*/
  .G5v83e, .jRJqje.Z8eykb.sMVRZe  {
	background: url(http://www.desertjewelmusic.com/wp-content/uploads/2018/03/225-butterfly-around-the-ball-in-hand-3d-wallpaperbutterfly-wallpaper.jpg) !important;
	 background-size: 100% !important;
	background-repeat: repeat !important;
  	background-attachment: fixed !important;
					background-position: 50% -100% !important;

	/*vertical lines on main calendar*/
}
.YvjgZe {
	border-color: rgba(255,255,255,0.25) !important;
	
}
.L1Ysrb {
            border-color: black !important;
        	color: black !important;    
		}
/* Time Column */
		.Gk2izd, .GENA3c {
            background: var(--GR-BLUE3) !important;
            border-color: white !important;
		color: white !important;
		}
.pCoqfc, .MANBAf {
		color: white !important;
		}

/* Hide GMT Marker*/

.Gk2izd {
	display: none !important;
}

/*Ribbon Groove*/
.eADW5d {
	border-bottom-style: groove
}
.ifwtOb {	
opacity: .8 !important;
	
	    -webkit-box-shadow: 1px 1px 10px 0px rgba(51, 51, 51, 0.5);
    -moz-box-shadow: 1px 1px 10px 0px rgba(51, 51, 51, 0.5);
    box-shadow: 1px 1px 10px 0px rgba(51, 51, 51, 0.5);
	border: none !important;

}
.ifwtOb:hover {
	opacity: 1 !important;
    -webkit-box-shadow: 1px 1px 10px 0px rgba(51, 51, 51, 0.7);
    -moz-box-shadow: 1px 1px 10px 0px rgba(51, 51, 51, 0.7);
    box-shadow: 1px 1px 10px 0px rgba(51, 51, 51, 0.7);
}
	/*Event Cells*/

.EfQccc, .g3dbUc.jKgTF {
	opacity: .7;
	border-radius: 10px !important;
	    -webkit-box-shadow: 1px 1px 10px 0px rgba(51, 51, 51, 0.5);
    -moz-box-shadow: 1px 1px 10px 0px rgba(51, 51, 51, 0.5);
    box-shadow: 1px 1px 10px 0px rgba(51, 51, 51, 0.5);
	border: none !important;

}
.EfQccc:hover {
	opacity: .9 !important;
    -webkit-box-shadow: 1px 1px 10px 0px rgba(51, 51, 51, 0.7);
    -moz-box-shadow: 1px 1px 10px 0px rgba(51, 51, 51, 0.7);
    box-shadow: 1px 1px 10px 0px rgba(51, 51, 51, 0.7);

}
/*event cells hide white border*/
.EfQccc.Hrn1mc.EiZ8Dd::after {
	border: transparent !important;
}


/* Add Button*/
.uXJUmd {
	display: none !important;
}
.XiVK6d
{
	background-color: #7d2922 !important;
	opacity: .7 !important;
}
.XHsn7e
{
	border-radius: 25% !important;
}

/* Day Numerals */

.KSxb4d {
font-size: 30px !important;
}
.SU7tYb
{
border-top-right-radius: 20px !important;
}
/* Grid Borders */
		.H7IzGb,
		.SU7tYb,
		.yEkOpe,
		.G5v83e,
		.YvjgZe,
		.mmsF1c::after {
            border-color:  rgba(255,255,255,0.25) !important;
		}
  /*Current Time line*/
  .WtFGnf-uDEFge{
    border-top: #6495ED solid 2px !important;
					}
}

@-moz-document url-prefix("http://www.google.com/calendar/"), url-prefix("https://www.google.com/calendar/"), url-prefix("http://calendar.google.com/"), url-prefix("https://calendar.google.com/") {
::-webkit-scrollbar{width:9px!important;}
body::-webkit-scrollbar{width:12.5px!important;}

::-webkit-scrollbar-track{background:rgba(255,255,255,0.22)!important;border-radius:8px!important;-webkit-box-shadow:inset 0 0 4px rgba(0,0,0,0.3)!important;}
body::-webkit-scrollbar-track{background:#fff url(https://pbs.twimg.com/media/CKecQtCWoAA8keI.png)!important;}

::-webkit-scrollbar-thumb{background-color:#999!important;background-image:-webkit-linear-gradient(294deg,rgba(255,255,255,.2) 25%,transparent 25%,transparent 50%,rgba(255,255,255,.2) 50%,rgba(255, 255, 255,.2) 75%,transparent 75%,transparent);min-height:50px;border-radius:5px!important;-webkit-box-shadow:inset 0 0 4px rgba(0,0,0,0.5)!important;}
}
