css:
@-moz-document url(chrome://browser/content/browser.xhtml) {
  :root {
    --tab-min-height: 28px !important;
    --tabs-border-color: transparent !important;
    --toolbar-bgcolor: white !important;
    --toolbarbutton-border-radius: 0 !important;
    --toolbarbutton-outer-padding: 0 !important;
    --toolbox-border-bottom-color: transparent !important;
  }

  .tab-line {height: 0 !important;}
  .titlebar-spacer[type="pre-tabs"] {border-inline-end: 0 !important;}
  .titlebar-placeholder[type="pre-tabs"] {opacity: 0 !important;}
  full-screen-warning-container, .tabbrowser-tab::before, .tabbrowser-tab::after {display: none !important;}
  #navigator-toolbox>toolbar {background: var(--toolbar-bgcolor) !important;}
  #nav-bar, #TabsToolbar {box-shadow: none !important;}
  #PanelUI-button {border-image-slice: 0 !important;margin-inline-start: 0 !important;}
  #PanelUI-menu-button {padding: 0 !important;}
  #urlbar, .searchbar-textbox {
    margin-top: 0 !important;
    margin-bottom: 0 !important;
    background: transparent !important;
    border: 0 !important;
    box-shadow: none !important;
  }
}
