css:
@-moz-document url-prefix("https://dev.twitch.tv/") {
:root {
	--text: #eeeeee;
	--background: #1D1B24;
	--alt-background: #201c2b;
	--border-color: #302A42;
}
}

@-moz-document url-prefix("https://dev.twitch.tv/docs") {
/* Sidebar Scrollbar only on Hover */
header {
	overflow-y: hidden;
}

header:hover {
	overflow-y: auto;
}

/* Sidebar */
header form,
header form #search-input,
.search,
body {
	background: var(--border-color);
	color: var(--text) !important;
}

header .sidebar dl,
.auth-drop-down {
	border-color: var(--border-color);
	background: var(--background);
}

header .sidebar dl dt a:hover {
	background: var(--border-color);
}

header .sidebar dl dt a,
header .sidebar dl dd a {
	color: var(--text);
}

.cta {
	background: var(--border-color);
	color: var(--text);
}

.social {
	background: var(--border-color);
	opacity: 0.97;
}

/* Content */
html {
	color: var(--text);
	background: var(--background);
}

.main,
.doc-content,
table:not(.highlight) tr:nth-child(odd) {
	background: var(--background);
	border-color: var(--border-color);
}

.doc-content {
	border-image: none;
	-webkit-border-image: none;
}

table tr th, table:not(.highlight) tbody tr:nth-child(even) {
	background: var(--alt-background);
}

table th, table td {
	border-color: var(--border-color);
}

.left-docs pre,
.right-code pre,
.text-content pre,
table thead + tbody tr:nth-child(even) {
	background: var(--alt-background);
	border: 1px solid var(--border-color);
	overflow: auto;
}

code {
	background: var(--border-color);
}

blockquote {
	background: var(--border-color);
	color: var(--text);
}

.text-content {
	width: auto;
}


/* Gist Embed */
.gist-data,
.gist-file {
	border-color: var(--border-color) !important;
	background: var(--border-color) !important;
}

.gist-meta {
	background-color: var(--border-color) !important;
	color: #afb9af !important;
}
.gist-meta a {
	color: var(--text) !important;
}

.highlight {
	background-color: var(--border-color) !important;
}

table.highlight,
table.highlight tr:nth-child(odd), 
table.highlight tr:nth-child(even) {
	background: var(--background);
}
table.highlight tr:hover {
	background: #363144;
}

table.highlight tr * {
	border-color: transparent;
}

.blob-num {
	background: var(--border-color) !important;
}
.blob-num::before {
	color: #afb9af !important;
}
/* Code */
.blob-code { color: #bbbbbb !important }
.pl-k { color: #c678dd !important; }
.pl-c1 { color: #e06c75 !important; }
.pl-s, .pl-pds { color: #98c379 !important; }
.pl-en { color: #61afef !important; }
.pl-smi { color: #e06c75 !important; }
.pl-pse { color: #AC6DC7 !important; }
.pl-s1 { color: #e06c75 !important; }


/* Suggested change by twitch.tv/alca */
header .sidebar dl dd a.active-highlight {
	color: hsl(259, 40%, 50%);
}

a {
    color: hsl(261, 35%, 58%);
}
}

@-moz-document url-prefix("https://dev.twitch.tv/console") {
.twilight-dev-site-root {
	background: var(--background);
	color: var(--text);
}

.tw-c-background-base,
.tw-c-background-alt {
	background: var(--alt-background) !important;
	color: var(--text);
}

.tw-tab:not(.tw-tab--disabled) .tw-tab__link.tw-tab__link--active,
.tw-c-text-alt-2,
.tw-c-text-alt,
.tw-form-label,
.tw-radio__label,
.tw-c-text-base,
.tw-checkbox .tw-checkbox__label {
	color: var(--text) !important;
}

.tw-tab-wrapper {
	box-shadow: inset 0 -1px 0 var(--border-color);
}

.tw-interactable--base,
.tw-table-heading {
	background: var(--border-color);
}
.tw-interactable--base:hover {
	background: var(--alt-background) !important;
}

.tw-border-b,
.tw-border-r,
.tw-border-t,
.tw-table-heading,
.tw-table-cell,
.tw-typeset hr,
input,
textarea,
select  {
	border-color: var(--border-color) !important;
}

.tw-core-button--secondary,
.tw-c-background-alt-2,
input,
textarea,
select {
	background-color: var(--border-color) !important;
	color: var(--text) !important;
}
.tw-core-button--secondary:hover {
	background-color: var(--alt-background) !important;
	color: var(--text) !important;
}

.tw-core-button--primary.tw-core-button--disabled,
.tw-core-button--primary.tw-core-button--disabled:active,
.tw-core-button--primary.tw-core-button--disabled:focus {
	background-color: var(--alt-background) !important;
	color: var(--text) !important;
	border-color: var(--border-color);
}
}
