css:
@-moz-document url-prefix("https://gamedevalliance.fr") {
html body {
    background: #14191f;
    color: #ddd;
}
html hr {
    border-top: 2px solid #1b2028;
}
html legend {
    color: #ddd;
}
html .darkenBackground {
    background: rgba(0, 0, 0, .5);
}
html #flarum-loading {
    color: #5c6570;
}
@media (min-width: 768px) {
    html .sideNav .Dropdown--select .Dropdown-menu>li>a {
        color: #5d7d7a;
    }
}
html .sideNav--horizontal:after {
    border-bottom: 1px solid #1b2028;
}
@media (min-width: 768px) and (max-width: 991px) {
    html .sideNav:after {
        border-bottom: 1px solid #1b2028;
    }
}
html .App:before {
    background: rgba(20, 25, 31, .98);
}
html .scrolled.App:before {
    -webkit-box-shadow: 0 2px 6px rgba(0, 0, 0, .5);
    box-shadow: 0 2px 6px rgba(0, 0, 0, .5);
}
@media (max-width: 767px) {
    html .App-primaryControl>.Button, html .App-backControl>.Button {
        color: #6c7d93 !important;
    }
}
@media (max-width: 767px) {
    html .App-drawer {
        background: #14191f;
        -webkit-box-shadow: 0 2px 6px rgba(0, 0, 0, .5);
        box-shadow: 0 2px 6px rgba(0, 0, 0, .5);
    }
    html .drawer-backdrop {
        background-color: rgba(10, 12, 15, .9);
    }
}
html .App-content {
    border-top: 1px solid #1b2028;
}
@media (max-width: 767px) {
    html .App-content {
        background: #14191f;
    }
}
html .AlertManager .Alert {
    -webkit-box-shadow: 0 2px 6px rgba(0, 0, 0, .5);
    box-shadow: 0 2px 6px rgba(0, 0, 0, .5);
}
html .Avatar {
    background-color: #1b2028;
}
html .Button:active,
html .Button.active,
html .open > .Dropdown-toggle.Button {
    background-color: #060709;
}
html .Button.disabled,
html .Button[disabled],
html fieldset[disabled] .Button {
    background: #1b2028;
}
html .Button--primary {
    color: #1f1914;
}
html .Button--danger {
    color: #a88;
    background: #411;
}
html .Button--danger:hover,
html .Button--danger:focus,
html .Button--danger.focus {
    background-color: #300c0c;
}
html .Button--danger:active,
html .Button--danger.active,
html .open > .Dropdown-toggle.Button--danger {
    background-color: #1b0707;
}
html .Button--danger.disabled,
html .Button--danger[disabled],
html fieldset[disabled] .Button--danger {
    background: #411;
}
html .Checkbox--switch .Checkbox-display {
    background: #1b2028;
}
html .Checkbox--switch .Checkbox-display:before {
    background: #14191f;
    box-shadow: 0 2px 4px rgba(0, 0, 0, .5);
}
html .disabled .Checkbox-display {
    color: #5c6570 !important;
}
html .Dropdown-menu {
    background: #14191f;
    -webkit-box-shadow: 0 2px 6px rgba(0, 0, 0, .5);
    box-shadow: 0 2px 6px rgba(0, 0, 0, .5);
    color: #ddd;
}
html .Dropdown-menu > li > a,
html .Dropdown-menu > li > button,
html .Dropdown-menu > li > span {
    color: #ddd;
}
html .Dropdown-menu > li > a:hover,
html .Dropdown-menu > li > button:hover {
    background: #1b2028;
}
html .Dropdown-menu > li.active > a,
html .Dropdown-menu > li.active > button {
    background: #1b2028;
}
html .Dropdown-header {
    color: #ddd;
    border-top: 1px solid #1b2028;
}
html .Dropdown-separator {
    background-color: #1b2028;
}
@media (max-width: 767px) {
    html .Dropdown .Dropdown-menu {
        -webkit-box-shadow: 0 2px 6px rgba(0, 0, 0, .5);
        box-shadow: 0 2px 6px rgba(0, 0, 0, .5);
    }
    html .Dropdown .Dropdown-menu > li > a,
    html .Dropdown .Dropdown-menu > li > button {
        background: #14191f;
    }
    html .Dropdown .Dropdown-menu > li > a:hover,
    html .Dropdown .Dropdown-menu > li > button:hover {
        background: #1b2028;
    }
}
html .Form-group > label {
    color: #ddd;
}
html .FormControl {
    color: #669993;
    background-color: rgb(27, 32, 40);
}
html .FormControl:focus,
html .FormControl.focus {
    background-color: #14191f;
    color: #ddd;
}
html .FormControl::-moz-placeholder {
    color: #5d7d7a;
}
html .FormControl:-ms-input-placeholder {
    color: #5d7d7a;
}
html .FormControl::-webkit-input-placeholder {
    color: #5d7d7a;
}
html .helpText {
    color: #6c7d93;
}
html .LoadingIndicator {
    color: #6c7d93;
}
html .modal-backdrop {
    background-color: rgba(10, 12, 15, .9);
}
html .Modal-content {
    background-color: #14191f;
}
html .Modal-body {
    background-color: #1b2028;
    color: #6c7d93;
}
html .Modal-body .FormControl {
    background-color: #14191f;
    color: #ddd;
}
html .Modal-footer {
    color: #6c7d93;
}
html .Modal-loading {
    background: rgba(20, 25, 31, .9);
}
@media (max-width: 767px) {
    html .ModalManager:before {
        background: rgba(20, 25, 31, .98);
        border-bottom: 1px solid #1b2028;
    }
}
@media (min-width: 768px) {
    html .Modal-content {
        -webkit-box-shadow: 0 7px 15px rgba(0, 0, 0, .5);
        box-shadow: 0 7px 15px rgba(0, 0, 0, .5);
    }
}
html .Navigation-drawer.new:after {
    border: 2px solid #14191f;
}
html .Placeholder p {
    color: #5c6570;
}
html .Search-input {
    color: #669993;
}
html .DiscussionSearchResult-excerpt {
    color: #6c7d93;
}
html .Select-caret {
    color: #6c7d93;
}
html .PostsUserPage-list > li {
    border-bottom: 1px solid #1b2028;
}
html .PostsUserPage-discussion,
html .PostsUserPage-discussion a {
    color: #5d7d7a;
}
html .Composer {
    -webkit-box-shadow: 0 2px 6px rgba(0, 0, 0, .5);
    box-shadow: 0 2px 6px rgba(0, 0, 0, .5);
}
html .Composer.minimized {
    background: #1b2028;
}
html .ComposerBody-loading {
    background: rgba(20, 25, 31, .9);
}
@media (max-width: 767px) {
    html .Composer {
        background: #14191f;
    }
    html .Composer:not(.minimized):before {
        background: rgba(20, 25, 31, .98);
        border-bottom: 1px solid #1b2028;
    }
    html .composer-backdrop {
        background-color: rgba(10, 12, 15, .9);
    }
    html .ComposerBody-header > li {
        border-bottom: 1px solid #1b2028;
    }
    html .normal .ComposerBody-header > li:first-child h3 {
        color: #6c7d93;
    }
}
@media (min-width: 768px) {
    html .Composer {
        background: rgba(20, 25, 31, .95);
    }
    html .Composer.active,
    html .Composer.fullScreen {
        background: #14191f;
    }
    html .Composer.active:not(.fullScreen) {
        -webkit-box-shadow: 0 0 0 2px #f38e50, 0 2px 6px rgba(0, 0, 0, .5);
        box-shadow: 0 0 0 2px #f38e50, 0 2px 6px rgba(0, 0, 0, .5);
    }
}
html .TextEditor textarea {
    color: #ddd;
}
@media (min-width: 768px) {
    html .TextEditor-controls {
        border-top: 1px solid #1b2028;
    }
}
html .DiscussionListItem-content {
    color: #5d7d7a;
}
html .DiscussionListItem-title {
    color: #ddd;
}
html .read .DiscussionListItem-title {
    color: #838786;
}
html .DiscussionListItem-relevantPosts .PostPreview {
    background: #1b2028;
    border-bottom: 2px dotted #14191f;
    color: #6c7d93;
}
html .DiscussionListItem:hover .DiscussionListItem-relevantPosts .PostPreview {
    border-color: #212831;
}
html .DiscussionListItem-relevantPosts .PostPreview:hover {
    background: #14191f;
}
@media (max-width: 767px) {
    html .DiscussionListItem-content:active {
        background: #1b2028;
    }
    html .DiscussionListItem-count {
        background: #1b2028;
        color: #6c7d93;
    }
}
@media (min-width: 768px) {
    html .DiscussionListItem:hover {
        background: #171d23;
    }
    html .DiscussionListItem-count {
        color: #6c7d93;
    }
    html .unread .DiscussionListItem-count {
        color: #ddd;
    }
}
@media (max-width: 767px) {
    html .DiscussionPage-nav {
        border-bottom: 1px solid #1b2028;
    }
}
@media (min-width: 768px) {
    html .DiscussionPage-list {
        background: #14191f;
        border-top: 1px solid #1b2028;
        -webkit-box-shadow: 0 6px 6px rgba(0, 0, 0, .5);
        box-shadow: 0 6px 6px rgba(0, 0, 0, .5);
    }
    html .DiscussionPage-list .DiscussionListItem.active {
        background: #1b2028;
    }
}
html .Hero {
    background: #1b2028;
    color: #fff;
}
html .NotificationGrid {
    background: #1b2028;
}
html .NotificationGrid td,
html .NotificationGrid th {
    border-bottom: 1px solid #14191f;
    color: #6c7d93;
}
html .NotificationGrid-checkbox.highlighted .Checkbox:not(.disabled),
html .NotificationGrid-checkbox .Checkbox:hover:not(.disabled) {
    background: #12161c;
}
@media (min-width: 768px) {
    html .NotificationList-header {
        border-bottom: 1px solid #1b2028;
    }
    html .NotificationList-header h4 {
        color: #6c7d93;
    }
}
html .NotificationList-empty {
    color: #6c7d93;
}
html .NotificationGroup {
    border-top: 1px solid #1b2028;
}
html .NotificationGroup-header {
    color: #ddd !important;
}
html .Notification {
    color: #6c7d93 !important;
}
html .unread.Notification {
    background: #1b2028;
}
html .Notification:hover {
    background: #1b2028;
}
html .Notification-excerpt {
    color: #5c6570;
}
html .NotificationsDropdown-unread {
    background: #6c7d93;
    color: #14191f;
    border: 1px solid #14191f;
}
html .Post-header,
html .Post-header a {
    color: #5d7d7a;
}
html .PostUser h3,
html .PostUser h3 a {
    color: #ddd;
}
html .Post-body a {
    border-bottom: 1px solid #1b2028;
}
html .Post-body blockquote {
    background: #1b2028;
    color: #669993;
    border-top: 2px dotted #14191f;
    border-bottom: 2px dotted #14191f;
}
html .Post-body code {
    background: #0e1115;
}
html .Post-body pre {
    background: #0e1115;
}
html .Post--hidden .Post-header,
html .Post--hidden .Post-header a,
html .Post--hidden .PostUser h3,
html .Post--hidden .PostUser h3 a {
    color: #5c6570;
}
html .Post--hidden .Post-header .Button--more {
    color: #5c6570;
    background: rgba(92, 101, 112, .3);
}
html .Post--hidden .Post-header .Button--more:hover,
html .Post--hidden .Post-header .Button--more:focus,
html .Post--hidden .Post-header .Button--more.focus {
    background-color: rgba(80, 88, 98, .35);
}
html .Post--hidden .Post-header .Button--more:active,
html .Post--hidden .Post-header .Button--more.active,
html .open > .Dropdown-toggle.Post--hidden .Post-header .Button--more {
    background-color: rgba(69, 75, 84, .4);
}
html .Post--hidden .Post-header .Button--more.disabled,
html .Post--hidden .Post-header .Button--more[disabled],
html fieldset[disabled] .Post--hidden .Post-header .Button--more {
    background: rgba(92, 101, 112, .3);
}
html .PostMeta .Dropdown-menu {
    color: #6c7d93;
}
html .PostMeta-number {
    color: #ddd;
}
html .EventPost,
html .EventPost a {
    color: #6c7d93;
}
html .Post-footer,
html .Post-actions,
html .Post-footer a,
html .Post-actions a {
    color: #5d7d7a;
}
html .PostPreview {
    color: #6c7d93;
}
html .PostPreview .username {
    color: #ddd;
}
html .ReplyPlaceholder {
    border: 2px dashed #1b2028;
    color: #6c7d93;
}
@media (min-width: 768px) {
    html .ReplyPlaceholder:hover {
        border-color: #1b2028;
    }
}
html .PostStream-item:not(:last-child) {
    border-bottom: 1px solid #1b2028;
}
html .fakeText {
    background: #1b2028;
}
html .PostStream-timeGap {
    color: #5d7d7a;
}
html .Scrubber a {
    color: #5d7d7a;
}
html .Scrubber-before,
html .Scrubber-after {
    border-left: 1px solid #1b2028;
}
html .Scrubber-unread {
    border-left: 1px solid #8a97a8;
    background-image: linear-gradient(to right, #1b2028, rgba(27, 32, 40, 0) 10px, rgba(27, 32, 40, 0));
    color: #6c7d93;
}
html .Scrubber-handle {
    background: #14191f;
}
html .disabled .Scrubber-bar {
    background: #1b2028;
}
html .Scrubber-description {
    color: #5d7d7a;
}
html .SignUpModal-welcome .Avatar {
    border: 4px solid #14191f;
    -webkit-box-shadow: 0 2px 6px rgba(0, 0, 0, .5);
    box-shadow: 0 2px 6px rgba(0, 0, 0, .5);
}
@media (max-width: 767px) {
    html .sliding.Slidable .Slidable-content {
        -webkit-box-shadow: 0 2px 6px rgba(0, 0, 0, .5);
        box-shadow: 0 2px 6px rgba(0, 0, 0, .5);
    }
}
html .UserCard {
    background: #1b2028;
}
html .UserCard--popover {
    -webkit-box-shadow: 0 2px 6px rgba(0, 0, 0, .5);
    box-shadow: 0 2px 6px rgba(0, 0, 0, .5);
}
html .UserCard-avatar .Avatar {
    -webkit-box-shadow: 0 2px 6px rgba(0, 0, 0, .5);
    box-shadow: 0 2px 6px rgba(0, 0, 0, .5);
}
html .EmojiDropdown > li > button {
    color: #ddd;
}
html .Post-flagged,
html .Post-flagged a {
    color: #14191f;
}
html .Post-flagged .Button--link,
html .Post-flagged .Search-input {
    color: #14191f;
}
html .Post-flagged .FormControl {
    color: #14191f;
}
html .Post-flagged .FormControl::-moz-placeholder {
    color: #14191f;
}
html .Post-flagged .FormControl:-ms-input-placeholder {
    color: #14191f;
}
html .Post-flagged .FormControl::-webkit-input-placeholder {
    color: #14191f;
}
html .Post-flagged .FormControl:focus {
    color: #14191f;
}
html .Post-flagged .Button,
html .Post-flagged .Button:hover {
    color: #14191f;
}
html .Post-flagged .Button:active,
html .Post-flagged .Button.active,
html .Post-flagged .Button:focus,
html .Post-flagged .Button.focus,
html .Post-flagged .open > .Dropdown-toggle.Button {
    color: #14191f;
}
html .Post-flagged,
html .Post-flagged a {
    color: #14191f !important;
}
html .FlagPostModal .checkbox strong {
    color: #ddd;
}
html .PostLikesModal-list a {
    color: #ddd;
}
html .PostMention,
html .UserMention {
    background: #1b2028;
    color: #669993;
}
html blockquote .PostMention,
html blockquote .UserMention {
    background: #14191f;
}
html .MentionsDropdown .PostPreview,
html .PostMention-preview .PostPreview,
html .Post-mentionedBy-preview .PostPreview {
    color: #6c7d93;
}
html .MentionsDropdown .PostPreview .username,
html .PostMention-preview .PostPreview .username,
html .Post-mentionedBy-preview .PostPreview .username {
    color: #ddd;
}
html .DiscussionListItem-info .item-excerpt {
    color: #5c6570;
}
html .SubscriptionMenuItem-description {
    color: #6c7d93;
}
html .TagLabel {
    background: #1b2028;
    color: #6c7d93;
}
html .TagLabel.untagged {
    border: 1px dotted #6c7d93;
    color: #6c7d93;
}
html .TagLabel.colored .TagLabel-text {
    color: #14191f !important;
}
html .DiscussionHero .TagsLabel .TagLabel.colored {
    background: #fff !important;
    color: #6c7d93;
}
html .DiscussionHero--colored,
html .DiscussionHero--colored a {
    color: #fff;
}
html .TagIcon {
    background: #1b2028;
}
html .TagIcon.untagged {
    border: 1px dotted #6c7d93;
}
@media (min-width: 768px) {
    html .TagDiscussionModal .Modal-header {
        background: #1b2028;
    }
    html .TagDiscussionModal .Modal-header h3 {
        color: #6c7d93;
    }
}
html .SelectTagList > li.pinned + li:not(.pinned) {
    border-top: 2px solid #1b2028;
}
html .SelectTagList > li.active {
    background: #1b2028;
}
html .SelectTagList > li.selected .TagIcon:before {
    color: #6c7d93;
}
html .SelectTagListItem-description {
    color: #6c7d93;
}
html .TagTile {
    background: #1b2028;
}
html .TagTile,
html .TagTile a {
    color: #6c7d93;
}
html .TagTile.colored,
html .TagTile.colored a {
    color: #14191f;
}
html .TagTile.colored .TagTile-description {
    color: rgba(20, 25, 31, .7);
}
html .TagTile.colored .TagTile-lastDiscussion {
    color: rgba(20, 25, 31, .7);
}
@media (min-width: 992px) {
    html .TagsPage .sideNav:after {
        border-bottom: 1px solid #1b2028;
    }
}




button.Button.Button--icon.hasIcon, div.ButtonGroup.Dropdown.dropdown.itemCount4 button.Dropdown-toggle.Button {
    background: #1b2028;
}

.Post-body tr { /* texte des tableaux */
    color: #f5f5f5;
}
.drawer-backdrop {
    background-color: rgba(14, 14, 14, 0.8);
}
.PostStream-item:not(:last-child) {
    border-bottom-color: rgba(228, 246, 244, 0.10);
}
details>div {
    border-left: 1px solid #1b2028;
    padding-left: 20px;
}
/* code et syntax highlight*/
.Post-body pre, .Post-body code {
    color: #d0d0d0;
}
.hljs-type, .hljs-string, .hljs-number, .hljs-selector-id, .hljs-selector-class, .hljs-quote, .hljs-template-tag, .hljs-deletion {
    color: #ea6e6e;
}
.hljs-title, .hljs-section {
    color: #ea6e6e;
    font-weight: bold;
}
.hljs, .hljs-subst {
    color: #a0a0a0;
}
/* scrollbar */
::-webkit-scrollbar-track {
    background: #1b2028;
}
}
