css:
@-moz-document url-prefix("https://letterboxd.com"), url-prefix("https://letterboxd.uservoice.com/") {
/*avatars*/

.avatar.-a110,
.avatar.-a110 a,
.avatar.-a110 img,
.avatar.-a110::after,
.avatar.-a24,
.avatar.-a24 a,
.avatar.-a24 img,
.avatar.-a24::after,
.avatar.-a16,
.avatar.-a16 img,
.avatar.-a16::after,
.avatar.-a150,
.avatar.-a150 a,
.avatar.-a150 img,
.avatar.-a150::after,
/*posters*/

.poster .poster-container,
.poster .image,
.poster,
.poster-container,
.list-frame,
.poster .frame,
.poster .image,
/*poster hover*/

.film-list-wrapper .overlay,
.poster .overlay,
/*list teasers hover green border fix*/

.list-link:after,
/*profile page avatar*/

.avatar.-a40,
.avatar.-a40 a,
.avatar.-a40 img,
.avatar.-a40::after,
/*buttons*/

.button,
.button.-compact,
.uvStyle-button,
/*select menus*/

.select,
/*profile navbar*/

#profile-nav nav,
/*activity setting bar*/

.activity-settings,
/*page navigation miscellanea*/

.load-more-button,
.notes-button,
.pagination .paginate-ajax a,
.pagination .paginate-ajax span,
.pagination .paginate-nextprev a,
.pagination .paginate-nextprev span,
/*call to action boxes*/

.text-slug,
/*teasers ("recent showdowns")*/

.teaser-media > .image,
/*badges*/

.badge,
.badge.-small,
.uvStyle-status,
/*stats year selector*/

.person-yir-promo .yir-selector,
/*checkboxes*/

.option-label.-checkmark > .checkbox + .substitute,
.option-label.-checkbox.-default > .checkbox + .substitute,
/*add menu*/

#add-menu,
/*input elements*/

.field,
.textarea,
.tt-hint,
.uvField,
/*profile menu subnav*/

#main-nav li .subnav,
/*feedback idea hover*/

.browse ol a,
/*poster placeholder*/

.film-prevnext.-inactive,
/*lightbox*/

#cboxContent,
/*share review link box*/

.actions-panel #share-on input.field,
/*amazon search box*/

.watch-panel .link,
/*film info panel*/

.watch-panel,
/*search text input*/

.search-form .field,
/*letterboxd crew avatars*/

body.editorial .page-bg .editorial-person figure,
/*pro page*/

.acc-type .acc-col,
.acc-type h2 {
    border-radius: 1px;
}
/*irregular borders*/

.actions-panel > ul > li:first-child {
    border-top-left-radius: 1px;
    border-top-right-radius: 1px;
}
.actions-panel > ul > li:last-child {
    border-bottom-left-radius: 1px;
    border-bottom-right-radius: 1px;
}
.actions-panel > ul > .action-large.-watchlist {
    border-top-right-radius: 1px;
}
/*friend add button fix*/

.featured-person .follow-button-wrapper .button.-compact {
    top: 3px;
}
.profile-summary > .stats li a strong {
    font-size: 2.5rem;
    font-weight: 200;
}
.profile-summary > .stats li {
    margin-right: 10px;
}
#profile-nav nav ul li a {
    padding: 10px 14px 11px;
}
}
