css:
@-moz-document domain("closed.pizza") {
#global-menu li.selected a {
    color: #FFFF00 !important;
}
#global-menu li.selected a {
    color: #FFFF00;
}
#global-menu li.selected a::before {
    color: #FFFF00;
}
#global-menu li a:hover, #global-menu li button:hover {
    box-shadow: inset 0 -4px 0 -1px #FFFF00;
}
#global-menu #global-my-menu .symbol::before {
    font-size: 18px;
    line-height: 18px;
    width: 22px;
    text-align: center;
    color: #FFFF00;
    position: absolute;
    top: 50%;
    margin-top: -9px;
}
h2.label {
    border-bottom: 3px solid #FFFF00;
    color: #FFFF00;
    font-weight: normal;
    font-size: 16px;
    padding: 17px 15px 10px;
    line-height: 1;
    margin: 0;
}
.user-data h4 span {
    background-color: #FFFF00;
    padding: 2px 12px 1px;
    display: block;
    white-space: nowrap;
    margin-bottom: -1px;
    font-size: 12px;
    -webkit-border-radius: 20px;
    -moz-border-radius: 20px;
    -ms-border-radius: 20px;
    -o-border-radius: 20px;
    border-radius: 20px;
    color: #202020
}
.sidebar-setting .sidebar-menu-post::before {
    content: "p";
    color: #FFFF00;
}
.sidebar-setting .sidebar-menu-replies::before {
    content: "r";
    color: #FFFF00;
}
.sidebar-setting .sidebar-menu-empathies::before {
    content: "e";
    color: #FFFF00;
}
.post-meta .empathy-added + .empathy::before, .reply-meta .empathy-added + .empathy::before {
    color: #FFFF00;
}
.post-meta .empathy-added + .empathy, .reply-meta .empathy-added + .empathy {
    color: #FFFF00;
}
h2.reply-label {
    clear: both;
    background: #FFFF00;
    color: #202020;
    border-top: 1px solid #FFFF00;
    padding: 3px 10px 1px;
}
.feeling-selector .feeling-button.checked {
    color: #FFFF00;
}
.textarea-with-menu.active-text .textarea-menu-text, .textarea-with-menu.active-memo .textarea-menu-memo, .textarea-with-menu.active-poll .textarea-menu-poll {
    background: -webkit-gradient(linear,left top,left bottom,from(#FFFF00),to(#FFFF00)) 0 0;
        background-color: rgba(0, 0, 0, 0);
}
.dialog .window-title {
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
    word-wrap: normal;
    height: 34px;
    padding: 0 10px;
    border-top: 1px solid #81e52e;
    border-bottom: 1px solid #FFFF00;
    background: #FFFF00;
    color: #202020;
    font-size: 16px;
    font-weight: bold;
    line-height: 34px;
    text-align: center;
    text-shadow: 0 -1px 0 rgba(0,0,0,0.3);
    -webkit-border-radius: 5px 5px 0 0;
    -moz-border-radius: 5px 5px 0 0;
    -ms-border-radius: 5px 5px 0 0;
    -o-border-radius: 5px 5px 0 0;
    border-radius: 5px 5px 0 0;
}
.user-organization {
    display: block;
    font-size: 12px;
    color: #FFFF00;
}
.user-sidebar .follow-button::before {
    color: #FFFF00;
    font-size: 13px;
    content: "Q";
}
.user-sidebar .friend-button::before {
    color: #FFFF00;
    font-size: 15px;
    content: "F";
}
.index-memo h2:not(.label), #help .help-content h2 {
    background: #FFFF00;
}
.tab2 a.selected, .tab3 a.selected {
    color: #202020;
    background-color: #81e52e;
    background: -webkit-gradient(linear,left top,left bottom,from(#FFFF00),to(#FFFF00));
    background: -webkit-linear-gradient(top,#FFFF00 #FFFF00);
    background: -moz-linear-gradient(top,#FFFF00 #FFFF00);
    background: -ms-linear-gradient(top,#FFFF00 #FFFF00);
    background: -o-linear-gradient(top,#FFFF00 #FFFF00);
    background: linear-gradient(top,#FFFF00 #FFFF00);
}
.post-meta .link-confirm::before {
    content: "o";
    font-size: 18px;
    color: #FFFF00;
}
a {
    color: #FFFF00;
    text-decoration: none;
}
.news-list a.link {
    color: #FFFF00;
}
.community-switcher .community-switcher-tab.selected {
    background: -webkit-gradient(linear,left top,left bottom,from(#FFFF00),to(#FFFF00));
}
#help .help-content h3 {
    clear: both;
    margin-top: 1.5em;
    border-bottom: 2px solid #FFFF00;
    font-size: 14px;
    font-weight: bold;
}
.list .toggle-button .follow-button:before {
    color: #FFFF00;
    content: "Q";
}
.open-spin {
      width: 22px;
  height: 22px;
	content: url(https://i.imgur.com/qPFBZf0.gif);
    animation: spin 0.0s;
}
#splatoon {
 display: none;   
}
#nprogress{pointer-events:none;}
#nprogress .bar{background:linear-gradient(45deg, #FFFF00, #FFFF00);position:fixed;z-index:1031;top:0;left:0;width:100%;height:2px;}
#nprogress .peg{display:block;position:absolute;right:0px;width:100px;height:100%;box-shadow:0 0 10px #FFFF00,0 0 4px #FFFF00;opacity:1.0;-webkit-transform:rotate(3deg) translate(0px,-4px);-ms-transform:rotate(3deg) translate(0px,-4px);transform:rotate(3deg) translate(0px,-4px);}
.nprogress-custom-parent{overflow:hidden;position:relative;}.nprogress-custom-parent #nprogress .spinner,.nprogress-custom-parent #nprogress .bar{position:absolute;}
.spoiler-button.checked:before {
    color: #FFFF00;
}
.spoiler-button.checked {
    color: #FFFF00;
}
#memo-drawboard-page button.selected {
    background: #FFFF00;
}
}
