css:
@-moz-document domain(myanonamouse.net) {
 
  :root {
    --main-bg-color: #7a9da5;
    --secondary-bg-color: #5EB452;
    --hover-bg-color: #38727F;
    --main-fg-color: #fff;
    --secondary-fg-color: #fff;
  }
 
  html {
    padding-bottom: 0 !important;
  }
 
  body {
    padding: 60px 0 !important;
  }
 
  div.footRow {
    margin-top: 20px !important;
    font-size: 11px !important;
    padding: 12px 0 6px !important;
    height: initial !important;
    max-height: initial !important;
    font-weight: bold !important;
    background: var(--main-bg-color) !important;
    color: var(--main-fg-color) !important;
  }
 
  /*front page*/
 
  .mainPageNewsHead {
    margin: 0 auto !important;
    text-align: left !important;
    width: 98% !important;
  }
 
  .mainPageNews,
  .mainPageNewsSu {
    font-size: 12px !important;
    text-align: left !important;
    width: 98% !important;
    margin-left: auto !important;
    margin-right: auto !important;
  }
 
  .mainPageNews {
    margin-top: 4px !important;
  }
 
  .fpShout {
    margin-top: 10px !important;
  }
 
  /*menu*/
  #mainmenu {
    position: fixed !important;
    top: 0 !important;
    left: 0 !important;
    right: 0 !important;
    background-color: #f1f1f1 !important;
    box-shadow: 0 50px 100px rgba(50,50,93,.1), 0 15px 35px rgba(50,50,93,.15), 0 5px 15px rgba(0,0,0,.1) !important;
  }
 
  #mainmenu > ul {
    display: flex !important;
  }
 
  #mainmenu li {
    flex: 1 !important;
    margin: 0 !important;
  }
 
  #mainmenu > ul > li > a {
    padding: 4px 10px !important;
  }
 
  #mainmenu li ul {
    left: 0 !important;
    right: 0 !important;
    box-shadow: 0 50px 100px rgba(50,50,93,.1), 0 15px 35px rgba(50,50,93,.15), 0 5px 15px rgba(0,0,0,.1) !important;
  }
 
  #mainmenu li:not(.mmDonBox) li:hover > a,
  #mainmenu > ul > li:not(.mmDonBox):hover > a {
    background-color: var(--secondary-bg-color) !important;
    color: var(--secondary-fg-color) !important;
  }
 
  .mmDonBox a {
    background-color: var(--main-bg-color) !important;
    color: var(--main-fg-color) !important;
  }
 
  /*blocks*/
  div.blockHead div.bHi {
    background-image: initial !important;
  }
 
  div.blockHead {
    height: initial !important;
    min-height: initial !important;
    background-image: initial !important;
    background-color: initial !important;
    border: initial !important;
  }
 
  div.blockHead div.blockHeadCon {
    position: initial !important;
    margin-left: 10px !important;
    letter-spacing: 1px !important;
  }
 
  div.blockBody {
    border: none !important;
  }
 
  div.blockBody div.blockBody {
    border: 1px solid #a7a7a7 !important;
  }
 
  div.blockBody div.blockBodyCon {
    overflow: initial !important;
  }
 
  div.blockFoot {
    display: none !important;
  }
 
  /*torrent single*/
  #mainTable {
    margin-top: 10px !important;
  }
 
  div.torDetRow {
    display: flex !important;
  }
 
  div.torDetLeft {
    font-size: 20px !important;
    font-weight: 400 !important;
    width: 160px !important;
    position: relative !important;
    padding: 20px 24px !important;
    text-align: right !important;
  }
 
  div.torDetRight {
    align-self: center !important;
    padding-left: 20px !important;
  }
 
  div.torDetInnerCon {
    border-radius: initial !important;
    padding-top: 30px !important;
  }
 
 
  div.torDetInnerTop {
    padding: 10px !important;
    border-radius: 0px !important;
  }
 
  div.torDetRight div.torDetInnerTop {
    padding: 6px !important;
  }
 
  div.torDetRight div.torDetInnerCon {
    border-radius: initial !important;
    padding-top: 20px !important;
  }
 
  img.torDetPoster {
    width: 100px !important;
  }
 
  #posterHolder {
    display: none !important;
  }
 
  /*search page*/
 
  form#mainSearch {
    padding: 0 7px 0 0 !important;
  }
 
  #tmBP {
    transform: translateX(2px) !important;
  }
 
  #mainSearch input#ts {
    padding: 6px 12px !important;
    text-align: center !important;
    border-color: #828282 !important;
    background-color: transparent !important;
  }
 
  #mainSearch input#ts:focus {
    border-color: #353535 !important;
  }
 
  select {
    padding: 5px !important;
    border-color: #828282 !important;
  }
     
  #mainSearch select#tss {
    text-align: center !important;
  }
 
  #mainSearch input[type='submit'] {
    padding: 6px 20px !important;
    border-color: #828282 !important;
    background: var(--main-bg-color) !important;
    color: var(--main-fg-color) !important;
  }
 
  #mainSearch input[type='submit']:hover {
    background: var(--hover-bg-color) !important;
  }
 
  button, input[type="button"], input[type="submit"], input[type="reset"], a.buttonLink, .torSearchNav, .torFormButton {
    border-radius: 0 !important;
    border-width: 1px !important;
    padding: 6px 20px !important;
    border-color: #828282 !important;
    background: var(--main-bg-color) !important;
    color: var(--main-fg-color) !important;
  }
 
  button:hover, input[type="button"]:hover, input[type="submit"]:hover, input[type="reset"]:hover, a.buttonLink:hover, .torSearchNav:hover, .torFormButton:hover {
    background: var(--hover-bg-color) !important;
  }
 
  .prevPage,
  .nextPage {
    text-indent: 0 !important;
    margin: 4px 2px !important;
    text-transform: uppercase !important;
    width: initial !important;
  }
 
  #searchResults h1 {
    text-align: center !important;
    font-size: 34px !important;
    text-transform: uppercase !important;
    letter-spacing: 10px !important;
    color: #292929 !important;
    font-weight: 200 !important;
  }
 
  #torSearch > label {
    padding-left: 7px !important;
    display: flex !important;
    /*display: inline-flex !important;*/
    /*flex-wrap: wrap !important;*/
    /*max-width: 560px !important;*/
    margin: 0 auto !important;
    text-align: left !important;
    align-items: center !important;
  }
 
  #torSearch > label > select {
    /*width: calc(100% - 100px) !important;*/
    /*float: right !important;*/
    margin-left: 10px !important;
    margin-right: 10px !important;
  }
 
  #torSearch > label > h1 {
    flex: 1;
  }
 
  div#torSearchControl {
    margin: 0 !important;
  }
 
  div.blockBody div#torSearchControl div.blockHead {
    display: none !important;
  }
 
  div.blockBody div#torSearchControl div.blockBody {
    border: 0 !important;
  }
 
  #userStat {
    -webkit-transform: translateY(6px) !important;
        -ms-transform: translateY(6px) !important;
            transform: translateY(6px) !important;
  }
 
  #userStat > span, #userStat > a {
    font-size: 13px !important;
  }
 
  #userStat img {
    max-height: 12px !important;
  }
 
  #msb {
    margin-bottom: 10px !important;
  }
 
  div.tP,
  a#millionInfo {
    padding: 12px 0 0 !important;
    transform: translateY(-13px) !important;
    opacity: 0 !important;
    max-height: 1px !important;
  }
 
  div.tP:hover,
  a#millionInfo:hover,
  #msb:hover + #preNav div.tP,
  #msb:hover + #preNav a#millionInfo {
    -webkit-transition: all 0.3s ease;
    -o-transition: all 0.3s ease;
    transition: all 0.3s ease;
    transform: translateY(-10px) !important;
    opacity: 1 !important;
    max-height: 30px !important;
  }
 
  #mainBody > h3,
  #mainBody > br,
  #mainBody > a {
    display: none !important;
  }
 
  /*browse page*/
  #torTitle {
    padding: 6px !important;
    width: 100% !important;
    box-sizing: border-box !important;
  }
 
  #torSearch > div.fbStart {
    margin: 8px 0 6px !important;
  }
 
  #torSearch > div.fbStart .searchIn {
    flex: 1 !important;
    padding-left: 4px !important;
    padding-top: 4px !important;
    background-size: auto 100% !important;
  }
 
  #torSearch > div.fbStart .searchIn:not(:first-of-type) {
    margin-left: 4px !important;
  }
 
  #torSearch h1 {
    align-self: stretch !important;
    display: flex !important;
    justify-content: center !important;
    align-items: center !important;
  }
 
  .torSearchNavBox {
    border-radius: 2px !important;
    margin: 3px 0 3px 3px !important;
    border-color: #9a9a94 !important;
    border-width: 2px !important;
  }
 
  #LimitingAdv {
    font-size: 15px !important;
    padding: 6px 0 !important;
  }
 
  h1#advNav {
    margin: 3px 0 3px 3px !important;
  }
 
  .searchIn, .torCatSel, .torMainCatSel {
    border-width: 1px !important;
    border-radius: 1px !important;
  }
 
  #dataSubset {
    width: 50% !important;
    margin-right: 8px !important;
    margin-top: 3px !important;
    margin-bottom: 5px !important;
  }
 
  #dataSource {
    float: right !important;
    width: 47% !important;
    margin-top: 3px !important;
  }
 
  .newTorTable th {
    vertical-align: middle !important;
  }
 
  a.sortAscA,
  a.sortAsc {
    transform: translateY(3px) !important;
    margin: 0 7px 0 3px !important;
  }
 
  a.sortDescA,
  a.sortDesc {
    margin: 0 3px 0 7px !important;
    transform: translateY(2px) !important;
  }
 
  a.sortAscA {
    background: transparent url('https://cdn.myanonamouse.net/images/ascNAs.png') no-repeat center top !important;
  }
 
  a.sortDescA {
    background: transparent url('https://cdn.myanonamouse.net/images/descNAs.png') no-repeat center bottom !important;
  }
 
  a.directDownload:not(:hover),
  a[title='bookmark']:not(:hover),
  a.sortAsc:not(:hover),
  a.sortDesc:not(:hover) {
    filter: grayscale(1) !important;
  }
 
  a.directDownload,
  a[title='bookmark'] {
    background-position: 50% !important;
    padding: 0 4px !important;
  }
 
  .newTorTable tr td:nth-of-type(2) {
    display: -webkit-flex !important;
    display: -moz-flex !important;
    display: -ms-flex !important;
    display: -o-flex !important;
    display: flex !important;
    -webkit-flex-direction: column !important;
    -moz-flex-direction: column !important;
    -ms-flex-direction: column !important;
    -o-flex-direction: column !important;
    flex-direction: column !important;
    -ms-align-items: center !important;
    align-items: center !important;
  }
 
  .newTorTable tr td:nth-of-type(2) img {
    padding: initial !important;
    margin-top: 7px !important;
    box-sizing: border-box !important;
    max-width: 21px !important;
  }
 
  .newTorTable tr td:nth-of-type(2) .spacer {
    display: none !important;
  }
 
  .newTorTable td:nth-child(3) > a:first-of-type {
    font-size: 22px !important;
    font-weight: 400 !important;
  }
 
  .newTorTable a.author {
    display: inline-block !important;
    margin-top: 7px !important;
    margin-right: 2px !important;
    background-color: #343434 !important;
    color: #fff !important;
    padding: 0 10px !important;
  }
 
  .newCatLink div:hover::after {
    max-width: 100px !important;
    padding: 10px !important;
    top: 0 !important;
    left: 0 !important;
    background-color: var(--main-bg-color) !important;
    color: var(--main-fg-color) !important;
    box-shadow: 2px 2px 6px rgba(23,23,23,0.3) !important;
  }
 
  /*optional grayscale icons, but actually its nice to have these coloured*/
  /*.newCatLink div::before {
    filter: grayscale(1);
  }
 
  tr:hover > td > a.newCatLink div::before {
    filter: none;
  }*/
}
