css:
@-moz-document regexp("(https:\/\/|http:\/\/|)(www\.|)music\.amazon\.de.*") {
/*Original by Robert Plant -edited*/
* {color: #FFFFFF !important;}
input,.wrapper,.playerIconSearch {background-color: #4D4D4D !important;}
.scrubberBackground {background-color: #999999 !important;}
.overlayDimmer {background-color: inherit !important;}
   
/*Craig Mason additions -edited*/
.gridItem{border: 1px solid #666666 !important;}
#left-content.dragonfly {border-right: 1px solid #666666 !important;}
#left-content.dragonfly .libraryPanel {border-top: 1px solid #666666 !important;}

/*No Right AD Pane -edited*/
#right-content-panel{display: none !important;}
#main-content-wrapper{width: auto !important;right: 0px !important;}
.dragSongArea{width: auto; }

/* */
th {background-color: #333333; color: #c0c0c0;}
body ul  {background-color: #333333 !important;color: #c0c0c0;} 
body div {background-color: #333333; color: #c0c0c0;}
section.superSelectBar {background-color: #333333 !important;}
#contextMenuContainer {background: transparent;}
#dragonflyLeftNav .mainLinks .mainLinksTitle {color: #7e828a !important;}
#left-content.dragonfly.normalColorScheme {background: transparent;}
#optionPanel .disabledOptionPanelLink {background-color: #333333 !important;color: #9f9797 !important;}
.listViewDurationContextButton .listViewContextButton {background-color: #333333;}
.listViewTrackNumber .playButton, .listViewTrackNumber .playingButton, .listViewTrackNumber .pausedButton, .rowItemIndex .playButton, .rowItemIndex .playingButton, .rowItemIndex .pausedButton {background-color: #333333;}
.playlistDetailsList .selectedItem td {background-color: #1d1d1d;}
.playlistDetailsListItem:not(.emptyRow) td {border-bottom: 1px solid #f8f9f9 !important;}
.playlistDetailsListItem:hover:not(.emptyRow):not(.header) td {background-color: #1d1d1d;}
.songList .selectable.selected td, .albumList .selectable.selected td {background-color: #272727;}
.songTracks tr:hover, .albumBody tr:hover {background-color: #1d1d1d;}
.dialogContainer .dropdown {background-color: #333333; color: #c0c0c0;}
.buttons.secondary.disabled .left, .buttons.secondary.disabled .right, .buttons.secondary.disabled .buttonCenter {background-color: #4d4d4d !important;color: #959595 !important;}
.buttons.secondary .left, .buttons.secondary .right, .buttons.secondary .buttonCenter {background-color: #2d2d2d !important;color: #9f9797 !important;}
section > div:first-child > textarea {color: black !important;}
}
