css:
@-moz-document domain(samehadaku.tv) {
	body {
	background: #121212;
    color: #dfdfdf;
}

#tie-wrapper {
    background: #1C1C1C;
    color: #dfdfdf;
}

.container-wrapper {
    background: #232323;
    color: #dfdfdf;
}

a {
    color: #0176C9;
}

#tie-block_2598 a:hover, #tie-block_2598 .mag-box-title h3 a:hover, #tie-block_2598 a.block-more-button:hover {
    color: #dfdfdf;
}

.mag-box .posts-items .post-excerpt {
    color: #dfdfdf;
}

.post-meta, .post-meta a:not(:hover) {
    color: #dfdfdf;
}

.pages-nav .next-prev a:not(:hover), .pages-nav .pages-numbers a:not(:hover) {
    color: #dfdfdf;
}

header> .container {
    background: #1C1C1C;
}

#theme-header {
    background: #1C1C1C;
}

.dark-skin .mag-box.mag-box .container-wrapper, .box-dark-skin.mag-box .container-wrapper {
    background: #1C1C1C;
}

body.boxed-layout.wrapper-has-shadow #tie-wrapper {
    box-shadow: none;
}

#text-html-widget-25 a {
    color: #0176c9 !important;
}

#breadcrumb, #breadcrumb a:not(:hover) {
    color: #dfdfdf;
}

.entry-header .post-meta, .entry-header .post-meta a:not(:hover) {
    color: #dfdfdf;
}

.download-eps ul li {
    list-style: none;
}

.download-eps {
    background: #121212;
}

.download-eps ul li {
    background: #232323;
}

.download-eps ul li strong {
    background: none repeat scroll 0% 0% #121212;
    border-right: 2px solid #121212;
    text-align: center;
}

.entry .post-bottom-meta a[href] {
    color: #dfdfdf;
}

.post-footer {
    background: #232323;
}

.entry span {
    color: #dfdfdf !important;
    background-color: #232323 !important;
}
}
