css:
@-moz-document domain(weibo.com) {
  .WB_global_nav,
  .WB_main_l,
  .WB_main_r,
  #plc_bot,
  .title_area,
  div[i-am-music-player],
  .WB_tab_a .tab_box .fr_box,
  .WB_tab_a .tab .b,
  .WB_frame_b,
  .WB_webim,
  .B_page .WB_footer {
      display: none!important;
  }

  .WB_frame #plc_main,
  .WB_main_c,
  .WB_frame_c,
  .B_page .WB_frame {
      width: 100%;
  }

  .S_page .WB_miniblog,
  .WB_miniblog {
      padding: 0;
  }

  body,
  .WB_frame,
  .S_page {
      background: url(https://ww1.sinaimg.cn/large/6836364ely1fmgroojanwj21hc0u0dy6.jpg) 50% no-repeat fixed;
      background-size: cover;
  }

  .WB_frame {
      height: 100vh;
  }

  .WB_miniblog {
      background: none!important;
  }

  .WB_tab_a .tab_box,
  .WB_tab_a .tab li {
      height: 32px!important;
  }

  /**/
  .B_page .WB_frame #plc_main,
  .WB_frame_c {
      display: block;
      margin: 0 auto;
      width: 850px;
  }
}
