css:
@-moz-document url("http://rinkworks.com/adventure/smash.cgi") {
body {
	background-color: #333;
	font-family: Arial, sans-serif;
	color: #ccc;
	font-size: 40px !important
}
body a {
	font-size: 40px !important
}
body > center > img {
	border: 1px solid #AAA;
	margin: 0 10px 15px;
	border-radius: 5px;
	padding: 5px;
	box-shadow: inset 0 0 7px #000;
	background: white;
	border-right: none;
	border-bottom: none;
	width: 80%
}
table[cellpadding='1'] {
	margin: 10px auto;
}
td[bgcolor='#ccddff'] {
	background-color: #222 !important;
	padding: 10px !important;
}
td[bgcolor='#ccddff'] font[color='#3333aa'] {
	color: #aaa !important;
}
td[bgcolor='#ccddff'] font[color='#3333aa']:hover {
	color: #fff !important;
}
table[cellspacing='3'] {
	padding: 10px 0 0;
}
table [bgcolor='#ffe4dd'] {
	background-color: #222 !important;
	color: #aaa !important;
	padding: 20px !important;
	line-height: 1.4em;
	width: 100%;
	display: block;
	font-size: 2rem
}
table [bgcolor='#ccccff'] {
	background-color: #223 !important;
	color: #aaa !important;
	padding: 20px !important;
	line-height: 1.4em;
	width: 100%;
	display: block;
	font-size: 2rem
}
td[bgcolor='#e8ffe8'] {
	background-color: #111 !important;
	color: #AAA !important;
	padding: 15px !important;
	border: 2px solid #444;
	border-radius: 5px;
	box-shadow: inset 0 0 8px #000;
	border-right: none;
	border-bottom: none;
}
td[bgcolor='#e8ffe8'] td,
td[bgcolor='#e8ffe8'] font {
	font-size: 2.5rem !important
}
table[cellpadding='0'] tr {
	display: block
}
table[cellpadding='0'] td[valign='top'] {
	display: block;
	width: 100%;
	padding: 1rem 1rem 2rem;
	border-bottom: 1px solid #666
}
table[cellpadding='0'] td[valign='top'] table {
	margin-right: 0;
}
table[cellpadding='0'] td[valign='top'] table td {
	font-size: 2.5rem;
	padding: .5rem 0;
}
td[bgcolor='#ffffdd'] {}
input[type='image'] {
	width: 0;
	height: 0;
	box-shadow: 1px 1px 3px #111;
	border: 45px solid #37C;
}
input[type='image']:hover {
	border-color: #7af;
}
table[width='100%'] {
	padding: 5px 15px;
	margin: 0 auto !important;
}
input[type='submit'] {
	border-radius: 3px;
	background-color: #888;
	border: 1px solid #888;
	box-shadow: 2px 2px 5px #000;
	color: #000;
	font-size: 2rem;
	margin-right: 1em;
	padding: 1rem
}
}
