@-moz-document domain("tagesanzeiger.ch"), domain("derbund.ch"), domain("bernerzeitung.ch"), domain("24heures.ch"), domain("tdg.ch"), domain("lematin.ch"), domain("bazonline.ch") {
/* ABO+ Overlay & noscroll */
#overlay_wrap,
#paywall_container,
#tamovl-page-wrap {
    display: none;
}

html body.modal-open {
    overflow: auto !important;
    position: unset !important;
    -webkit-user-select: auto;
    -moz-user-select: auto;
    -ms-user-select: auto;
    user-select: auto;
}

html body.h-disable-scroll {
    overflow: auto !important;
    position: static !important;
    user-select: auto;
}

/* No-wants */
#topNav,
.subscribe,
.o-overlay,
.a-backdrop
{
    display: none !important;
}


/* Ads */
#wideBoardContainer,
.ad-inside-full-top,
.ad-outside-right,
#wideBoard,
#sidebarSky,
#colRightAd,
.teads-inread,
#mainColRightPublireportage,
.publireportageFront,
#footerAdSpecial,
.sidelineAdsBoxFront,
.mainColBottomMorePublireportage,
.publireportage,
.ad-inside-half,
#adspot_sitebar,
div[id^='gboutstream'],
.relatedThema,
.sf-widget
{
	display: none;
}


/* Social */
.NnLikeButton,
.socialMedia,
.socialLinks,
.twitterRessortBox
{
	display: none;
}
}
