css:
@-moz-document url-prefix("https://harrypotter.wikia.com/d") {
.discussion-content-wrapper {
    box-shadow: 11em 11em 11em 181em #000000;
    margin-top: 0.5em;
}

.discussion-dropdown,
.discussion-dropdown .wds-dropdown__content,
.discussion-fieldset legend,
.discussion .empty-forum-message,
.discussion-left-rail__header,
.discussion .post-title .post-details-link,
.discussion .post-detail.is-deleted .discussion-content,
.discussion .discussion-content .post-details-link,
.discussion .discussion-content,
.discussions .og-title,
.discussion .post-title,
.discussion-community-unit .discussions-links,
.discussion-user-activity-glossary h5,
.guidelines-page .guidelines-text
.rail .discussion-contributors-title,
.rail,
.user-activity-options a,
.user-activity-options a:active,
.user-activity-options a:visited,
.discussion-fieldset li,
.modal-dialog-wrapper,
.discussion .post-reply.is-parent-deleted .discussion-content,
.discussion .post-detail.is-deleted .discussion-content a,
.discussion .post-reply.is-deleted .discussion-content a,
.discussion-inline-editor .discussion-inline-editor-textarea,
.discussion-inline-editor .discussion-textarea-with-counter,
.discussion-standalone-editor .discussion-standalone-editor-textarea,
.discussion-standalone-editor .discussion-textarea-with-counter,
.discussion-standalone-editor .discussion-standalone-editor-label,
.discussion-fieldset li,
.poll-title__text,
.poll-footer__voted.wds-button,
.poll-footer__voted:hover.wds-button,
.discussion-standalone-editor .discussion-standalone-editor-title .discussion-textarea-label,
.discussion-standalone-editor .discussion-standalone-editor-title .discussion-textarea-counter,
.user-activity__wikiname,
.no-followed-posts h6,
.discussion .discussion-content,
.editor-textarea,
.poll-footer__voted,
.discussion-dropdown .wds-dropdown__content,
.discussion-dialog .modal-dialog,
.discussion-wrapper .wds-button.wds-is-text,
.discussion-wrapper .wds-button.wds-is-text:hover:not(:disabled),
.voter-list-modal .modal-dialog {
    color: #3a3a3a;
}

.discussion-forum-tabs li.not-active {
    border-bottom-color: #3a3a3a;
    color: #3a3a3a;
}

.discussion .discussion-editor-label {
    color: #777777;
}

.discussion-categories-list-content label:hover,
.discussion-hide-deleted-container label:hover {
    background-color: #f2f2f2;
}

.discussion .post-actions>li:not(:last-child) {
    border-right: 1px solid #bbbbbb;
}

.discussion.forum .post-detail {
    border: 1px solid #cccccc;
}

.discussion.post .post-detail {
    border-right: 1px solid #bbbbbb;
    border-left: 1px solid #bbbbbb;
    border-bottom: 1px solid #bbbbbb;
}

.discussion .top-note {
    border-bottom: 1px solid #bbbbbb;
}

.discussion.forum .post-detail,
.discussion.forum .post-reply {
    border: 1px solid #bbbbbb;
}

.pop-over-container {
    border: 2px solid #cccccc;
}

.discussion .more-options li:not(:last-child),
.discussion-user-activity-list .row .user .username,
.discussion-user-activity-list .row .details {
    border-bottom: 1px solid #cccccc;
}

.icon,
.discussion .post-detail.is-deleted .icon:not(.delete):not(.undelete),
.discussion .post-reply.is-deleted .icon:not(.delete):not(.undelete),
.discussion .post-detail.is-locked .replies-area .replies {
    fill: #999999;
}

.discussion .post-bottom-row,
.discussion .timestamp {
    color: grey;
}

.discussion-error .error-content {
    background-color: #f2f2f2;
    border-radius: 8px;
    border: 1px solid #cccccc;
    background-image: -webkit-gradient(linear,0% 0%,0% 100%,color-stop(50%,#ffffff),color-stop(100%,#f2f2f2));
    box-shadow: 0 1px 1px 0 rgba(0,0,0,0.1);
}

.discussion .load-older {
    background: transparent;
}

.discussion .top-note .text-content,
.discussion .post-detail.is-locked .replies-area {
    color: #999999;
}

.discussion-filter-header {
    font-weight: bold;
}

.discussion-filter-header a {
    font-weight: normal;
}

.discussion-standalone-editor .discussion-standalone-editor-toolbar {
    border-bottom: 1px solid transparent;
    background-color: #e6e6e6;
}

.discussion-inline-editor .discussion-textarea-label,
.discussions .discussion-image-upload__button,
.discussion-inline-editor .discussion-inline-editor-submit,
.discussion-categories .discussion-categories-edit-link,
.discussion-filter-header a,
.discussion-forum-tabs li,
.no-followed-posts .find-posts-button,
.discussion .load-more-button,
.user-activity-options a,
.user-activity-options a:active,
.user-activity-options a:visited,
.discussions .discussion-category-picker-required-message,
.discussion-standalone-editor .discussion-standalone-editor-save-button,
.discussion-standalone-editor .discussion-standalone-editor-label,
.poll-add-button,
.discussion-standalone-editor .discussion-standalone-editor-title .discussion-textarea-label,
.editor-input-label-wrapper__label,
.editor-toolbar__button.is-secondary,
.editor-toolbar__button,
.discussions .discussion-image-upload__button.is-desktop,
.poll-add-button.is-desktop,
.discussion.post .load-older,
.discussion-wrapper .wds-button.wds-is-text,
.poll-footer__voted,
.discussion .load-newer {
    text-transform: none;
}

.poll-title,
.post .poll-title {
    background-color: transparent;
    border-bottom: 1px solid transparent;
}

.poll-footer {
    border-top: 1px solid transparent;
}

.poll-wrapper,
.post .poll-wrapper {
    border: 1px solid #cccccc;
}

.discussion .poll-bar {
    background-color: #ffffff;
    border: solid 1px #bbbbbb;
}

.discussions .discussion-category-picker-required-message,
.discussion-inline-editor .discussion-textarea-label,
.discussion-inline-editor .discussion-inline-editor-title .discussion-textarea-counter,
.editor-input-label-wrapper__label,
.editor-textarea-with-counter__counter {
    color: #aaaaaa;
}

.discussion-inline-editor .discussion-inline-editor-toolbar,
.editor-toolbar {
    background-color: #e6e6e6;
}

.discussions .discussion-category-picker-button {
    background: #aaaaaa;
}

.discussion-inline-editor .discussion-inline-editor-floating-container,
.inline-editor__floating-container {
    background-color: #ffffff;
    border: 4px solid #aaaaaa;
    position: relative;
}

.discussion-inline-reply-editor.is-sticky .discussion-inline-editor-floating-container,
.inline-reply-editor.is-sticky .inline-editor__floating-container {
    border: 4px solid #aaaaaa;
    border-bottom: 0;
    bottom: 0;
    margin-left: -20px;
    top: auto;
    width: 564px;
}

.discussions .category-pill {
    background-color: #e6e6e6;
    color: #555555;
}

.discussion.post .post-detail .side-spaced {
    background: #d9e6e9;
}

.discussion .side-spaced {
    padding-left: 30px;
    padding-right: 30px;
    background-color: #d9e6e9;
    background-image: -webkit-gradient(linear,0% 0%,0% 100%,color-stop(50%,#e4edef),color-stop(100%,#d9e6e9));
    box-shadow: 0 2px 2px 0 rgba(0,0,0,0.1);
}

.discussion .post-actions {
    border-top: 1px solid #bbbbbb;
    background-color: #d9e6e9;
    background-image: -webkit-gradient(linear,0% 0%,0% 100%,color-stop(50%,#e4edef),color-stop(100%,#d9e6e9));
    box-shadow: 0 2px 2px 0 rgba(0,0,0,0.1);
}

.discussion-user-list .post-reply,
.discussion-reported-posts .post-reply {
    background-color: #d9e6e9;
    background-image: -webkit-gradient(linear,0% 0%,0% 100%,color-stop(50%,#e4edef),color-stop(100%,#d9e6e9));
    box-shadow: 0 2px 2px 0 rgba(0,0,0,0.1);
}

.post-card-body {
    background-color: #d9e6e9;
    background-image: -webkit-gradient(linear,0% 0%,0% 100%,color-stop(50%,#e4edef),color-stop(100%,#d9e6e9));
}

.poll-answers .poll-answer__result-bar.is-active,
.poll-answers .poll-answer__result-bar {
    background-color: #bfbfbf;
    border-top: 1px solid #d9e6e9;
    border-bottom: 1px solid #d9e6e9;
    opacity: 1;
}

.user-activity__circle-icon {
    color: #996e00;
}

.user-activity__circle {
    background-color: #e6e6e6;
}

.user-activity-main-content {
    background: transparent;
}

.discussion .has-upvoted .icon {
    fill: #6D757A;
}

input[type="text"][disabled],
input[type="text"][readonly],
fieldset[disabled] input[type="text"],
input[type="password"][disabled],
input[type="password"][readonly],
fieldset[disabled] input[type="password"],
input[type="date"][disabled],
input[type="date"][readonly],
fieldset[disabled] input[type="date"],
input[type="datetime"][disabled],
input[type="datetime"][readonly],
fieldset[disabled] input[type="datetime"],
input[type="datetime-local"][disabled],
input[type="datetime-local"][readonly],
fieldset[disabled] input[type="datetime-local"],
input[type="month"][disabled],
input[type="month"][readonly],
fieldset[disabled] input[type="month"],
input[type="week"][disabled],
input[type="week"][readonly],
fieldset[disabled] input[type="week"],
input[type="email"][disabled],
input[type="email"][readonly],
fieldset[disabled] input[type="email"],
input[type="number"][disabled],
input[type="number"][readonly],
fieldset[disabled] input[type="number"],
input[type="search"][disabled],
input[type="search"][readonly],
fieldset[disabled] input[type="search"],
input[type="tel"][disabled],
input[type="tel"][readonly],
fieldset[disabled] input[type="tel"],
input[type="time"][disabled],
input[type="time"][readonly],
fieldset[disabled] input[type="time"],
input[type="url"][disabled],
input[type="url"][readonly],
fieldset[disabled] input[type="url"],
input[type="color"][disabled],
input[type="color"][readonly],
fieldset[disabled] input[type="color"],
textarea[disabled],
textarea[readonly],
fieldset[disabled] textarea {
    background-color: transparent;
}

.editor-textarea:disabled {
    opacity: 1.0;
}

.post-entry-point__items,
.post-entry-point__item:hover {
    background-color: #ffffff;
}

.poll-answers .poll-answer.is-selected .poll-answer__text {
    color: #3a3a3a;
    font-weight: inherit;
}

.poll-answers .poll-answer__result-bar {
    border-radius: 0;
}

.poll-answers .poll-answer__input, .poll-answers .poll-answer__text {
    border-radius: 0;
    color: #3a3a3a;
    background-color: transparent;
    border-color: transparent;
}

.poll-answers .poll-answer__input:focus {
    background-color: transparent;
    border-color: transparent;
}

.is-sticky .mobile-reply-entry-point__sticky-container {
    border-top: 4px solid #aaaaaa;
}

.mobile-reply-entry-point__sticky-container {
    border: 4px solid #aaaaaa;
}

.editor-textarea:invalid {
    font-family: Consolas,"Eupheima UCAS",Ayuthaya,Menlo,monospace;
}

.discussion .load-more-button.top-list-button {
    background-color: #d9e6e9;
    background-image: -webkit-gradient(linear,0% 0%,0% 100%,color-stop(50%,#e4edef),color-stop(100%,#d9e6e9));
    border: 1px solid #bbbbbb;
    border-radius: 2px;
}

.editor-toolbar.is-standalone {
    border-bottom: 0;
}

.discussion-dialog .modal-dialog footer {
    border-top: 1px solid #cccccc;
}

.poll-answers .poll-answer.is-selected .poll-answer__result-bar {
    background-color: #bfbfbf;
    opacity: 1;
}

.post-image-editor-tools-wrapper .post-image-inner-image {
    background-color: #ebe2cc;
    border: 1px solid #996e00;
}

.is-reply.is-sticky .inline-editor__floating-container {
    border: 4px solid #aaaaaa;
}

.forum-tabs>li.not-active {
    border-bottom-color: #3a3a3a;
    color: #3a3a3a;
}
}
