css:
@-moz-document url-prefix("https://yobit.net/") {
body {background-color: #25303c ;}
.left_bar {background:#25303c;}
table.marketes tbody {color: #d3c0c0;}	
.center_box {background: #25303c;}
.right_bar {background: #25303c;}
table.sell_orders tbody {color: #edcccc;}
.fild_box .inset .line_first {color: #ffffff;}
.fild_box .inset .line span {color: #d2c2c2;}
.chat_box #scrollbar7 p {color: #c8bdbd;}
table.balances tbody {color: #8ad78e;}
.fild_box .meta .sm {color: #ffffff;}
.balance_box {
    border-right: 1px solid #49545f;
    border-bottom: 1px solid #25303c;
}
.buy_box {
    border-right: 1px solid #59646f;
    border-bottom: none;
}
.market_box {
    border-right: 1px solid #59646f;
}
.buy_orders_box {
    border-right: 1px solid #59646f;
}
.sell_orders_box {
    border-right: 1px solid #59646f;
}
.sell_box {
    border-right: 1px solid #59646f;
    border-bottom: none;
}
.news_box {
    border-left: 1px solid #59646f;
}
.play_lotto_box {
    border-left: 1px solid #59646f;
    border-bottom: none;
}
.chat_box {
    border-left: 1px solid #59646f;
    border-bottom: 1px solid #59646f;
}

.chat_box .fildes_box .p_1 .p_3 input[type=text] {
   background-color: #59646f;
    color: #fff4f4;
    border: 1px solid #25303c;}

.fild_box .inset .line input[type=text] {
    color: #f6f6f6;
    background-color: #59646f;
    border: 1px solid #25303c;}

.dataTables_wrapper .dataTables_paginate .paginate_button {
    color: #a49898;
}
.investbox_page .create_new2 .poles input[type=text] {
    background-color: #59646f;
    color: #ffffff;
    border: 1px solid #25303c;}

.search_box_trade input[type=search] {
    position: relative;
    left: 4px;
    background-color: transparent;
    font-size: 12px;
    color: #ffffff;
}
table.marketes tbody tr.active {
    background: #606e80;
}
table.marketes tbody tr:hover {
    cursor: pointer;
    background: #737e8c;
}
.paginate_button {
    /* color: #333333!important; */}

.highcharts-background{
fill: #26313c;}
.timeline-Viewport{
background-color: #384e61;
}
.top_center_list li.c_2 {
    color: #ab9c9c;}
.top_center_list li.c_3 {
    color: #ab9c9c;}
.top_center_list li.c_4 {
    color: #ab9c9c;}
.top_center_list li.c_5 {
    color: #ab9c9c;}
.highcharts-series highcharts-tracker{
fill: #182029;
}

.dice_page .create_new input[type=text] {
        color: #ebebeb;
    background-color: #59646f;}

.search_box input[type=search] {
    color: #c1c6cd;
    background-color: #25303c;}
.top_meta_dice .ch .el_chek strong {
    color: #b4b6b7;}
.dice_table tbody {
    color: #b4adad;}
.highcharts-axis-labels{
stroke: #dadada;
}

rect{
stroke: #4e5964;
    fill: #4e5964;
}
.sell_box input[type=button] {
    background: #db0711;
}
.sell_box input[type=button]:hover {
    background: #fa1818;
}
.chart_container a {
    color: #e3dede;
    background-color: #4e5964;
}
.chart_container a:hover {
    background-color: #697d91;
}
.scrolling .thumb {
    background: #4e5964;}
.search_box_trade {
    border: 1px solid #4e5964;}
.top_center {
    border-bottom: 1px solid #4e5964;
}
.big_table tbody tr:nth-child(2n) {
    background: #33404d;
}
.big_table tbody {
    color: #d1c5c5;
}
.paginate_button {
    color: #a39292!important;
    border: 1px solid #ffffff94;
}
}
