@-moz-document url-prefix("https://keep.google.com") {

/* REMOVE TITLE SHADOW */
body .ma6Yeb-r8s4j-gkA7Yd .IZ65Hb-YPqjbf.r4nke-YPqjbf {
	border-bottom: 0;
	box-shadow: none;
	padding-bottom: 9px !important;
	padding-top: 10px;
	margin-bottom: 1px;
}

/* ADD TITLE SHADOW TO TEXT */
body .ma6Yeb-r8s4j-gkA7Yd .IZ65Hb-YPqjbf.r4nke-YPqjbf+.IZ65Hb-YPqjbf.h1U9Be-YPqjbf:not(.LwH6nd),
body .ma6Yeb-r8s4j-gkA7Yd .IZ65Hb-YPqjbf.r4nke-YPqjbf+.gkA7Yd-sKfxWe.rymPhb-IZ65Hb-gkA7Yd {
	border-top: 1px solid #000;
	box-shadow: inset 0px 1px 0px #333;
}

/* REMOVE BORDER TOP */
body .ma6Yeb-r8s4j-gkA7Yd .IZ65Hb-YPqjbf.r4nke-YPqjbf[style*='display: none']+.IZ65Hb-YPqjbf.h1U9Be-YPqjbf:not(.LwH6nd),
body .ma6Yeb-r8s4j-gkA7Yd .IZ65Hb-YPqjbf.r4nke-YPqjbf[style*='display: none']+.gkA7Yd-sKfxWe.rymPhb-IZ65Hb-gkA7Yd {
	border-top: 0;
}

/* ADD TITLE SHADOW TO TEXT */
body .ma6Yeb-r8s4j-gkA7Yd .IZ65Hb-YPqjbf.r4nke-YPqjbf[style*='display: none']+.IZ65Hb-YPqjbf.h1U9Be-YPqjbf:not(.LwH6nd) {
	box-shadow: inset 0px -1px 0px 0px #000;
}

/* REMOVE BOX SHADOW */
body .ma6Yeb-r8s4j-gkA7Yd .IZ65Hb-YPqjbf.r4nke-YPqjbf[style*='display: none']+.gkA7Yd-sKfxWe.rymPhb-IZ65Hb-gkA7Yd {
	box-shadow: none;
}

/* MOVE TITLE TO TOP */
.ma6Yeb-r8s4j-gkA7Yd .IZ65Hb-YPqjbf.r4nke-YPqjbf {
	position: absolute;
	top: 0%;
	width: 100%;
}

/* MOVE IMAGE BELOW TITLE */
.ma6Yeb-r8s4j-gkA7Yd .IZ65Hb-HiaYvf-neVct {
	margin-top: 42px;	
}

/* IF NO TITLE NO MOVE */
.ma6Yeb-r8s4j-gkA7Yd .di8rgd-r4nke .IZ65Hb-HiaYvf-neVct {
	margin-top: 0;
}

/* ADJUST FOR BORDERS */
.ma6Yeb-r8s4j-gkA7Yd .IZ65Hb-YPqjbf.h1U9Be-YPqjbf {
	padding-top: 13px;
}

/* ADJUST FOR BORDERS NO TITLE */
.ma6Yeb-r8s4j-gkA7Yd .IZ65Hb-YPqjbf.r4nke-YPqjbf[style*='display: none']+.IZ65Hb-YPqjbf.h1U9Be-YPqjbf {
	padding-top: 12px;
}

/* ADJUST FOR LISTS  */
.ma6Yeb-r8s4j-gkA7Yd .gkA7Yd-sKfxWe.rymPhb-IZ65Hb-gkA7Yd {
	padding-top: 11px;	
}

/* ADJUST FOR LISTS NO TITLE */
.ma6Yeb-r8s4j-gkA7Yd .IZ65Hb-YPqjbf.r4nke-YPqjbf[style*='display: none']+.gkA7Yd-sKfxWe.rymPhb-IZ65Hb-gkA7Yd {
	padding-top: 10px;
}

}
