/* ==UserStyle==
@name           Dark theme for Epic Games bug report Form
@namespace      github.com/openstyles/stylus
@version        0.1.0
@description    Theme for bug report form for Unreal Engine on Formstack
@author         Alexey Mak (S-ed, Sedokun)
==/UserStyle== */

@-moz-document url-prefix("https://epicsupport.force.com/unrealengine/s/") {
	
	html, body {
	background-color: #303030;
}

.siteforceSldsOneColLayout {
	background-color: #454545 !important;
	border-radius: 5px;
}

.uiInput--input .input,
.uiInput--input.input,
.uiInput--textarea .textarea,
.uiInput--textarea.textarea {
	color: #ccc;
	background-color: #303030;
	border-color: #606060;
}

.uiInput--input.input:focus,
.uiInput--input .input:focus,
.uiInput--input.input:active,
.uiInput--input .input:active,
.uiInput--textarea .textarea:focus,
.uiInput--textarea.textarea:focus,
.uiInput--textarea .textarea:active,
.uiInput--textarea.textarea:active {
	background-color: #303030;
	border-color: #f90;
	box-shadow: none;
}

.slds-button_neutral,
.slds-button--neutral {
	background-color: #303030 !important;
	border-color: #606060;
}

.slds-picklist__label .slds-truncate {
	color: #ccc !important;
}

.slds-button:focus {
	box-shadow: none;
	border-color: #f90;
}

.slds-dropdown {
	background-color: #303030;
	border-color: #606060;
}

.slds-truncate {
	color: #ccc !important;
}

.slds-dropdown__item > a:hover,
.slds-dropdown__item > a:focus {
	background-color: #454545;
}

.cUnrealEngineBugSubmissionsCaseForm .slds-select {
	color: #ccc;
}

option{
	border-color: #606060;
}

.uiInput--select.select:not([multiple]),
.uiInput--select .select:not([multiple]),
.uiInput--select .uiPopupTrigger a {
	background-image: none;
	background-color: #303030;
	border-color: #606060;
}

.uiInput--select.select:focus,
.uiInput--select .select:focus,
.uiInput--select.select:active,
.uiInput--select .select:active,
.uiInput--select .uiPopupTrigger a:focus,
.uiInput--select .uiPopupTrigger a:active {
	box-shadow: none;
	border-color: #f90;
}
}
