css:
@-moz-document domain("eurogamer.net")
{

.advertContainer {display:none;}

.secondary {display:none;}

.primary {background-color:#0084CC;}

.primary a {color:#fff !important}

.sidebar {display:none}

.main {padding-right:0 !important}

#comments {margin-left:0 !important}

.document {float:none; background-color:transparent}

.author {background-color:white !important; margin-left:-20px; border-right:none;}

.homepage-billboard-container {display:none;}
.recommendations {display:none}

.latest {background-color: white; padding: 20px;}

.list-item.deal {border:0 !important; border-top:1px solid #E1E9ED !important; padding:16px 0 0 !important; border-bottom:1px solid #E1E9ED !important}
}
