css:
@-moz-document domain("eurogamer.net") {
#browserMaster,
.below,
.document,
.desktop-header,
.tags,
.job-title,
.article .title,
.cover-wrapper,
.end,
.newsletter-promo,
.from,
.date,
.inline-reply,
.page-header-slim h1 span:not([itemprop=name]),
.blog-item .subtitle,
.small-list .subtitle,
aside, 
aside div,
aside h2,
strong, 
figure, 
figcaption,
p.username, .account .button,
.compact-archive-item .subtitle,
.game-grid-block .grid-item,
#sidebar, #sidebar li, ul.selector, ul.selector li a,
.forum,
.post, 
.content, .content h1,
form, 
em, .timestamp, .thread, .col1, .col2, .col3, .col4, .selector ul,
blockquote,
.breadcrumb,
.search, 
.gsc-control-cse, .gs-snippet,
.gsc-tabsArea, .gsc-tabHeader, .gsc-tabhInactive,
.gsc-cursor-page {
    background-color: rgb(30, 30, 30) !important;
    color: rgb(220, 220, 200) !important;
    background-image: none !important;
}
.list-item .details {
    color: rgb(30, 30, 30);
    background-color: transparent;
}
.medium-list .details,
.stack .details {
    background-color: #0084CC !important;
}
.stack .prefix,
.medium-list .prefix {
    color: rgb(30, 30, 30);
}
.medium-list a.comment-count,
.grid a.comment-count {
    color: #5DAD5D !important;
}
p.section-title {
    color: #0084CC;
}
.button.outline,
.button.outline.on,
.button, .pager .buttons .button,
span.button {
    color: rgb(220, 220, 200);
    background-color: rgb(30, 30, 30);
    background-image: none !important;
}
span.button:disabled,
span.button[disabled] {
    color: rgb(220, 220, 200);
    background-color: rgb(30, 30, 30) !important;
}
.actions .button, button.submit, button.on {
    border-color: rgb(255, 255, 255) !important;
}
.button.on {
    background-color: #0084CC !important;
}
a {
    color: rgb(220, 220, 200) !important;
}
a.username {
    color: rgb(220, 220, 200) !important;
}
#comment-textarea, textarea, .pager {
    background-color: rgb(30, 30, 30);
    color: rgb(220, 220, 200);
    border-color: white;
}
.desktop-mpu {
    width: 0px !important;
    height: 0px !important;
}
aside:after {
    border-bottom: rgb(30, 30, 30) !important;
}
}
