css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.eurogamer.net") {


@media (min-width: 767px){
    
    /* Home Page */
    .medium-list.hd .list-item .details
    {
        height: auto;
        min-height: 0;
        padding-top: 16px;
        padding-bottom: 16px;
    }
    .medium-list.hd .list-item .cover
    {
        margin-bottom: 0;
        height: auto;
        bottom: 0;
        background-position: center center;
    }
    
    .small-list .list-item
    {
        margin-top: 8px;
        padding-top: 8px;
    }
    
    /* Article Page */
	.article,
    .article main,
    .article main h2, 
    .article main h3, 
    .article main h4, 
    .article main ol, 
    .article main p, 
    .article main ul
    {
        max-width: 720px;
    }
    .page-header {
        margin: 0 0 16px;
    }
    .page-header .details
    {
        padding: 16px 6px 12px;
        text-align: left;
    }

    .article main
    {
		font-size: 16px;
        line-height: 1.2;
    }
    
    
    .mormont-recommendations
    {
        border-top: solid 1px #e2e2e2;
        border-bottom: solid 1px #e2e2e2;
        padding: 12px 0 6px;
		margin-bottom: 20px;
    }
    .mormont-recommendations.eurogamer .mormont-recommendation.small:last-child
 	{   
        margin-bottom: 0;
	}
    .article footer {
        margin-top: 16px;
        margin-bottom: 16px;
        padding-top: 16px;
        padding-bottom: 16px;
    }
    /* Comments */
    .container-690
    {
        max-width: 720px;
    }
    
    .threaded-comments .comment .details
    {
        font-size: 15px;
        line-height: 1.3;
        padding-bottom: 16px;
    }
    .threaded-comments .comment .body table
    {
        max-width: 100%;
    }
}

}
