css:
@-moz-document domain("meneame.net") {
/* variables */

:root {
	--base-color: #131313; /* Change this to adjust base color */
	--white: #FFFFFF;
	--pure-black: #000000;
	--blacker: #444444;
	--classic-grey: #cccccc;
	--grey: #2b2b2b;
	--light-grey: #555555;
	--dark-grey: #333333;
	--darker-grey: #222222;
	--darkest-grey: #111111;
	--light-grey: #999999;
	--lighter-grey: #888888;
	--lighten-grey: #777777;
	--warning: #272727;
    --admin: #af8282;
    --meneo: #9a5d40;
	--brand:#ff6600;
}

/* bordes */

div,
a,
span {
	border: none !important;
}

button,
select,
form {
	border-color: var(--blacker) !important;
}


/* fondos */

.menu01-itemsl span.button-new,
.widget-popular-links-slider .link.slick-slide.slick-active,
.section-profile .contents-layout,
.pages a,
.pages span,
.news-summary .warn,
.story-blog .warn,
#toplink,
.news-details a.comments,
.sidebox .header,
fieldset,
.comment-body,
.topbox,
.header-top-wrapper,
.comment-header,
.comment-header a,
.comment-header span,
.comment-header strong,
select,
.sneaker-legend {
	background: var(--base-color) !important;
}


/* fondos_secundarios */

.news-details a:link, .news-details a:visited { border-radius:10px; }
.sidebox .header { border-radius:0; }
.sidebox {
    background:var(--grey);
    margin-top:0;
}
.news-shakeit .votes,
.news-shakeit .clics,
.sidebox .subname,
#toplink a.footer,
.header-menu-wrapper,
.news-shakeit,
.upname a:visited,
.subname a:link,
legend,
legend a,
legend a:visited,
.button,
input[type="button"],
input[type="submit"],
.rich-edit-key {
	color: var(--dark-grey);
	background: var(--light-grey) !important;
}


/* botones */

.section-profile .contents-layout,
.sidebox-sub,
.comment.high .comment-body,
.comment.high .comment-header,
.comment.high .comment-header .comment-date,
.comment.high .comment-header a {
	background: var(--warning) !important;
}


/* botones */

.droparea,
.subname,
.voters-list,
.menealo,
.menealo a,
.section-profile .contents-layout .contents-menu a.selected,
tr:hover td,
table.keyvalue tr:hover th,
.section-profile .contents-layout .contents-menu a:hover,
.section-profile .contents-layout .contents-menu a:hover span .sidebox .clics,
.btn.btn-mnm.btn-inverted,
.news-body .box {
	background: var(--dark-grey) !important;
}


/* textos */
table th,
.section-profile-header-stats strong,
.section-profile-header-info strong { color:var(--white) !important; }
.votes-counter,
.news-summary .warn,
.story-blog .warn,
.note,
body,
.news-content,
p,
span,
.menealo span,
.comment-text,
.section-profile .contents-layout .contents-menu a span,
select {
	color: var(--light-grey) !important;
}
textarea[name="comment_content"] {
	color: var(--white) !important;
}
.sidebox h4 {
	position: relative;
	font-weight: normal;
	text-align: center;
	text-transform: uppercase;
	letter-spacing: 3px;
	line-height: 25px;
}

.sidebox .mainsites ul li {
	display: block;
	color: #b1aeae;
	border: 0.5px solid var(--blacker);
	padding: 8px 10px 10px 10px;
	margin-bottom: 10px;
	-webkit-border-radius: 35px;
	-moz-border-radius: 35px;
	border-radius: 35px;
	margin: -10px auto 20px auto;
	text-align: center;
}
.news-body img.thumbnail {
	position: absolute;
	left: 50%;
	top: 30%;
	max-width: 70%;
	-webkit-transform: translate(-50%, -20%);
	-ms-transform: translate(-50%, -20%);
	transform: translate(-50%, -20%);
	margin: 0;
}

a[style="color: #f00"] { color:#f16767 !important; }
.comment-footer .voted.up, .comment-footer .vote.up:hover { color:green !important; }
.comment-footer .voted.down, .comment-footer .vote.down:hover { color:#f16767 !important; }
.alert-danger {
	background: #5d4d4d !important;
}

.header-menu01 .selected a,
.header-menu01 ul li a:hover {
	background: none;
	border-bottom: 3px solid var(--classic-grey) !important;
	margin-bottom: -3px;
	padding-bottom: 6px;
}
.comment-date { padding:0 !important; }
.comment { overflow:visible; margin-bottom:20px; }
.sidebox-sub .footer,
.sidebox-sub { padding-bottom:10px; background:var(--dark-grey) !important; }
.finished,.sidebox-sub .btn.btn-mnm { background:var(--blacker) !important; }

.news-summary .warn {
    margin-bottom: 6px !important;
    display: inline-block;
    border-radius: 5px;
    background: var(--warning) !important;
}
.news-body .box {
    margin: 8px 0;
    padding: 10px;
    background: var(--warning) !important;
}
textarea[name="comment_content"] {
	margin-top:15px;
    border-color:var(--light-grey);
}
.subs-listing .actions a.following-no { background-color:#4a4a4a; }
.news-content {
    display:block;
    padding:5px 0 0 0;
    margin:5px 0 0 0;
    position:relative !important;
    font-size:0.9em;
}
.edit-link { position:absolute; left:-5px; top:0px; background:var(--dark-grey); text-align:center !important; padding:1px 3px 1px 5px; border-radius:5px; border:1px solid var(--light-grey) !important; }

/* enlaces */

.comment-header a,
.news-details,
.news-details.main {
	color: var(--light-grey) !important;
}

/* medallas */
.section-profile-header-medals img[src*="medal"] { 
    width: 50px;
    height: 50px;
    background-size: contain;
    background-repeat: no-repeat;
    visibility:visible;
}

.section-profile-header-medals * {
	background-color:var(--light-grey) !important;
    border-radius:100px;
    margin:0 5px;
    padding:5px;
}
