/* textos */

.story-blog .best-comments .item .text,
.section,
.section-profile-header-info p,
.menealo span,
.news-details button,
.alert-danger p {
	color: var(--classic-grey) !important;
}

.comment-text a,
.news-details a:link,
.news-details a:visited,
.news-tags,
.news-tags a:link,
.news-tags a:visited,
.comment-text a,
.menealo a {
	font-weight: bold;
	color: var(--lighter-grey) !important;
}

.news-details,
#tooltip-text,
.news-summary.promoted-article .center-content {
	background: var(--darker-grey);
}


/* votos */

.clics,
.clics span,
.news-shakeit .votes,
.sidebox .votes span,
.sidebox .clics span,
.header-menu01 a,
.news-shakeit .votes a,
.alert p,
.comment.high .comment-header {
	color: var(--darker-grey) !important;
}


/* titulos */

h1,
h2,
h2 a,
h3,
h4,
h4 a,
h5,
h6,
.section-profile .contents-layout .contents-menu a span,
.sidebox-sub .description {
	color: var(--classic-grey) !important;
}
/* extras */



.subname.big {
	color: var(--darkest-grey) !important;
	font-size: 10px;
	padding: 1px 8px;
	text-decoration: none !important;
	background: var(--light-grey) !important;
    float:left;
    margin-top:-1px;
    display:block;
}

.current,
.item span {
	color: var(--brand) !important;
}

#footcol1,
.sneaker {
	background: none;
}

.news-submitted a,
a,
.label,
.menealo a,
.searchform .searchbox,
body #variable input#comment-input,
#variable input {
	color: var(--white) !important;
}

/* menu */

body {
	padding-top: 85px;
}

.sidebox .subname a {
	text-decoration: none;
}

#header {
	width: 100%;
	top: 0;
	position: fixed;
	z-index: 100;
}
input,
textarea,
.sidebox .votes,
.sidebox .clics {
	background-color: var(--dark-grey) !important;
    color:var(--white) !important;
}
/* perfil */

.section-profile .contents-layout .contents-body,
body,
.topbox,
.sidebox-rounded.orange .header {
	background: var(--darker-grey) !important;
}


/* admin */
.tabsub {
    height: 32px !important;
    margin: 10px 0 20px 0;
    padding-left: 20px;
    border-bottom: 1px solid;
    border-color: var(brand);
}
.genericform {
	background: none;
}

.tabsub li.tabsub-this a {
	padding: 2px 10px 0px 10px;
}

.tabsub li.tabsub-this,
.genericform .btn {
	background-color: var(--light-grey);
	color: var(--lighter-grey);
	height: 19px;
}

.tabsub li a:hover {
	background-color: var(--lighten-grey);
}
.tabsub li,
.tabsub li a,
.tabsub li.tabsub-this, .genericform .btn { height:23px; border-radius:3px; }

.genericform tr td,
.genericform th {
	background-color: var(--darker-grey);
	color: var(--lighter-grey);
	border-top: 1px solid var(--lighten-grey) !important;
}
.widget-popular-links-slider,
.genericform tr:hover td,
.genericForm tr:hover th {
	background-color: var(--blacker) !important;
}
.sidebox .mainsites ul li,
.genericform tr:nth-child(even) td,
.genericForm tr:nth-child(even) th {
	background-color: var(--dark-grey);
}

#cboxContent,
#notifier_panel {
	background: var(--blacker);
}
#notifier_panel a:hover, #notifier_panel div:hover a {
    color: var(--white) !important;
}
.badge-mnm { 
    color: var(--white) !important; 
}
.alert-warning,
.label-mnm,
.badge-mnm,
.reporters-list {
	background: var(--dark-grey);
}
a.action-link span { color:var(--white) !important; }
td.tooltip { display:table-cell; }
.table-striped>tbody>tr:nth-of-type(odd),
.table-striped>tbody>tr:nth-of-type(odd):hover { background:var(--dark-grey); }
.table-hover>tbody>tr:hover { background:none !important; }
.alert-warning,
.button {
	color: var(--darkest-grey) !important;
}
.news-shakeit { background:#696969 !important; }
.menealo { left:-11px !important; }
.menealo span {
	color: var(--white) !important;
	background: var(--meneo) !important
}
.tabsub li.tabsub-this a:hover,
.comment.high .comment-body a,
.comment.high .comment-body {
	color: var(--light-grey) !important;
}
.table>tbody>tr>td, .table>tbody>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th, .table>thead>tr>td, .table>thead>tr>th,
.sidebox .comments li {
	border-color: var(--dark-grey) !important;
    padding:8px;
}

#cboxContent .voters-list .item a {
	color: var(--lighten-grey);
}

#notifier_panel div:hover {
	background: var(--light-grey) !important;
}

#notifier_panel a,
#notifier_panel {
	color: var(--lighter-grey) !important;
}
.sidebox h5 { line-height:1.5 }
.commentform .sign,
.progress-bar span {
	color: var(--blacker) !important;
}
.story-blog .main-content .text blockquote, .section.section-article-submit .input-editable blockquote,
.poll-vote .poll-option .progress-bar {
	background-color: var(--pure-black);
    color:var(--light-grey);
}


.comment.high .comment-text,
.comment-date,
.news-shakeit,
 ::placeholder {
	color: var(--classic-grey) !important;
    border-radius:3px;
    padding:5px;
    border-width:2px;
    border-color:var(--light-grey);
}
#variable input[type="image"] { padding:8px !important; }
.sidebox .votes {
	border-radius: 10px !important;
	background-color: var(--darker-grey) !important;
}
.popular-link-widget-right div.description,
.sidebox .votes span {
	color: var(--lighten-grey) !important;
}
.genericform td .btn { height:19px; }
.section.section-article-submit .input-title,
.genericform textarea,
input[type="text"].form-control,
.genericform .sign { color:var(--dark-grey) !important; }
#header-top a.sub-name::before {
    content:"Dark Theme | ";
    color:var(--light-grey);
}

#header-top a.sub-name:hover {
    text-decoration:none !important;
}
.form .form-group-highlight,
#editor * {
    color:var(--dark-grey) !important;
    background:var(--classic-grey) !important;
    padding:10px;
}
.form .form-group-highlight { padding:2px; }

.comment.high .comment-text,
.comment-date,
.news-shakeit,
 ::placeholder {
	color: var(--classic-grey) !important;
    border-radius:3px;
    padding:5px;
    border-width:2px;
    border-color:var(--light-grey);
}
#variable input[type="image"] { padding:8px !important; }
.sidebox .votes {
	border-radius: 10px !important;
	background-color: var(--darker-grey) !important;
}
.popular-link-widget-right div.description,
.sidebox .votes span {
	color: var(--lighten-grey) !important;
}
.genericform td .btn { height:19px; }
.section.section-article-submit .input-title,
.genericform textarea,
input[type="text"].form-control,
.genericform .sign { color:var(--dark-grey) !important; }
#header-top a.sub-name::before {
    content:"Dark Theme | ";
    color:var(--light-grey);
}

#header-top a.sub-name:hover {
    text-decoration:none !important;
}
.form .form-group-highlight,
#editor * {
    color:var(--dark-grey) !important;
    background:var(--classic-grey) !important;
    padding:10px;
}
.form .form-group-highlight { padding:2px; }

.comment.high .comment-header,
.comment.high .comment-header a,
.comment.high .comment-header span.ts,
.comment.high .comment-body,
.comment.high .comment-header strong { background-color:var(--dark-grey) !important; }
/* fisgona */

.sneaker-chat,
.sneaker-chat a {
	color: #dc2f2f;
}

.sneaker-chat-friends,
.sneaker-chat-friends a {
	color: #39ad39;
}
.section-profile h3 { margin-bottom:15px !important; }


.news-shakeit .votes a,.clics,.clicks * { color:var(--darkest-grey) !important; }

/* admin */
.decorated input { margin:0; }
.sneaker-chat, .sneaker-chat a,
.sneaker-chat-admin, .sneaker-chat-admin a { color:var(--admin); }
.report-action,.admin-button,.table-striped .btn {
    display:inline-block;
    height:auto !important;
