css:
@-moz-document url-prefix("https://redserver.su/"), url-prefix("http://redserver.su/") {
.rs-scroll-controller {
	position: fixed;
	bottom: 0px;
	left: 0px;
	width: 17vh;
	height: 100vh;
	z-index: 100;
    opacity: 0.5;
     vertical-align: top;
}
.rs-scroll-controller:hover {
    opacity: 1;
    background-color: rgba(17, 20, 64, .3);
    filter: alpha(opacity=100);
}


.rs-scroll-btn {
	background-color: rgba(255, 255,255, 0);
    width: 100%;
	height: 100%;
	border: 0;
	transform: rotate(0);
	line-height: 1.75;
	transition: .15s ease-in-out;
	transition-property: transform opacity;
	opacity: 1;
	visibility: visible;
    color: rgba(255, 255,255, 1);
    cursor: pointer;
}

.rs-scroll-btn .top{
    margin-right: 24px;
    text-align: center;
	vertical-align: middle;                                            
    height: 98%;
	font-size: 15px;
    font-family: Arial;
    font-weight: bold;
	color: rgba(255, 255,255, 1);
}

.rs-scroll-btn .bot{
    text-align: left;
    margin-left: 7px;
    height: 98%;
	font-size: 15px;
    font-family: Arial;
	color: rgba(255, 255,255, 1);
}

.rs-scroll-btn:focus {
	outline: none;
}

.rs-scroll-btn[data-scroll-position=hide] {
	opacity: 0;
	visibility: hidden;
}

.rs-scroll-btn[data-scroll-position=""] {
	opacity: 0;
	visibility: hidden;
}

@media (max-width: 100vh) {
	.rs-scroll-controller {
		display: none;
		visibility: hidden;
	}
}
}
