css:
@-moz-document domain("htmlbook.ru") {
.layout {
    background: linear-gradient(to right, #3b3a35, #3b3a35 210px, #232221 211px, #232221);
}

.jspTrack {
    background: #45443f;
}

aside {
    background: #3b3a35;}

header {
    background: #3b3a35;
 
}

a {
    color: #3D8754;
}   

a:visited {

    color: #7d8754;
}


a:hover {
    color: #4Da764;
}

a:visited:hover {

    color: #8da764;
}

a:visited:active {

    color: #d85858;
}

a:active {
    color: #d85858;
}

.spec th, .cssver th {
    background: #6C6A61;
}

.sp td.warn, .warn {
    background: #383321;
}

.tile li a {
    color: #2D6744;
}

.page-links a {
    color: #2D6744;
}
 

.faq-popular .views-field-field-faq-img, .faq-popular .views-field-field-html-cat, .faq-popular .views-field-field-css-img {
    opacity: 0;
}

body {
    color: #dbdbdb;
}

.info {
    background: #151313;
}

.example, pre, .example2 {
    background: #050404;
}

pre code {
    border-right: 25px solid #171716;
}

.sp td.yes, .yes {
    background: #101a12;
}

td.yes {
    background: #37473a;
}

.standart td {
    border: 1px solid #898477;
}

.standart td.xh {
    border-left: 3px double #898477;
}

td.no {
    background: #4a3131;
}

.br td {
    background-color: #5F5D58;
}

.standart td.h, .standart td.xh {
    background: #484338;;
}

.example-support .html, .example-support .css {
    border-right: 1px solid #4d534c;
}

.example-support span {
    border-left: 1px solid #4d534c;
    padding: 4px;
}

h1 {
    color: #fff;
}

#content h2 {
    color: #e3e3e3;
}

h3, .ref .exampleTitle {
    color: #e3e3e3;
}

.tag {
    color: #bb6800;
}

.tag .keyword {
    color: #bb6800;
}

.exampleTitle, .exampleTitle2 {
    color: #bebaab;
}

.css .keyword {
    color: #dbdbdb;
}



#views-similar-block, #views-similar-html, #views-similar-css, #views-similar-cssref, #views-similar-cssfaq, #views-similar-htmlref, #views-similar-practical, #views-similar-seealso, #views-ref-practical {
    background: #39362F;
    border-top: 3px solid #CA5600;
}

.page-links {
    background: #39362F;
    padding: 10px;
    margin: 20px 0 30px;
    text-align: center;
}

#topmenu .show, .tile {
    background: #B48B69;
}

#topmenu li {
    background: #7a6f6c;
}

aside .block_title {
    background: #CCCABB;
    color: rgba(62, 61, 56, 0.8);
    text-shadow: 1px 2px 3px #00000080, 0 0 0 #000, 1px 2px 3px #DAD7C5;
}

.pointer {
    border-color: transparent transparent transparent #3C5637;
}

.active-trail a.active, .open a.active, #views-practical_block-theme .active, #views-blog_last-block .active {
    background: #3C5637;
}

#ac {
    color: #c5c5c5;
    background: #242424;
}

#q {
    color: #c5c5c5;
    background: #242424;
}

#filter {
    background: #333;
    border: 1px solid #666;
}

#cse-search-box {
    background: #333;
    border: 1px solid #666;
}

.note {
    background: url(https://cdn1.savepice.ru/uploads/2018/3/21/4fd0617968aae260d64ea631b37eead1-full.png) no-repeat 0 3px;
}

.attribute, .css .rule .keyword {
    color: #d85858;
}

.placeholder{
    background: #333;

}

.tile a.active {
    background: #7b664a;
}

#main-nav {
    background: #7b664a;
}

footer {
   
    background: linear-gradient(to bottom, #3b3a35 0%, #030201 100%);
}

.sp td.no, .no {
    background: #481212;
}
}

@-moz-document url-prefix("https://disqus.com/embed/comments/?base=default&f=htmlbook") {
body {
    color: #ededed;
    background: #232221
}

.dropdown-toggle {
    color: #a49b8b !important;
}

.nav-tab > a {
    color: #a49b8b;
}

.tab-conversation.active > a {
    color: #a49b8b !important;
}
}
