css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {
/*Change this url to have another background in the "main menu"*/

:root {
    --c1: #a51f1f;
    --c2: #5d4f86;
    --c3: rgba(165, 31, 31, 0.53);
}

body {
    color: white;
    font-size: 10pt;
    font-family: Verdana, Helvetica, Arial, sans-serif;
    background: url('http://i.imgur.com/wlHm7B4.png') !important;
    /*Here you can adjust the size of the background image: 100% is the exact size, more is bigger and less is smaller*/
    background-size: 100%;
    /*Here you can adjust the position of the background image along the y axis*/
    background-position-y: 100%;
    /*Here you can adjust the position of the background image along the x axis*/
    background-position-x: 100%;
    /*Here you can choose if and how the background image will be repeated, by default it's repeated*/
    background-repeat: repeat;
}
.fa-user::before {
    content: "";
}

/*Change this url to have another Pokemon near your name, the image must be 32x32*/
.userbar::before {
    display: block;
    content: "";
    width: 32px;
    height: 32px;
    float: left;
    position: relative;
    top: -7px;
    background: url('http://graphics.tppcrpg.net/mini/376.gif');
}
.header .logo {
    float: left;
    margin: 2px 0 0 2px;
    height: 0px;
}
.header {
    background: #464547b3;
    background-image: url(https://di.community/uploads/monthly_2018_01/DIBanner-LightTransBG-100.png.29dfce8699aa57295b572dac2e9cf880.png);
    background-repeat: no-repeat;
    background-size: 11%;
    background-position-y: 47%;
}
a:link,
a:link:hover {
    color: var(--c1);
}
a:visited {
    color: #8e8e8e;
}
a.ilink.yours,
a.subtle:hover {
    color: #8e8e8e;
}
.button,
.popupmenu button.button,
button {
    outline: none;
    cursor: pointer;
    text-align: center;
    text-decoration: none;
    border-radius: 5px;
    box-shadow: none;
    font-family: Verdana, Helvetica, Arial, sans-serif;
    color: #ececec;
    text-shadow: none;
    border: solid 1px #ececec;
    background: #393939;
    font-size: 9pt;
    padding: 3px 8px;
    height: auto;
}
.button:hover,
.popupmenu button.button:hover,
.button:active,
.button:focus,
button:hover {
    border: 1px solid var(--c1);
    background: -webkit-linear-gradient(left top, rgba(0, 0, 0, 0.2), rgba(3, 2, 2, 0.44)), #464547b3;
    border-radius: 5px;
    -webkit-box-shadow: 0 1px 2px rgba(0, 0, 0, .2), inset 0 -1px 2px rgba(255, 255, 255, 1);
    -moz-box-shadow: 0 1px 2px rgba(0, 0, 0, .2), inset 0 -1px 2px rgba(255, 255, 255, 1);
    font-family: Verdana, Helvetica, Arial, sans-serif;
    color: #ececec;
    box-shadow: none;
}
.button.disabled,
.button.disabled:hover,
.button.disabled:active,
.mainmenuwrapper .menugroup .button.disabled,
.mainmenuwrapper .menugroup .button.disabled:hover,
.mainmenuwrapper .menugroup .button.disabled:active {
    cursor: default;
    background: #393939;
    border-color: #000;
    border-style: solid;
    color: #000;
    text-shadow: none;
    box-shadow: 0 1px 2px rgba(0, 0, 0, .1);
    opacity: 0.9;
}
.closebutton:hover,
.closebutton:active,
.minimizebutton:hover,
.minimizebutton:active {
    color: #BB2222;
    border: 0px;
    background: transparent;
    border-radius: 0px;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    font-family: Verdana, Helvetica, Arial, sans-serif;
    box-shadow: none;
}
.minimizebutton:hover,
.minimizebutton:active {
    color: #FFF;
}
div.roomlist:nth-child(2) > p:nth-child(1) > button:nth-child(1) {
    position: relative;
    top: -7px;
    right: -30px;
}
div.roomlist:nth-child(2) > p:nth-child(1) > span:nth-child(2) {
    position: relative;
    top: -9px;
    left: -90px;
}
.ps-popup > p:nth-child(1) > strong:nth-child(2) {
    font-size: 12pt;
    font-weight: bold;
}

/***************/

/*****PopUp*****/

/***************/
.ps-popup {
    position: absolute;
    top: auto;
    right: auto;
    left: auto;
    bottom: auto;
    text-align: left;
    background: #282828;
    color: #ececec;
    border: 2px solid var(--c1);
    padding: 2px 10px;
    border-radius: 13px;
    box-shadow: none;
    z-index: 20;
    padding-top: 4px;
    padding-bottom: 4px;
}
.popupmenu li:first-child h3,
.popupmenu h3 {
    margin-top: 0;
    color: #ececec;
    font-weight: bold;
}
.popupmenu button {
    height: 16px;
}
.popupmenu button.sel {
    border-color: var(--c1);
    padding: 0 3px;
    font-style: italic;
    font-weight: bold;
    background: transparent;
    color: #c5c1c1;
}
.popupmenu button {
    color: #c5c1c1;
}
.popupmenu button:hover,
.popupmenu button.sel:hover {
    border-radius: 0px;
    border-bottom-left-radius: 0px;
    border-left: 1px solid #ececec;
    background: rgba(0, 0, 0, 0);
    color: var(--c1);
    border-top-width: 0px;
    border-right-width: 0px;
    border-bottom-width: 0px;
}
.ps-popup .textbox {
    border: 1px solid #AAAAAA;
    border-radius: 3px;
    padding: 2px 3px;
    font-family: Verdana, Helvetica, Arial, sans-serif;
    font-size: 9pt;
    box-shadow: inset 0px 1px 2px #CCCCCC, 1px 1px 0 rgba(255, 255, 255, .6);
    background: #F8FBFD;
    color: black;
}
.userdetails > strong:nth-child(2) > a:nth-child(1),
.userdetails > strong:nth-child(1) > a:nth-child(1) {
    font-size: 11pt;
}

/***************/

/*BarraLaterale*/

/***************/
.ps-room {
    top: 52px;
}
.ps-room.ps-room-light {
    background: rgb(45, 45, 45);
    color: #f8f8f8;
}
.maintabbarbottom {
    bottom: -2px;
    height: 3px;
    background: #f8f8f8;
    border: none;
}
.roomlist a.ilink {
    border: 3px solid #ececec;
    background: -webkit-linear-gradient(left top, rgba(0, 0, 0, 0.1), rgba(3, 2, 2, 0.44)), url('http://i.imgur.com/cON9TA0.png');
    border-radius: 8px;
    color: #ececec;
    text-shadow: none;
    font-size: 11pt;
    font-style: italic;
}
.roomlist a.ilink:hover {
    border-color: var(--c1);
    background: -webkit-linear-gradient(left top, rgba(0, 0, 0, 0.1), rgba(3, 2, 2, 0.44)), url(http://i.imgur.com/cON9TA0.png);
    color: #ececec;
    box-shadow: 1px 1px 2px rgba(0, 0, 0, 0.7), -1px -2px 0px rgba(0, 0, 0, 0.16) inset, 0px 1px 1px rgba(0, 0, 0, 0.45) inset;
}
.roomcounters > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > button:nth-child(2),
.roomcounters > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) > button:nth-child(1) {
    color: #ececec;
    text-shadow: none;
    box-shadow: none;
    border: 2px solid var(--c1);
    background: -webkit-linear-gradient(left top, rgba(0, 0, 0, 0.2), rgba(3, 2, 2, 0.44)), #464547b3;
    border-radius: 9px;
    opacity: 0.9;
}
.roomcounters > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > button:nth-child(2):hover,
.roomcounters > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) > button:nth-child(1):hover {
    box-shadow: 2px 2px 3px rgba(0, 0, 0, 0.7), -1px -2px 0px rgba(0, 0, 0, 0.16) inset, 0px 1px 1px rgba(0, 0, 0, 0.45) inset;
    border-color: #ececec;
}

/********/

/*Header*/

/********/
.roomlisttop {
    margin-top: 38px;
}
.pad > button:nth-child(1) {
    float: left !important;
}
.tabbar li:first-child a.button {
    margin-left: 10px;
}
.tabbar a.button.cur,
.tabbar a.button.cur:hover {
    color: #f8f8f8;
    text-shadow: none;
    background-image: url("http://i.imgur.com/Lr9P13M.png");
    box-shadow: none;
    border: solid 3px #ececec;
    border-bottom: 0;
    cursor: default;
    padding-top: 3px;
    padding-bottom: 5px;
    top: 1px;
    margin-right: -4px;
}
.tabbar a.button {
    outline: none;
    display: inline-block;
    height: 28px;
    min-width: 55px;
    cursor: pointer;
    text-align: center;
    text-decoration: none;
    border-radius: 0px;
    white-space: nowrap;
    transform: skewX(-30deg);
    color: #ececec;
    border: solid 3px var(--c1);
    background-image: url("http://i.imgur.com/Lr9P13M.png");
    position: relative;
    padding: 3px 2px 3px 2px;
    padding-right: 2px;
    margin: 0 -3px 0 0;
    margin-left: 0px;
    top: 1px;
    border-radius: 0;
    border-top-left-radius: 0px;
    box-shadow: none;
    font-size: 11px;
    text-shadow: none;
}
.tabbar a.button span,
.tabbar a.button i {
    transform: skew(30deg);
}
.tabbar a.closable {
    padding-right: 6px;
}
.tabbar a.button:hover,
.tabbar a.button.cur:hover,
.tabbar a.button.notifying {
    background: -webkit-linear-gradient(left top, rgba(0, 0, 0, 0.1), rgba(3, 2, 2, 0.44)), url("http://i.imgur.com/Lr9P13M.png");
    border-color: #ececec
}
.userbar .username {
    font-size: 11pt;
}

/**************/

/***MainMenu***/

/**************/
.menugroup {
    background: #393939;
}
.menugroup .button.big,
body .button.mainmenu2,
body .button.mainmenu3,
body .button.mainmenu4,
body .button.mainmenu5,
body .button.mainmenu1 {
    border: 2px solid #ececec;
    background: -webkit-linear-gradient(left top, rgba(0, 0, 0, 0.1), rgba(3, 2, 2, 0.44)), url('http://i.imgur.com/cON9TA0.png');
    border-radius: 8px;
    color: #ececec;
    box-shadow: none
}
body .button.mainmenu2,
body .button.mainmenu3,
body .button.mainmenu4,
body .button.mainmenu5,
body .button.mainmenu1,
.menugroup .button.big:focus,
.menugroup .button.big:hover {
    border: 2px solid var(--c1);
    background: -webkit-linear-gradient(left top, rgba(0, 0, 0, 0.1), rgba(3, 2, 2, 0.44)), url('http://i.imgur.com/cON9TA0.png');
    color: #ececec;
}
body .button.mainmenu2:hover,
body .button.mainmenu3:hover,
body .button.mainmenu4:hover,
body .button.mainmenu5:hover,
body .button.mainmenu1:hover,
body .button.mainmenu2:focus,
body .button.mainmenu3:focus,
body .button.mainmenu4:focus,
body .button.mainmenu5:focus,
body .button.mainmenu1:focus {
    border: 2px solid #ececec;
    background: -webkit-linear-gradient(left top, rgba(0, 0, 0, 0.1), rgba(3, 2, 2, 0.44)), url('http://i.imgur.com/cON9TA0.png');
}
.select,
.select:focus {
    background: url('http://i.imgur.com/cON9TA0.png');
    color: #ececec;
    font-weight: bold;
    border: 0px solid transparent;
    box-shadow: 2px 2px 4px rgba(36, 36, 36, 0.7), -2px -2px 2px rgba(0, 0, 0, 0.16) inset, 0px 1px 1px rgba(255, 255, 255, 0.35) inset;
}
.select:hover {
    background: -webkit-linear-gradient(left top, rgba(95, 95, 95, 0.2), rgba(15, 12, 12, .22)), url('http://i.imgur.com/cON9TA0.png');
    border: 0px solid transparent;
    box-shadow: 2px 2px 4px rgba(36, 36, 36, 0.7), -2px -2px 2px rgba(0, 0, 0, 0.16) inset, 0px 1px 1px rgba(255, 255, 255, 0.35) inset;
}
.select::after,
.select:disabled::after,
.teamselect > strong:nth-child(1) {
    color: #fff;
}
.teamselect {
    height: 50px;
    white-space: nowrap;
    overflow: hidden;
}
.teamselect,
.teamselect:hover,
.teamselect:active,
.teamselect:focus {
    width: 230px;
    padding: 0 5px;
    font-size: 9pt;
    text-align: left;
    font-family: Verdana, Helvetica, Arial, sans-serif;
    cursor: pointer;
    border-radius: 7px;
    border: 1px solid transparent;
    background: #fff;
    box-shadow: inset 0 1px 0 #FFFFFF;
    background: linear-gradient(to bottom, #b4b4b4, #b4b4b4);
    color: #ececec;
    box-sizing: border-box;
    height: 50px;
    white-space: nowrap;
    overflow: hidden;
    text-shadow: 1px 1px 1px #3c3c3c;
}
.challenge > form:nth-child(1) > p:nth-child(2) > label:nth-child(1),
.challenge > form:nth-child(1) > p:nth-child(3) > label:nth-child(1) {
    color: #ececec;
    text-shadow: 0 1px 0 black;
}
div.roomlist:nth-child(2) > div:nth-child(1) > a:nth-child(1) {
    box-shadow: none;
    border: 2px solid #ececec;
}
div.roomlist:nth-child(2) > div:nth-child(1) > a:nth-child(1):hover {
    box-shadow: none;
    border: 2px solid var(--c1);
}
div.roomlist:nth-child(2) > div:nth-child(1) > a:nth-child(1) {
    font-style: normal;
}

/**********/

/****PM****/

/**********/
.pm-window h3,
.pm-window h3:hover,
.pm-window h3:focus,
.pm-window.focused h3,
.pm-window.focused h3:hover {
    background: rgb(57, 57, 57);
    color: #ececec;
    padding: 4px 3px 4px 7px;
    border: 2px solid rgb(57, 57, 57);
    border-top-right-radius: 8px;
    border-top-left-radius: 8px;
}
.pm-window h3.pm-minimized {
    border-radius: 8px;
}
.pm-log,
.pm-window.focused .pm-log {
    background: #282828;
    color: #ececec;
    word-wrap: break-word;
    border: 2px solid #393939;
    border-bottom: 0px;
}
.newsentry {
    font-size: 9pt;
    padding: 1px 10px;
    border-bottom: 2px solid #393939;
}
.unread {
    background-color: var(--c3);
}
.pm-log-add,
.pm-window.focused .pm-log-add {
    color: black;
    border: 1px solid #393939;
    background: rgb(57, 57, 57);
}
.chatbox.nolabel {
    margin: 0 14px 0 6px;
    padding: 5px 0 5px 0;
    border: 1px solid transparent;
}
.nolabel > textarea:nth-child(2),
.nolabel > textarea:nth-child(2):hover,
.nolabel > textarea:nth-child(2):active,
.nolabel > textarea:nth-child(2):focus {
    border: 1px solid #AAAAAA;
    border-radius: 3px;
    padding: 2px 3px;
    font-family: Verdana, Helvetica, Arial, sans-serif;
    font-size: 9pt;
    box-shadow: inset 0px 1px 2px #CCCCCC, 1px 1px 0 rgba(255, 255, 255, .6);
    background: #F8FBFD;
    color: black;
}
.chat.mine {
    background: none;
}
.pm-window h3.pm-notifying {
    border-color: #813232;
    background-color: #813232;
}
.pm-window h3.pm-notifying:hover {
    border-color: #8c3434;
    ;
    background-color: #8c3434;
    ;
    box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.5);
}
.challenge {
    margin-top: -1px;
    background: rgb(65, 65, 65);
    border: 1px solid #393939;
    color: #ececec;
    padding: 1px 5px;
    border-bottom: 0px;
}
.pm-log-add,
.pm-window.focused .pm-log-add {
    color: #ececec;
}

/***********/

/*ChatRooms*/

/***********/
.userlist, .userlist li {
    display: block;
    list-style-type: none;
    margin: 0;
    padding: 0;
    text-align: left;
    z-index: 20;
}
.userlist li {
    border-bottom: 0px;
    height: 19px;
    font: 11pt Verdana, sans-serif;
    white-space: nowrap;
}
.userlist-maximized {
    height: auto;
    bottom: 0;
    overflow: auto;
    background: #2d2d2d;
}
.userlist li button {
    box-sizing: border-box;
    display: block;
    height: auto;
    background: rgb(45, 45, 45);
    border: 0;
    padding: 1px 0;
    margin: 0;
    height: 19px;
    width: 100%;
    white-space: nowrap;
    font: 10pt Verdana, sans-serif;
    text-align: left;
    cursor: pointer;
    border-radius: 0px;
}
.userlist li button:hover,
.userlist li button:active {
    background: #393939;
    border-radius: 0px;
}
.chat-log {
    background: rgb(40, 40, 40);
    color: #ececec;
}
.broadcast-red {
    background-color: var(--c1);
}
.infobox {
    border: 1px solid #ececec;
}
.textbox:focus,
.textbox:hover {
    border-color: #AAAAAA;
    box-shadow: inset 0px 1px 2px #CCCCCC, 1px 1px 0 rgba(255, 255, 255, .6);
}
.chat-log-add {
    padding: 3px 10px 0 4px;
    border-top: 1px solid #ececec;
}
.chatbox label {
    padding-top: 4px;
    width: 78px;
    margin-left: -79px;
}
.chatbox {
    padding: 4px 0 0 0;
}
.userlist-minimized .userlist-count,
.userlist-maximized .userlist-count,
.userlist-minimized .userlist-count:focus,
.userlist-maximized .userlist-count:focus,
.userlist-minimized .userlist-count:hover,
.userlist-maximized .userlist-count:hover {
    cursor: pointer;
    background: #393939;
}

.message-announce {
    background: #72473E;
    color: white;
    padding: 1px 4px 2px;
}

/* Tournaments */
.tournament-bracket-tree-node-loss > rect {
    stroke: rgba(42, 36, 36, 0.8);
    color: black;
    fill: rgba(134, 37, 23, 0.7);
}
.tournament-bracket-tree-node-win > rect {
    fill: rgb(58, 89, 143);
    stroke: rgba(42, 36, 36, 0.8);
    color: black;
}
.tournament-bracket-tree-node-match-team-draw,
.tournament-bracket-tree-node-match-team-loss {
    fill: #d8d8d8;
}
.ps-popup > div:nth-child(1) > svg:nth-child(1) > g:nth-child(1) > g:nth-child(21) > rect:nth-child(1) {
    fill: rgba(114, 114, 114, 0.9);
    stroke: rgba(42, 36, 36, 0.8);
    color: black;
}
.tournament-status,
.tournament-toggle {
    background-color: rgb(57, 57, 59);
    border-right: 1px solid #ececec;
    color: #ececec;
}
.tournament-wrapper,
.tournament-wrapper:active {
    border-bottom: 1px solid #ececec;
}
.tournament-title {
    color: #ececec;
    text-align: center;
}
.tournament-box {
    border-bottom: 1px solid #ececec;
    border-top: 1px solid #ececec;
    border-right: 1px solid #ececec;
    background-color: rgb(57, 57, 59);
}
.tournament-wrapper.active + .chat-log {
    top: 28px;
}
.tournament-bracket-tree-node > text > a,
.tournament-bracket-tree-node > text > a:hover {
    fill: var(--c1);
    font-weight: bold;
}

/*********************************************************
 * Ladder
 *********************************************************/
.ladder {
    background: #282828;
}
.ladder table {
    width: 490px;
}
.ladder table,
.ladder td,
.ladder th {
    border-collapse: collapse;
    border: 1px solid #ECECEC;
}
.ladder td,
.ladder th {
    padding: 3px 5px;
    font-family: Verdana;
    font-size: 11pt;
}
.ladder th {
    text-align: left;
    font-size: 10pt;
    background: #902323;
    ;
    color: #ececec;
}
.ladder span {
    color: #aaa;
}
.ladder > table:nth-child(4) > tbody:nth-child(1) > tr:nth-child(2) {
    color: #e4cf5f;
    font-weight: bold;
}
.ladder > table:nth-child(4) > tbody:nth-child(1) > tr:nth-child(3) {
    color: #abb3c2;
    font-weight: bold;
}
.ladder > table:nth-child(4) > tbody:nth-child(1) > tr:nth-child(4) {
    color: #a84f4f;
    font-weight: bold;
}

/*Teambuilder*/
.folderpane {
    border-left: 5px solid transparent;
    background-color: #2d2d2d;
}
.folder.cur .selectFolder,
.folder.cur .selectFolder:hover,
.folder.cur .selectFolder:active {
    cursor: default;
    color: var(--c2) !important;
    background: transparent;
    padding-left: 6px;
    border: 2px transparent;
    border-right: 2px solid #ececec;
    font-weight: bold;
}
.folder .selectFolder {
    background-color: transparent !important;
    color: #ececec !important;
    padding-top: 7px;
    border-right: 2px solid #ececec;
}
.folder .selectFolder:hover {
    background-color: transparent !important;
    color: var(--c2) !important;
    border-right: 2px solid #ececec;
}
.folderpane h3 {
    background-color: transparent !important;
    color: var(--c1) !important;
    border-bottom: 2px solid #ececec;
    border-left: 2px solid #ececec;
    padding: 1px 5px;
    border-right: 2px solid #ececec;
    border-bottom-left-radius: 6px;
    cursor: context-menu;
    font-weight: bold;
}
.folderpane {
    border-color: #282828;
    border-right-color: rgb(40, 40, 40);
    border-left-color: rgb(40, 40, 40);
    border-right: 2px solid #ececec;
    position: absolute;
