.switchmenu {
    padding-top: 12px;
}
.switchmenu button,
.switchmenu button:hover,
.switchmenu button:active {
    border: 1px solid #ececec;
    background: #393939;
    text-shadow: none;
    box-shadow: none;
    color: #ececec;
    text-align: left;
    border-radius: 5px;
    margin-left: 0;
}
.switchmenu button:hover,
.switchmenu button:active {
    border-color: var(--c1);
    background: -webkit-linear-gradient(left top, rgba(0, 0, 0, 0.2), rgba(3, 2, 2, 0.44)), #464547b3;
}
.switchmenu button.disabled,
.switchmenu button:disabled,
.switchmenu button.disabled:hover {
    cursor: default;
    background: rgba(101, 101, 101, 0.5) !important;
    border-color: transparent !important;
    -webkit-box-shadow: none !important;
    box-shadow: none !important;
    color: #fff !important;
}
.megaevo:hover {
    border: 2px solid var(--c1);
    background: -webkit-linear-gradient(left top, rgba(0, 0, 0, 0.2), rgba(3, 2, 2, 0.44)), #464547b3;
    -webkit-box-shadow: 0 1px 2px rgba(0, 0, 0, .2), inset 0 -1px 2px rgba(255, 255, 255, 1);
    font-family: Verdana, Helvetica, Arial, sans-serif;
    color: #ececec;
    box-shadow: none;
}
.megaevo {
    clear: both;
    display: block;
    width: 174px;
    margin: 0 auto 0;
    position: relative;
    top: 9px;
    left: -7px;
    padding: 2px;
    text-align: center;
    cursor: pointer;
    border: 2px solid #ececec;
    border-radius: 11px;
    color: #ececec;
    background: #393939;
    font-size: 10pt;
    font-weight: bold;
    padding-top: 0px;
}

.ps-room .battle-userlist {
    left: 641px;
    background-color: #2d2d2d;
}

.chat.highlighted {
    background: var(--c1);
}

.battle-log .chat.highlighted {
    background: var(--c1);
}

.folderpane {
    overflow: hidden;
}

.replayDownloadButton {
    position: relative;
    float: right;
}

.controls > p:nth-child(1) > button:nth-child(2) {
    width: 113px;
}

.rightbar, .leftbar {
    background: #393939;
    width: 14%;
}

.rightbar {
    border-left: 2px solid #ececec;
    width: 15%;
}

.leftbar {
    border-right: 2px solid #ececec;
}

.ps-room .battle-controls {
    background: #282828;
    color: #ececec;
    border-top: 2px solid #ececec;
    top: 360px !important;
    padding-top: 10px;
}

div[class*=battle-log]{
    border-left: 1px solid #ececec;
}

.turn {
    transform: perspective(150px) rotatex(-25deg);
    background: #282828;
    top: -4px;
    width: 80px;
    height: 24px;
    border: 2px solid #ececec;
    padding-left: 12px;
}

.trainer {
    left: 1px;
}

.rightbar .trainer{
    right: 4px;
}

.leftbar .trainer{
    right: -4px;
}

.rightbar .trainer strong{
    left: 8px;
}

*::-webkit-scrollbar-track
{
	border-radius: 10px;
	background-color: transparent;
}

*::-webkit-scrollbar
{
	width: 6px;
	background-color: transparent;
}

.message-announce {
    background: #393939;
    color: white;
    padding: 1px 4px 2px;
}

.leftbar::before {
    display: none;
}
}
