/* ==UserStyle==
@name           Crunchyroll Large Player 720p for 1080p Monitors
@namespace      USO Archive
@author         Eremes
@description    `For 1080p Monitors.

Please select if premium or not because HTML change if your account are free or premium.`
@version        2020305.22.2
@license        NO-REDISTRIBUTION
@preprocessor   uso
@advanced dropdown ispremium "Premium Account?" {
ispremium "No" <<<EOT 760 EOT;
ispremiumYes "Yes" <<<EOT 0 EOT;
}
==/UserStyle== */
@-moz-document domain("www.crunchyroll.com") {

	#featured_carousel {
		min-width: 900px !important;
	}

	#welcome_left {
		width: 900px !important;
	}

	#showmedia_video_player,
	#showmedia_video_box_wide {
		height: 720px !important;
		width: 1280px !important;
	}

	#showmedia_video {
		height: 720px !important;
		width: 1280px !important;
	}

	#showmedia {
		width: 1280px !important;
	}

	#showmedia #main_content {
		width: 960px;
	}

	#showmedia #sidebar {
		margin-top: /*[[ispremium]]*/px !important;
	}

	.showmedia-submenu {
		height: auto;
	}

	.game-banner-wrapper {
		text-align: center !important;
	}

	.game-banner-container {
		width: 100% !important;
	}

	#template_container {
		width: 1280px !important;
	}
	#source_home .more-options {
		width: 960px !important;
	}
	.collection-carousel {
		width: 936px !important;
	}
	.collection-carousel-contents {
		width: 890px !important;
	}
}

@-moz-document url("https://www.crunchyroll.com/home/queue") {

	#main_content {
		width: 100% !important;
	}

	.queue-sortable .queue-item {
		width: 95%;
		margin: 10px auto !important;
	}

	.series-info {
		margin: 0 !important;
	}

	.queue-sortable .queue-item .queue-item-content {
		box-sizing: border-box !important;
		width: 100%;
	}

	.queue-sortable .queue-item .anchor-to-episode {
		box-sizing: border-box !important;
		width: 80%;
		flex: 1 1;
	}

	.source-home .dropdown-collection {
		flex: 0 0 36px;
		margin: 0 !important;
		background: url("data:image/png;base64,")
	}
}
